INSERT INTO `tr_contract` (`CONTRACT_ID`, `CONTRACT_NO`, `CONTRACT_TITLE`, `CONTRACT_CONTENT`, `TEMPLATE_ID`, `COMPILED`, `PDF_PATH`, `PUBLISHED`, `PUBLISHED_DATETIME`, `REVIEWER_ID`, `REVIEWER_STATUS`, `REVIEWER_NOTE`, `REVIEWER_DATETIME`, `LEGAL_ID`, `LEGAL_STATUS`, `LEGAL_NOTE`, `LEGAL_DATETIME`, `FINANCE_ID`, `FINANCE_STATUS`, `FINANCE_NOTE`, `FINANCE_DATETIME`, `VENDOR_ID`, `VENDOR_SIGNATURE`, `VENDOR_CERTIFICATE`, `VENDOR_DATETIME`, `OFFICER_ID`, `OFFICER_SIGNATURE`, `OFFICER_CERTIFICATE`, `OFFICER_DATETIME`, `ACTIVE`, `DELETED`, `CREATED_ON`, `CREATED_BY`, `UPDATED_ON`, `UPDATED_BY`, `LEGAL_SLA`, `FINANCE_SLA`, `VENDOR_SLA`, `OFFICER_SLA`) VALUES
(125, '001/F00237/2019-S0', 'Jasa Pemeriksaan Kesehatan Harian di Lokasi Operasional MOR VII', '{\"[CONTRACT_TITLE]\":\"Jasa Pemeriksaan Kesehatan Harian di Lokasi Operasional MOR VII\",\"[CONTRACT_NUMBER]\":\"001/F00237/2019-S0\",\"[CONTRACT_DATE_CREATION]\":\"hari Rabu tanggal Tiga belas bulan Maret tahun 2019\",\"[PERTAMINA_SUBSIDIARY_NAME]\":\"PT Pertamina (Persero)\",\"[PERTAMINA_SUBSIDIARY_ADDRESS]\":\"Jalan Garuda No. 1 Makassar, Sulawesi Selatan\",\"[PERTAMINA_REPRESENTATIVE_NAME]\":\"Yock Yorlando\",\"[PERTAMINA_REPRESENTATIVE_POSITION]\":\"Region Manager HSSE MOR VII\",\"[VENDOR_COMPANY_NAME]\":\"PT. Mitrasejati Yakapepe\",\"[VENDOR_COMPANY_ADDRESS]\":\"Jalan Garuda No. 1 Makassar, Sulawesi Selatan\",\"[VENDOR_REPRESENTATIVE_NAME]\":\"Fitriani Darus\",\"[VENDOR_REPRESENTATIVE_POSITION]\":\"Direktur\",\"[NILAI_PEKERJAAN]\":\"Rp923.108.756,- (Sembilan ratus dua puluh tiga juta seratus delapan ribu tujuh ratus lima puluh enam rupiah)\",\"[NOMOR_LELANG]\":\"001A-ABO/I03127/2019\",\"[NOMOR_RFQ]\":\"660006233 \",\"[NO_BA_AANWIJZING_KANTOR]\":\"-\",\"[TANGGAL_BA_AANWIJZING_KANTOR]\":\"-\",\"[SURAT_ADMINISTRASI_VENDOR]\":\"039/MS/I/2019\",\"[TANGGAL_SURAT_ADMINISTRASI]\":\"18 Januari 2019\",\"[NO_BA_NEGOSIASI]\":\"219/I03127/2019-S5\",\"[TANGGAL_BA_NEGOSIASI]\":\"15 Februari 2019\",\"[SRT_NEGOSIASI_VENDOR]\":\"084/MS/II/2019\",\"[TANGGAL_SURAT_NEGOSIASI]\":\"02/18/2019\",\"[SURAT_KESANGGUPAN]\":\"085/MS/II/2019\",\"[TANGGAL_SURAT_KESANGGUPAN]\":\"02/18/2019\",\"[NO_SK_PENETAPAN]\":\"001/F00237/2018-S0\",\"[TANGGAL_SK_PENETAPAN]\":\"02/25/2019\",\"[SURAT_PENUNJUKAN_PEMENANG]\":\"066/I03127/2019-S5\",\"[TANGGAL_SURAT_PENUNJUKAN]\":\"02/28/2019\",\"[LINGKUP_PEKERJAAN]\":\"1. Pekerjaan Pemeriksaan Kesehatan Harian;<br>\\n2. Pekerjaan Pelatihan P3K;<br>\\n3. Pekerjaan Pengukuran Hygiene Industri Semester.\",\"[LOKASI_PEKERJAAN]\":\"seluruh wilayah TBBM, DPPU dan Depot LPG Pertamina MOR VII.\",\"[NO_REKENING]\":\"152-00-0215549-3\",\"[NO_REKENING_BANK]\":\"PT. Bank Mandiri (Persero) Tbk Branch Makassar Sulawesi\",\"[JANGKA_WAKTU_PELAKSANAAN]\":\"365\",\"[TANGGAL_MULAI_PEKERJAAN]\":\"1 Maret 2019\",\"[DIREKSI_PEKERJAAN]\":\"Sr. Spv. HSE Operation\",\"[PENGAWAS_PEKERJAAN]\":\"Jr. Analyst Safety & Hygiene Industry\",\"[PERTAMINA_SUBSIDIARY_TELEPHONE]\":\"(0411) 871181\",\"[PERTAMINA_SUBSIDIARY_FAX]\":\"(0411) 823123\",\"[VENDOR_COMPANY_TELEPHONE]\":\"(0411) 835458\",\"[VENDOR_COMPANY_FAX]\":\"(0411) 871181\"}', 22, '<htmlpagefooter name=\"footerTemplate\" style=\"display:none\">\n                    <table width=\"100%\" style=\"vertical-align: bottom; font-family: sans-serif; font-size: 9pt;\">\n                    <tr><td style=\"text-align: left;\" colspan=\"3\"> Catatan: </td></tr>\n                    <tr><td style=\"text-align: left;\" colspan=\"3\"> <ul><li> UU ITE No. 11 Tahun 2008 Pasal 5 ayat 1 <br> <i>\"Informasi Elektronik dan/atau Dokumen Elektronik dan/atau hasil cetaknya merupakan alat bukti hukum yang sah.\"</i> </li><li> Dokumen ini telah ditandatangani secara elektonik menggunakan <b>sertifikat elektronik</b> yang diterbitkan <b>BSrE</b> </li></ul> </td></tr>\n                    <tr><td width=\"33%\" style=\"text-align: left;\"></td>\n                    <td width=\"33%\" style=\"text-align: center;\">Halaman {PAGENO} dari {nbpg}</td>\n                    <td width=\"33%\" style=\"text-align: right;\"></td></tr></table>\n</htmlpagefooter>\n                    <setpagefooter name=\"footerTemplate\" page=\"\" value=\"on\" show-this-page=\"1\" /><p style=\"text-align: center;\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><strong>POKOK-POKOK PERJANJIAN<br />\nJasa Pemeriksaan Kesehatan Harian di Lokasi Operasional MOR VII<br />\n001/F00237/2019-S0</strong></span></span></p>\n\n<p>&nbsp;</p>\n\n<p style=\"text-align: justify;\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">Perjanjian ini (&ldquo;KONTRAK&rdquo;) dibuat dan ditandatangani pada&nbsp;hari Rabu tanggal Tiga belas bulan Maret tahun 2019 (<strong>&ldquo;TANGGAL KONTRAK</strong>&rdquo;), oleh:</span></span></p>\n\n<table align=\"left\" border=\"0\" style=\"width:100%;border-collapse:collapse;\">\n	<tbody>\n		<tr>\n			<td style=\"width:6.5%\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">I.</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><span style=\"font-style: normal;\"><span style=\"font-weight: normal;\"><strong>PT Pertamina (Persero)</strong>,&nbsp;suatu perusahaan yang didirikan berdasarkan hukum Indonesia, berkedudukan dan berkantor pusat di Jakarta Pusat, dalam hal ini diwakili oleh&nbsp;<strong>Yock Yorlando</strong> selaku <strong>Region Manager HSSE MOR VII</strong>, selanjutnya dalam <strong>KONTRAK</strong> ini disebut &rdquo;<strong>PIHAK PERTAMA</strong>&rdquo;, dan</span></span></span></span></td>\n		</tr>\n		<tr>\n			<td valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">II.</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><span style=\"font-style: normal;\"><span style=\"font-weight: normal;\"><strong>PT. Mitrasejati Yakapepe</strong>, suatu perusahaan yang didirikan berdasarkan hukum Indonesia berkedudukan dan berkantor pusat di&nbsp;Jalan Garuda No. 1 Makassar, Sulawesi Selatan dalam hal ini diwakili oleh <strong>Fitriani Darus</strong>, selaku&nbsp; <strong>Direktur</strong>, selanjutnya dalam <strong>KONTRAK</strong> ini disebut &rdquo;<strong>PIHAK KEDUA</strong>&rdquo;</span></span></span></span></td>\n		</tr>\n	</tbody>\n</table>\n\n<p><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">&nbsp;<font color=\"#000000\">Selanjutnya <b>PERUSAHAAN </b>dan <b>KONTRAKTOR </b>secara sendiri-sendiri disebut &ldquo;<b>PIHAK</b>&rdquo; dan secara bersama-sama disebut &ldquo;<b>PARA PIHAK</b>&rdquo;.</font></span></span></p>\n\n<p align=\"center\" style=\"margin: 0cm 0cm 8pt; text-align: center;\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><b><font color=\"#000000\">BAHWASANYA;</font></b></span></span></p>\n\n<p style=\"margin: 0cm 0cm 8pt; text-align: justify;\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><font color=\"#000000\"><b>PIHAK PERTAMA&nbsp;</b>mempunyai kegiatan usaha dibidang energi yaitu minyak dan gas bumi, energi baru dan terbarukan, dan dalam kaitannya dengan hal tersebut <b>PIHAK PERTAMA&nbsp;</b>memerlukan dilakukannya pekerjaan sebagaimana diatur dalam <b>KONTRAK </b>ini; dan <b>PIHAK KEDUA&nbsp;</b>adalah perusahaan yang mempunyai kemampuan dan pengalaman serta sanggup untuk melaksanakan pekerjaan yang diperlukan oleh <b>PIHAK PERTAMA</b>.</font></span></span></p>\n\n<p style=\"margin: 0cm 0cm 8pt;\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><b><font color=\"#000000\">BERDASARKAN PERTIMBANGAN DI ATAS, PARA PIHAK MENYEPAKATI HAL-HAL BERIKUT:</font></b></span></span></p>\n\n<ol>\n	<li><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><span style=\"font-style: normal;\"><span style=\"font-weight: normal;\">&nbsp;&nbsp;&nbsp;Dokumen-dokumen yang disebut di bawah merupakan bagian yang tidak terpisahkan dari KONTRAK ini:</span></span></span></span></li>\n</ol>\n\n<table style=\"margin: auto auto auto 36pt; border: rgb(0, 0, 0); border-image: none; border-collapse: collapse;\">\n	<tbody>\n		<tr>\n			<td style=\"padding: 0cm 5.4pt; border: rgb(0, 0, 0); border-image: none; width: 34.65pt;\" valign=\"top\" width=\"46\">\n			<p style=\"margin: 0cm 0cm 0pt;\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><font color=\"#000000\">&nbsp; A.</font></span></span></p>\n			</td>\n			<td style=\"padding: 0cm 5.4pt; border: rgb(0, 0, 0); border-image: none; width: 77.95pt;\" valign=\"top\" width=\"104\">\n			<p style=\"margin: 0cm 0cm 0pt;\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><font color=\"#000000\">Lampiran A</font></span></span></p>\n			</td>\n			<td style=\"padding: 0cm 5.4pt; border: rgb(0, 0, 0); border-image: none; width: 318.9pt;\" valign=\"top\" width=\"425\">\n			<p style=\"margin: 0cm 0cm 0pt;\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><font color=\"#000000\">: Syarat-syarat dan Ketentuan-ketentuan Umum.</font></span></span></p>\n			</td>\n		</tr>\n	</tbody>\n</table>\n\n<table style=\"margin: auto auto auto 36pt; border: rgb(0, 0, 0); border-image: none; border-collapse: collapse;\">\n	<tbody>\n		<tr>\n			<td style=\"padding: 0cm 5.4pt; border: rgb(0, 0, 0); border-image: none; width: 34.65pt;\" valign=\"top\" width=\"46\">\n			<p style=\"margin: 0cm 0cm 0pt;\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><font color=\"#000000\">&nbsp; B.</font></span></span></p>\n			</td>\n			<td style=\"padding: 0cm 5.4pt; border: rgb(0, 0, 0); border-image: none; width: 77.95pt;\" valign=\"top\" width=\"104\">\n			<p style=\"margin: 0cm 0cm 0pt;\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><font color=\"#000000\">Lampiran B</font></span></span></p>\n			</td>\n			<td style=\"padding: 0cm 5.4pt; border: rgb(0, 0, 0); border-image: none; width: 318.9pt;\" valign=\"top\" width=\"425\">\n			<p style=\"margin: 0cm 0cm 0pt;\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><font color=\"#000000\">: Lingkup Pekerjaan.</font></span></span></p>\n			</td>\n		</tr>\n	</tbody>\n</table>\n\n<table style=\"margin: auto auto auto 36pt; border: rgb(0, 0, 0); border-image: none; border-collapse: collapse;\">\n	<tbody>\n		<tr>\n			<td style=\"padding: 0cm 5.4pt; border: rgb(0, 0, 0); border-image: none; width: 34.65pt;\" valign=\"top\" width=\"46\">\n			<p style=\"margin: 0cm 0cm 0pt;\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><font color=\"#000000\">&nbsp; C.</font></span></span></p>\n			</td>\n			<td style=\"padding: 0cm 5.4pt; border: rgb(0, 0, 0); border-image: none; width: 77.95pt;\" valign=\"top\" width=\"104\">\n			<p style=\"margin: 0cm 0cm 0pt;\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><font color=\"#000000\">Lampiran C</font></span></span></p>\n			</td>\n			<td style=\"padding: 0cm 5.4pt; border: rgb(0, 0, 0); border-image: none; width: 318.9pt;\" valign=\"top\" width=\"425\">\n			<p style=\"margin: 0cm 0cm 0pt;\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><font color=\"#000000\">: Harga Kontrak dan Ketentuan Pembayaran.</font></span></span></p>\n			</td>\n		</tr>\n	</tbody>\n</table>\n\n<table style=\"margin: auto auto auto 36pt; border: rgb(0, 0, 0); border-image: none; border-collapse: collapse;\">\n	<tbody>\n		<tr>\n			<td style=\"padding: 0cm 5.4pt; border: rgb(0, 0, 0); border-image: none; width: 34.65pt;\" valign=\"top\" width=\"46\">\n			<p style=\"margin: 0cm 0cm 0pt;\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><font color=\"#000000\">&nbsp; D.</font></span></span></p>\n			</td>\n			<td style=\"padding: 0cm 5.4pt; border: rgb(0, 0, 0); border-image: none; width: 77.95pt;\" valign=\"top\" width=\"104\">\n			<p style=\"margin: 0cm 0cm 0pt;\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><font color=\"#000000\">Lampiran D</font></span></span></p>\n			</td>\n			<td style=\"padding: 0cm 5.4pt; border: rgb(0, 0, 0); border-image: none; width: 318.9pt;\" valign=\"top\" width=\"425\">\n			<p style=\"margin: 0cm 0cm 0pt;\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><font color=\"#000000\">: Jangka Waktu</font></span></span></p>\n			</td>\n		</tr>\n	</tbody>\n</table>\n\n<table style=\"margin: auto auto auto 36pt; border: rgb(0, 0, 0); border-image: none; border-collapse: collapse;\">\n	<tbody>\n		<tr>\n			<td style=\"padding: 0cm 5.4pt; border: rgb(0, 0, 0); border-image: none; width: 34.65pt;\" valign=\"top\" width=\"46\">\n			<p style=\"margin: 0cm 0cm 0pt;\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><font color=\"#000000\">&nbsp; E.</font></span></span></p>\n			</td>\n			<td style=\"padding: 0cm 5.4pt; border: rgb(0, 0, 0); border-image: none; width: 77.95pt;\" valign=\"top\" width=\"104\">\n			<p style=\"margin: 0cm 0cm 0pt;\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><font color=\"#000000\">Lampiran E</font></span></span></p>\n			</td>\n			<td style=\"padding: 0cm 5.4pt; border: rgb(0, 0, 0); border-image: none; width: 318.9pt;\" valign=\"top\" width=\"425\">\n			<p style=\"margin: 0cm 0cm 0pt;\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><font color=\"#000000\">: Health, Safety, Environment (HSE) dan Contractor Safety Management System (CSMS)</font></span></span></p>\n			</td>\n		</tr>\n	</tbody>\n</table>\n\n<table style=\"margin: auto auto auto 36pt; border: rgb(0, 0, 0); border-image: none; border-collapse: collapse;\">\n	<tbody>\n		<tr>\n			<td style=\"padding: 0cm 5.4pt; border: rgb(0, 0, 0); border-image: none; width: 34.65pt;\" valign=\"top\" width=\"46\">\n			<p style=\"margin: 0cm 0cm 0pt;\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><font color=\"#000000\">&nbsp; F. </font></span></span></p>\n			</td>\n			<td style=\"padding: 0cm 5.4pt; border: rgb(0, 0, 0); border-image: none; width: 77.95pt;\" valign=\"top\" width=\"104\">\n			<p style=\"margin: 0cm 0cm 0pt;\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><font color=\"#000000\">Lampiran F</font></span></span></p>\n			</td>\n			<td style=\"padding: 0cm 5.4pt; border: rgb(0, 0, 0); border-image: none; width: 318.9pt;\" valign=\"top\" width=\"425\">\n			<p style=\"margin: 0cm 0cm 0pt;\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><font color=\"#000000\">: Asuransi</font></span></span></p>\n			</td>\n		</tr>\n	</tbody>\n</table>\n\n<table style=\"margin: auto auto auto 36pt; border: rgb(0, 0, 0); border-image: none; border-collapse: collapse;\">\n	<tbody>\n		<tr>\n			<td style=\"padding: 0cm 5.4pt; border: rgb(0, 0, 0); border-image: none; width: 34.65pt;\" valign=\"top\" width=\"46\">\n			<p style=\"margin: 0cm 0cm 0pt;\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><font color=\"#000000\">&nbsp; G.</font></span></span></p>\n			</td>\n			<td style=\"padding: 0cm 5.4pt; border: rgb(0, 0, 0); border-image: none; width: 77.95pt;\" valign=\"top\" width=\"104\">\n			<p style=\"margin: 0cm 0cm 0pt;\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><font color=\"#000000\">Lampiran G</font></span></span></p>\n			</td>\n			<td style=\"padding: 0cm 5.4pt; border: rgb(0, 0, 0); border-image: none; width: 318.9pt;\" valign=\"top\" width=\"425\">\n			<p style=\"margin: 0cm 0cm 0pt;\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><font color=\"#000000\">: Lain-lain</font></span></span></p>\n			</td>\n		</tr>\n	</tbody>\n</table>\n\n<p style=\"margin: 0cm 0cm 0pt 36pt;\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><font color=\"#000000\">&nbsp;</font></span></span></p>\n\n<table align=\"left\" border=\"0\" style=\"width: 100%; border-collapse: collapse;\">\n	<tbody>\n		<tr>\n			<td style=\"width: 6.5%; text-align: center;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">2.</span></span></td>\n			<td valign=\"top\">\n			<p style=\"margin: 0cm 0cm 0pt; text-align: justify;\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><font color=\"#000000\">Pekerjaan yang harus dilaksanakan oleh KONTRAKTOR adalah Jasa Pemeriksaan Kesehatan Harian di Lokasi Operasional MOR VII sebagaimana diuraikan dalam Lampiran B dan dilaksanakan sesuai jangka waktu yang diuraikan dalam Lampiran D (&ldquo;PEKERJAAN&rdquo;).</font></span></span></p>\n			</td>\n		</tr>\n		<tr>\n			<td style=\"width: 6.5%; text-align: center;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">3.</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><span style=\"color: rgb(51, 51, 51); line-height: 107%; mso-fareast-font-family: Calibri; mso-bidi-font-family: Arial; mso-fareast-theme-font: minor-latin; mso-ansi-language: EN-US; mso-fareast-language: EN-US; mso-bidi-language: AR-SA;\">Harga borongan atas pelaksanaan seluruh&nbsp;<strong>PEKERJAAN&nbsp;</strong>ini adalah sebesar<strong>&nbsp;Rp923.108.756,- (Sembilan ratus dua puluh tiga juta seratus delapan ribu tujuh ratus lima puluh enam rupiah) </strong>belum termasuk PPN 10%, dengan tata cara pembayaran sebagaimana diuraikan dalam Lampiran C (&ldquo;<strong>HARGA KONTRAK</strong>&rdquo;)</span></span></span></td>\n		</tr>\n		<tr>\n			<td style=\"width: 6.5%; text-align: center;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">4.</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><span style=\"color: rgb(51, 51, 51); line-height: 107%; mso-fareast-font-family: Calibri; mso-bidi-font-family: Arial; mso-fareast-theme-font: minor-latin; mso-ansi-language: EN-US; mso-fareast-language: EN-US; mso-bidi-language: AR-SA;\"><strong>KONTRAK </strong>ini mulai berlaku pada <strong>TANGGAL KONTRAK</strong> atau jangka waktu lain yang disepakati <strong>PARA PIHAK</strong> &nbsp;dan akan tetap berlaku sampai dengan ditandatangninya Berita Acara Serah Terima Pekerjaan (<strong>BASTP</strong>) (&ldquo;<strong>JANGKA WAKTU KONTRAK</strong>&rdquo;) kecuali diakhiri lebih awal sesuai dengan ketentuan KONTRAK ini.</span></span></span></td>\n		</tr>\n	</tbody>\n</table>\n\n<p style=\"margin: 0cm 0cm 8pt 36pt;\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><font color=\"#000000\">&nbsp;</font></span></span></p>\n\n<p style=\"margin: 0cm 0cm 8pt; text-align: justify;\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><font color=\"#000000\"><b>KONTRAK </b>ini dibuat dalam rangkap 2 (dua), masing-masing mempunyai kekuatan hukum yang sama, dan ditandatangani oleh <b>PARA PIHAK</b>.&nbsp;</font></span></span></p>\n\n<table style=\"width: 100%; border-collapse: collapse;\">\n	<tbody>\n		<tr>\n			<td style=\"width: 50%; text-align: center;\" valign=\"top\">&nbsp;</td>\n			<td style=\"text-align: center;\" valign=\"top\">&nbsp;</td>\n		</tr>\n		<tr>\n			<td style=\"width: 50%; text-align: center;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><strong>PIHAK PERTAMA</strong></span></span></td>\n			<td style=\"text-align: center;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><b>PIHAK KEDUA</b></span></span></td>\n		</tr>\n		<tr>\n			<td style=\"text-align: center;\" valign=\"top\">&nbsp;</td>\n			<td style=\"text-align: center;\" valign=\"top\">&nbsp;</td>\n		</tr>\n		<tr>\n			<td style=\"text-align: center;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">[OFFICER_SIGNATURE]</span></span></td>\n			<td style=\"text-align: center;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">[VENDOR_SIGNATURE]</span></span></td>\n		</tr>\n		<tr>\n			<td style=\"text-align: center;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">Yock Yorlando</span></span></td>\n			<td style=\"text-align: center;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">Fitriani Darus</span></span></td>\n		</tr>\n		<tr>\n			<td style=\"text-align: center;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">Region Manager HSSE MOR VII</span></span></td>\n			<td style=\"text-align: center;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">Direktur</span></span></td>\n		</tr>\n	</tbody>\n</table>\n\n<p>&nbsp;</p>\n\n<p style=\"text-align: right;\">&nbsp;</p>\n\n<div style=\"page-break-after: always\"><span style=\"display: none;\">&nbsp;</span></div>\n\n<p style=\"text-align: right;\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">LAMPIRAN A</span></span></p>\n\n<p style=\"text-align: center;\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><strong>SYARAT-SYARAT DAN KETENTUAN-KETENTUAN UMUM</strong></span></span></p>\n\n<p style=\"text-align: center;\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><strong>PASAL1</strong></span></span></p>\n\n<p style=\"text-align: center;\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><strong>DEFINISI</strong></span></span></p>\n\n<p style=\"margin: 0cm 0cm 8pt;\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><font color=\"#000000\">Istilah-istilah di bawah yang digunakan dalam <b>KONTRAK </b>ini mempunyai arti sebagai berikut:</font></span></span></p>\n\n<table align=\"left\" border=\"0\" style=\"width: 100%; border-collapse: collapse;\">\n	<tbody>\n		<tr>\n			<td style=\"width: 6.5%;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">a.</span></span></td>\n			<td valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><strong>AFILIASI </strong>adalah suatu perusahaan yang:</span></span></td>\n		</tr>\n	</tbody>\n</table>\n\n<table style=\"margin: auto auto auto 36pt; border: rgb(0, 0, 0); border-image: none; border-collapse: collapse;\">\n	<tbody>\n		<tr>\n			<td style=\"padding: 0cm 5.4pt; border: rgb(0, 0, 0); border-image: none; width: 27.55pt;\" valign=\"top\" width=\"37\">\n			<p style=\"margin: 0cm 0cm 0pt; text-align: justify;\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><font color=\"#000000\">a)</font></span></span></p>\n			</td>\n			<td style=\"padding: 0cm 5.4pt; border: rgb(0, 0, 0); border-image: none; width: 403.95pt;\" valign=\"top\" width=\"539\">\n			<p style=\"margin: 0cm 0cm 0pt; text-align: justify;\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><font color=\"#000000\">mengendalikan, atau;</font></span></span></p>\n			</td>\n		</tr>\n		<tr>\n			<td style=\"padding: 0cm 5.4pt; border: rgb(0, 0, 0); border-image: none; width: 27.55pt;\" valign=\"top\" width=\"37\">\n			<p style=\"margin: 0cm 0cm 0pt; text-align: justify;\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><font color=\"#000000\">b)</font></span></span></p>\n			</td>\n			<td style=\"padding: 0cm 5.4pt; border: rgb(0, 0, 0); border-image: none; width: 403.95pt;\" valign=\"top\" width=\"539\">\n			<p style=\"margin: 0cm 0cm 0pt; text-align: justify;\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><font color=\"#000000\">dikendalikan oleh, atau;</font></span></span></p>\n			</td>\n		</tr>\n		<tr>\n			<td style=\"padding: 0cm 5.4pt; border: rgb(0, 0, 0); border-image: none; width: 27.55pt;\" valign=\"top\" width=\"37\">\n			<p style=\"margin: 0cm 0cm 0pt; text-align: justify;\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><font color=\"#000000\">c)</font></span></span></p>\n			</td>\n			<td style=\"padding: 0cm 5.4pt; border: rgb(0, 0, 0); border-image: none; width: 403.95pt;\" valign=\"top\" width=\"539\">\n			<p style=\"margin: 0cm 0cm 0pt; text-align: justify;\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><font color=\"#000000\">berada di bawah kendali yang sama dengan salah satu <b>PIHAK</b>.</font></span></span></p>\n			</td>\n		</tr>\n	</tbody>\n</table>\n\n<table align=\"left\" border=\"0\" style=\"width: 100%; border-collapse: collapse;\">\n	<tbody>\n		<tr>\n			<td style=\"width: 6.5%; text-align: center;\" valign=\"top\">&nbsp;</td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">Yang dimaksud dengan &quot;kendali&quot; adalah kepemilikan secara langsung atau tidak langsung lebih dari 50% saham atau lebih dari 50% hak suara.</span></span></td>\n		</tr>\n		<tr>\n			<td style=\"width: 6.5%;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">b.</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><strong>DOKUMEN KONTRAK </strong>adalah <strong>POKOK-POKOK PERJANJIAN </strong>dan seluruh dokumen yang disebut dalam PASAL 1 <strong>POKOK-POKOK PERJANJIAN</strong>.</span></span></td>\n		</tr>\n		<tr>\n			<td style=\"width: 6.5%;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">c.</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><strong>HAK KEKAYAAN INTELEKTUAL </strong>yaitu segala hak eksklusif yang diberikan oleh peraturan perundang-undangan kepada seseorang atau sekelompok orang atas karya ciptanya, termasuk namun tidak terbatas pada hak cipta, paten, merek, rahasia dagang dan desain industri.</span></span></td>\n		</tr>\n		<tr>\n			<td style=\"width: 6.5%;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">d.</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><strong>HARI KALENDER </strong>adalah kurun waktu 24 jam secara terus-menerus, yang dimulai pada pukul 00.00 WIB sampai dengan pukul 00.00 WIB pada hari berikutnya.</span></span></td>\n		</tr>\n		<tr>\n			<td style=\"width: 6.5%;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">e.</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><strong>HARI KERJA </strong>adalah seluruh <strong>HARI KALENDER </strong>kecuali hari Sabtu, hari Minggu dan hari libur nasional Republik Indonesia.</span></span></td>\n		</tr>\n		<tr>\n			<td style=\"width: 6.5%;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">f.</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><strong>JANGKA WAKTU KONTRAK </strong>adalah jangka waktu yang dimulai sejak <strong>TANGGAL KONTRAK </strong>atau tanggal lain yang disepakati <strong>PARA PIHAK </strong>sampai dengan tanggal yang disebut dalam Lampiran D.</span></span></td>\n		</tr>\n		<tr>\n			<td style=\"width: 6.5%;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">g.</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><strong>KESENGAJAAN DAN/ATAU KELALAIAN </strong>adalah tindakan yang baik sengaja dan/atau tidak disengaja dimaksudkan untuk melanggar atau tidak memenuhi suatu kewajiban berdasarkan <strong>KONTRAK</strong>, peraturan perundangan, <em>best oilfield practices </em>atau kepatuhan.</span></span></td>\n		</tr>\n		<tr>\n			<td style=\"width: 6.5%;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">h.</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><strong>LOKASI PEKERJAAN </strong>adalah kantor, lapangan atau tempat-tempat lain dimana <strong>PIHAK PERTAMA </strong>melakukan kegiatannya, di tempat mana <strong>PEKERJAAN </strong>wajib dilakukan sebagaimana disebut dalam Lampiran B.</span></span></td>\n		</tr>\n		<tr>\n			<td style=\"width: 6.5%;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">i.</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><strong>MASA PENYELESAIAN PEKERJAAN </strong>yaitu jangka waktu bilaman <strong>PEKERJAAN </strong>wajib diselesaikan oleh <strong>PIHAK KEDUA </strong>seperti diatur dalam Lampiran D.</span></span></td>\n		</tr>\n		<tr>\n			<td style=\"width: 6.5%;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">j.</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><strong>PERSONIL PIHAK KEDUA </strong>adalah mereka yang dipekerjakan atau dilibatkan oleh <strong>PIHAK KEDUA </strong>untuk melaksanakan <strong>KONTRAK</strong>, baik langsung maupun tidak langsung termasuk namun tidak terbatas pada <strong>SUBKONTRAKTOR </strong>dan <em>vendor</em>.</span></span></td>\n		</tr>\n		<tr>\n			<td style=\"width: 6.5%;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">k.</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><strong>PERSONIL PIHAK PERTAMA </strong>adalah mereka yang dipekerjakan atau dilibatkan oleh <strong>PIHAK PERTAMA, </strong>baik langsung maupun tidak langsung dan <strong>PIHAK KETIGA </strong>yang oleh <strong>POHAK PERTAMA </strong>diundang untuk berada di <strong>LOKASI PEKERJAAN</strong>.</span></span></td>\n		</tr>\n		<tr>\n			<td style=\"width: 6.5%;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">l.</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><strong>PIHAK PERTAMA </strong>adalah PT. Pertamina (Persero) sebagai perusahaan pemberi pekerjaan.</span></span></td>\n		</tr>\n		<tr>\n			<td style=\"width: 6.5%;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">m.</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><strong>PIHAK KEDUA </strong>adalah perusahaan yang berbentuk badan hukum yang memenuhi syarat untuk menerima penyerahan sebagaian pelaksanaan pekerjaan dari <strong>PIHAK PERTAMA</strong>.</span></span></td>\n		</tr>\n		<tr>\n			<td style=\"width: 6.5%;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">n.</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><strong>PIHAK KETIGA </strong>adalah mereka yang bukan <strong>PIHAK PERTAMA, PERSONIL PIHAK PERTAMA, PIHAK KEDUA </strong>atau <strong>PERSONIL PIHAK KEDUA</strong>.</span></span></td>\n		</tr>\n		<tr>\n			<td style=\"width: 6.5%;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">o.</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><strong>POKOK-POKOK PERJANJIAN </strong>adalah dokuAmen yang berjudul &quot;POKOK-POKOK PERJANJIAN&#39; yang ditandatangani oleh <strong>PARA PIHAK.</strong></span></span></td>\n		</tr>\n		<tr>\n			<td style=\"width: 6.5%;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">p.</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><strong>SUBKONTRAKTOR </strong>adalah suatu pihak yang disetujui secara tertulis oleh <strong>PIHAK PERTAMA </strong>untuk melakukan sebagian dari <strong>PEKERJAAN </strong>dalam kapasitas sebagai <strong>SUBKONTRAKTOR </strong>dari <strong>PIHAK KEDUA</strong>.</span></span></td>\n		</tr>\n	</tbody>\n</table>\n\n<p>&nbsp;</p>\n\n<p style=\"text-align: center;\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><strong>PASAL 2</strong>&nbsp;</span></span></p>\n\n<p style=\"text-align: center;\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><strong>ISI DAN PENAFSIRAN ISI KONTRAK</strong></span></span></p>\n\n<table align=\"left\" style=\"width: 100%; border-collapse: collapse;\">\n	<tbody>\n		<tr>\n			<td style=\"width: 6.5%;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">2.1</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">Seluruh perintah, <font color=\"#000000\">pemberitahuan, otorisasi, persetujuan kepada <b>PIHAK KEDUA </b>wajib dibuat dalam bentuk tertulis dan ditandatangani sekurang-kurangnya oleh Pengawas Pekerjaan <b>PIHAK PERTAMA </b>yang disebut dalam Lampiran G. Meskipun demikian, <b>PIHAK PERTAMA </b>berhak untuk memberikan perintah secara lisan kepada <b>PIHAK KEDUA </b>dan <b>PIHAK KEDUA </b>wajib tuntuk kepada perintah tersebut dengan ketentuan bahwa perintah itu wajib kemudian ditegaskan secara tertulis oleh <b>PIHAK KEDUA </b>kepada <b>PIHAK PERTAMA </b>dalam waktu maksimal 3 <b>HARI KERJA </b>setelah menerima perintah lisan. Apabila penegasan tertulis dari <b>PIHAK KEDUA </b>tersebut tidak dibantah secara tertulis oleh <b>PIHAK PERTAMA </b>dalam waktu maksimal 3 <b>HARI KERJA </b>setelah diterimanya penegasan tertulis dari <b>PIHAK KEDUA</b>, maka perintah lisan dari <b>PIHAK PERTAMA </b>tersebut dianggap dan berlaku sebagai suatu perintah tertulis.</font></span></span></td>\n		</tr>\n		<tr>\n			<td style=\"width: 6.5%;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">2.2</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">Seluruh <font color=\"#000000\">judul dan sub-judul yang dipakai dalam <b>DOKUMEN KONTRAK </b>adalah sekedar untuk kemudahan acuan dan tidak dapat digunakan untuk menentukan pemahaman atau penafsiran <b>KONTRAK </b>ini.</font></span></span></td>\n		</tr>\n		<tr>\n			<td style=\"width: 6.5%;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">2.3</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><strong>KONTRAK </strong><font color=\"#000000\">ini memuat segala kesepakatan di antara <b>PARA PIHAK </b>dan karenanya mengesampingkan segala bentuk kesepakatan dan persetujuan baik lisan maupun tertulis di antara <b>PARA PIHAK </b>yang dibuat sebelum ditandatanganinya <b>KONTRAK </b>ini</font></span></span></td>\n		</tr>\n		<tr>\n			<td style=\"width: 6.5%;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">2.4</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">Tidak <font color=\"#000000\">ada hak apapun dari satu <b>PIHAK </b>berdasarkan <b>KONTRAK </b>ini yang dapat dianggap dikesampingkan atau dilepaskan, kecuali dinyatakan secara tertulis oleh <b>PIHAK </b>tersebut.</font></span></span></td>\n		</tr>\n		<tr>\n			<td style=\"width: 6.5%;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">2.5</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">Setiap <font color=\"#000000\">perubahan atas <b>KONTRAK</b> ini wajib disetujui secara tertulis oleh <b>PARA PIHAK </b>dan dimuat dalam suatu addendum yang wajib ditandatangani oleh <b>PARA PIHAK </b>dan menjadi kesatuan dan bagian yang tidak terpisahkan dari <b>KONTRAK </b>ini.</font></span></span></td>\n		</tr>\n		<tr>\n			<td style=\"width: 6.5%;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">2.6</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">Jika satu <font color=\"#000000\">atau lebih ketentuan dalam <b>KONTRAK </b>ini dinyatakan illegal, tidak sah, tidak berlaku atau tidak dapat dilaksanakan dengan alasan apapun (termasuk kerana adanya ketentuan peraturan perundang-undangan atau putusan pengadilan atau badan lain yang memiliki yurisdiksi atas <b>PARA PIHAK </b>atau <b>KONTRAK </b>ini), maka ketentuan atau beberapa ketentuan tersebut akan dianggap dihapus dari <b>KONTRAK </b>ini, tanpa mengurangi keberlakuan ketentuan-ketentuan lainnya. Apabila ketidak-berlakuan ketentuan atau beberapa ketentuan itu secara substansial akan mempengaruhi <b>HARGA KONTRAK</b>, maka dengan itikad baik <b>PARA PIHAK </b>akan melakukan perundingan untuk mengganti ketentuan yang dinyatakan illegal, tidak sah, tidak berlaku atau tidak dapat dilaksanakan tersebut dengan ketentuan bahwa ketentuan-ketentuan lainnya tetap sesuai dengan maksud dari ketentuan semula.</font></span></span></td>\n		</tr>\n		<tr>\n			<td style=\"width: 6.5%;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">2.7</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">Apabila <font color=\"#000000\">terdapat perbedaan atau pertentangan isi dari ketentuan-ketentuan dalam <b>DOKUMEN KONTRAK</b>, maka <b>PIHAK PERTAMA </b>yang akan menentukan ketentuan mana yang berlaku</font></span></span></td>\n		</tr>\n	</tbody>\n</table>\n\n<p>&nbsp;</p>\n\n<p style=\"text-align: center;\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><span style=\"font-weight: normal;\"><strong>PASAL 3</strong></span></span></span></p>\n\n<p style=\"text-align: center;\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><span style=\"font-weight: normal;\"><strong>SURAT MENYURAT</strong></span></span></span></p>\n\n<table align=\"left\" style=\"width: 100%; border-collapse: collapse;\">\n	<tbody>\n		<tr>\n			<td style=\"width: 6.5%;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">3.1</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">Surat menyurat <font color=\"#000000\">terkait <b>KONTRAK </b>ini (termasuk pemberitahuan, perintah atau komunikasi lainnya) wajib dilakukan secara tertulis dan disampaikan langsung, melalui kurir, surat tercatat atau faksimili ke alamat yang disebut pada Lampiran G.</font></span></span></td>\n		</tr>\n		<tr>\n			<td style=\"width: 6.5%;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">3.2</span></span></td>\n			<td valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">Surat <font color=\"#000000\">menyurat dianggap telah dikirimkan:</font></span></span></td>\n		</tr>\n		<tr>\n			<td style=\"width: 6.5%;\" valign=\"top\">&nbsp;</td>\n			<td valign=\"top\">\n			<table align=\"left\" style=\"width: 100%; border-collapse: collapse;\">\n				<tbody>\n					<tr>\n						<td style=\"width: 6.5%; text-align: center;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">a)</span></span></td>\n						<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">dengan adanya <font color=\"#000000\">tanda terima tertulis yang ditandatangani oleh penerima jika surat menyurat tersebut diserahkan langsung.</font></span></span></td>\n					</tr>\n					<tr>\n						<td style=\"width: 6.5%; text-align: center;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">b)</span></span></td>\n						<td valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">dengan lewatnya <font color=\"#000000\"><strong>7 HARI KALENDER</strong> sejak tanggal pengeposan surat; atau</font></span></span></td>\n					</tr>\n					<tr>\n						<td style=\"width: 6.5%; text-align: center;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">c)</span></span></td>\n						<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">dengan lewatnya <font color=\"#000000\">waktu 1 x 24 jam sejak waktu yang tertera dalam lembar bukti dilakukannya transmisi elektronik yang layaknya ada jika surat menyurat dilakukan dengan faksimili.</font></span></span></td>\n					</tr>\n				</tbody>\n			</table>\n			</td>\n		</tr>\n		<tr>\n			<td style=\"width: 6.5%;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">3.3.</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">Jika <font color=\"#000000\">terjadi perubahan alamat, <b>PIHAK </b>yang berubah alamatnya wajib mengirimkan pemberitahuan kepada <b>PIHAK </b>lainnya selambat-lambatnya 5 (lima) <b>HARI KALENDER </b>sebelum alamat baru itu berlaku. Tanpa adanya pemberitahuan tersebut, surat menyurat yang dilakukan ke alamat yang lama akan dianggap telah dilakukan ke alamat yang lama akan dianggap telah dilakukan sesuai dengan <b>KONTRAK </b>ini.</font></span></span></td>\n		</tr>\n	</tbody>\n</table>\n\n<p>&nbsp;</p>\n\n<p style=\"text-align: center;\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><span style=\"font-weight: normal;\"><strong>PASAL 4</strong></span></span></span></p>\n\n<p style=\"text-align: center;\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><strong>PERNYATAAN DAN JAMINAN PIHAK KEDUA</strong></span></span></p>\n\n<table align=\"left\" style=\"width: 100%; border-collapse: collapse;\">\n	<tbody>\n		<tr>\n			<td style=\"width: 6.5%;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">4.1.</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><strong>PIHAK KEDUA </strong><font color=\"#000000\">dengan ini menyatakan dan menjadmin kepada <b>PIHAK PERTAMA </b>bahwa <b>PIHAK KEDUA</b>dengan ini menyatakan dan menjadmin kepada <b>PIHAK PERTAMA </b>bahwa <b>PIHAK KEDUA:</b></font></span></span></td>\n		</tr>\n		<tr>\n			<td style=\"width: 6.5%;\" valign=\"top\">&nbsp;</td>\n			<td valign=\"top\">\n			<table align=\"left\" style=\"width: 100%; border-collapse: collapse;\">\n				<tbody>\n					<tr>\n						<td style=\"width: 6.5%; text-align: center;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">a)</span></span></td>\n						<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">adalah <font color=\"#000000\">suatu perusahaan berbentuk badan hokum yang didirikan berdasarkan peraturan perundang-undangan dan <b>KONTRAK </b>ini ditandatangani oleh pejabat <b>PIHAK KEDUA </b>yang berwenang;</font></span></span></td>\n					</tr>\n					<tr>\n						<td style=\"width: 6.5%; text-align: center;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">b)</span></span></td>\n						<td valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">cakap <font color=\"#000000\">secara hukum untuk mengikat diri dalam dan melaksanakan <b>KONTRA </b>ini;</font></span></span></td>\n					</tr>\n					<tr>\n						<td style=\"width: 6.5%; text-align: center;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">c)</span></span></td>\n						<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">memiliki <font color=\"#000000\">kualifikasi, izin-izin dan memenuhi seluruh persyaratan yang diperlukan untuk melaksanakan <b>PEKERJAAN </b>selama <b>JANGKA WAKTU KONTRAK </b>sesuai ketentuan dan standar yang berlaku; dan</font></span></span></td>\n					</tr>\n					<tr>\n						<td style=\"width: 6.5%; text-align: center;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">d)</span></span></td>\n						<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">telah mengetahui dan <font color=\"#000000\">memahami ruang lingkup <b>PEKERJAAN </b>serta keadaan dan informasi yang akan mempengaruhi pelaksanaan <b>KONTRAK.</b></font></span></span></td>\n					</tr>\n				</tbody>\n			</table>\n			</td>\n		</tr>\n		<tr>\n			<td style=\"width: 6.5%;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">4.2.</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><strong>PIHAK KEDUA </strong><font color=\"#000000\">dengan ini menyatakan bahwa <b>PERSONEL PIHAK KEDUA </b>yan dipekerjakan sesuai dengan <b>KONTRAK </b>ini merupakan pekerja yang memiliki hubungan kerja hanya dengan <b>PIHAK KEDUA </b>dan <b>PIHAK KEDUA </b>akan menanggung seluruh risiko hukum yang terkait dengan hubungan kerja dengan <b>PERSONEL PIHAK KEDUA </b>dimaksud.</font></span></span></td>\n		</tr>\n	</tbody>\n</table>\n\n<p>&nbsp;</p>\n\n<p style=\"text-align: center;\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><span style=\"font-weight: normal;\"><strong>PASAL 5</strong></span></span></span></p>\n\n<p style=\"text-align: center;\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><strong>KEWAJIBAN PIHAK KEDUA BERKAITAN DENGAN PELAKSANAAN PEKERJAAN</strong></span></span></p>\n\n<table align=\"left\" style=\"width: 100%; border-collapse: collapse;\">\n	<tbody>\n		<tr>\n			<td style=\"width: 6.5%;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">5.1.</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><strong>PIHAK KEDUA </strong>wajib menyediakan seluruh peralatan, peralatan, saran dan fasilitas yang diperlukan untuk melaksanakan <strong>PEKERJAAN </strong>(&quot;<strong>PERALATAN</strong>&quot;).</span></span></td>\n		</tr>\n		<tr>\n			<td style=\"width: 6.5%;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">5.2.</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><strong>PIHAK KEDUA </strong>wajib menyediakan seluruh bahan-bahan seperti yang disebut dalam Lampiran B, yang diperlukan untuk melakukan <strong>PEKERJAAN </strong>(&quot;<strong>MATERIAL</strong>&quot;).</span></span></td>\n		</tr>\n		<tr>\n			<td style=\"width: 6.5%;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">5.3.</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><strong>PIHAK KEDUA </strong><font color=\"#000000\">wajib memperbaiki dan/atau mengganti <b>PERALATAN </b>dan/atau <b>MATERIAL </b>yang tidak sesuai dengan ketentuan dalam Lampiran B, meskipun <b>PERALATAN </b>dan/atau <b>MATERIAL </b>tersebut telah ditempatkan dan/atau dipasang dan/atau dipakai oleh <b>PIHAK KEDUA</b>. Perbaikan dan/atau penggantian tersebut wajib dilakukan oleh <b>PIHAK KEDUA </b>tanpa membebankan biaya apapun kepada <b>PIHAK PERTAMA </b>dan wajib dilaksanakan dalam waktu yang ditetnukan oleh <b>PIHAK PERTAMA</b>. Jika <b>PIHAK KEDUA </b>melalaikan kewajibannya dalam Pasal ini, <b>PIHAK PERTAMA </b>dapat melakukan sendiri perbaikan dan/atau penggantian tersebut atau menunjuk <b>PIHAK KETIGA </b>untuk melakukannya dan biaya yang timbul akan dibebankan kepada dan menjadi tanggung jawab <b>PIHAK KEDUA </b>dan/atau akan dipotong dari pembayaran <b>HARGA KONTRAK</b></font></span></span></td>\n		</tr>\n		<tr>\n			<td style=\"width: 6.5%;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">5.4.</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><strong>PIHAK KEDUA </strong>wajib menyediakan <strong>PERSONIL PIHAK KEDUA </strong>sesuai yang diatur dalam Lampiran B, atau jika diatur secara spesifik dalam Lampiran B, dalam jumlah dan dengan kualifikasi yang memadai untuk melaksanakan <strong>PEKERJAAN</strong>.</span></span></td>\n		</tr>\n		<tr>\n			<td style=\"width: 6.5%;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">5.5.</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><strong>PIHAK PERTAMA </strong>berhak memerintahkan dan <strong>PIHAK KEDUA </strong>wajib untuk segera mengganti <strong>PERSONIL PIHAK KEDUA</strong> yang oleh <strong>PIHAK PERTAMA </strong>dinilai tidak cakap atau tidak disiplin dalam melakukan tugasnya. <strong>PIHAK KEDUA </strong>wajib menyediakan pengganti <strong>PERSONIL PIHAK KEDUA </strong>dan pengganti itu wajib berada di <strong>LOKASI PEKERJAAN </strong>dalam waktu paling lambat 2 x 24 jam setelah <strong>PIHAK KEDUA </strong>menerima perintah dari <strong>PIHAK PERTAMA</strong>.</span></span></td>\n		</tr>\n		<tr>\n			<td style=\"width: 6.5%;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">5.6.</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><strong>PIHAK KEDUA </strong><font color=\"#000000\">wajib bekerja sama dengan <b>PIHAK KETIGA </b>lainnya yang dipekerjakan oleh <b>PIHAK PERTAMA </b>untuk melaksanakan pekerjaan-pekerjaan lain yang berkaitan dengan <b>PEKERJAAN.</b></font></span></span></td>\n		</tr>\n		<tr>\n			<td style=\"width: 6.5%;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">5.7.</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><strong>PIHAK KEDUA </strong><font color=\"#000000\">wajib mengirimkan laporan pelaksanaan <b>PEKERJAAN </b>seperti yang diatur dalam Lampiran B dan memberikan informasi yang setiap saat dapat diminta oleh <b>PIHAK PERTAMA </b>berkaitan dengan pelaksanaan <b>PEKERJAAN</b>.</font></span></span></td>\n		</tr>\n		<tr>\n			<td style=\"width: 6.5%;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">5.8.</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><strong>PIHAK KEDUA </strong><font color=\"#000000\">wajib sepenuhnya tuntuk kepada dan mematuhi perintah <b>PERINTAH PERTAMA</b>, kecuali secara hokum tidak memungkinkan atau pelaksanaannya akan menimbulkan bahaya terhadap orang atau barang. Jika perintah tersebut akan berakibat pada <b>PEKERJAAN </b>dan <b>PARA PIHAK </b>setuju untuk melakukan perubahan atas ruang lingkup <b>PEKERJAAN, HARGA KONTRAK </b>atau <b>MASA PENYELESAIAN PEKERJAAN</b>, maka perintah tersebut akan dituangkan dalam suatu adendum.</font></span></span></td>\n		</tr>\n		<tr>\n			<td style=\"width: 6.5%;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">5.9.</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><strong>PIHAK KEDUA </strong><font color=\"#000000\">wajib sepenuhnya berusahan menghindarkan terjadinya gangguan dalam bentuk apapun pada kegiatan operasi <b>PIHAK PERTAMA </b>dan/atau kegiatan <b>PIHAK KETIGA </b>lainnya yang dipekerjakan oleh <b>PIHAK PERTAMA </b>lainnya di <b>LOKASI PEKERJAAN</b>.</font></span></span></td>\n		</tr>\n		<tr>\n			<td style=\"width: 6.5%;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">5.10.</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">Selambat-lambatnya <font color=\"#000000\">15 (lima belas) <b>HARI KALENDER</b> sejak <b>TANGGAL SELESAINYA PEKERJAAN </b>atau tanggal putusnya <b>KONTRAK </b>seperti diatur dalam PASAL 17, <b>PIHAK KEDUA </b>atas biayanya sendiri, wajib segera membersihkan dan merapikan <b>LOKASI PEKERJAAN </b>dari sisa-sisa <b>PEKERJAAN </b>dan <b>PERALATAN. </b>Jika <b>PIHAK KEDUA </b>melalaikan kewajibannya tersebut di atas, <b>PIHAK PERTAMA </b>berhak melakukan sendiri atau menyeluruh <b>PIHAK KETIGA </b>untuk melakukan pembersihan dan perapihak seperti yang disebut di atas atas biaya dan risiko <b>PIHAK KEDUA</b>.</font></span></span></td>\n		</tr>\n	</tbody>\n</table>\n\n<p>&nbsp;</p>\n\n<p style=\"text-align: center;\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><strong>PASAL 6</strong></span></span></p>\n\n<p style=\"text-align: center;\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><strong>PIHAK KEDUA ADALAH &quot;KONTRAKTOR MANDIRI&quot;</strong></span></span></p>\n\n<table align=\"left\" border=\"0\" style=\"width: 100%; border-collapse: collapse;\">\n	<tbody>\n		<tr>\n			<td style=\"width: 6.5%;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">6.1.</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">Walaupun <font color=\"#000000\"><strong>PIHAK KEDUA </strong>wajib mengakomodasi dan melaksanakan saran-saran dan perintah <b>PIHAK PERTAMA, PIHAK KEDUA </b>tetap berstatus sebagai kontraktor yang mandiri. <b>PIHAK KEDUA </b>dan <b>PERSONIL PIHAK KEDUA </b>atau <b>SUBKONTRAKTOR </b>bukanlah merupakan pembantu, agen atau personil atau pekerja <b>PIHAK PERTAMA</b>. <b>PIHAK KEDUA </b>bertanggung jawab penuh atas pelaksanaan <b>PEKERJAAN</b>. Pengawasan yang dilakukan dan/atau perintah yang diberikan oleh <b>PIHAK PERTAMA </b>sama sekali tidak mengurangi tanggung jawab tersebut.</font></span></span></td>\n		</tr>\n		<tr>\n			<td style=\"width: 6.5%;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">6.2.</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">Setiap <font color=\"#000000\">persetujuan yang diberikan oleh <b>PIHAK PERTAMA </b>atas usulan <b>PIHAK KEDUA </b>tidak boleh, dengan cara apapun, dianggap sebagai pembatasan atau pembebasan <b>PIHAK KEDUA </b>dari kewajiban dan tanggungjawabnya berdasarkan <b>KONTRAK </b>ini, termasuk tanggung jawab atas konsekuensi yang timbun karena perbuatan-perbuatan yang telah diperintahkan atau disetujui oleh <b>PIHAK PERTAMA.</b></font></span></span></td>\n		</tr>\n	</tbody>\n</table>\n\n<p>&nbsp;</p>\n\n<p style=\"text-align: center;\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><strong>PASAL 7</strong></span></span></p>\n\n<p style=\"text-align: center;\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><strong>KESELAMATAN DAN KESEHATAN KERJA SERTA PERLINDUNGAN LINGKUNGAN</strong></span></span></p>\n\n<table align=\"left\" border=\"0\" style=\"width: 100%; border-collapse: collapse;\">\n	<tbody>\n		<tr>\n			<td style=\"width: 6.5%; text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><font color=\"#000000\"><strong>PIHAK KEDUA </strong>wajib mematuhi ketentuan mengenai keselamatan dan kesehatan kerja serta lindungan lingkungan sebagaimana diatur dalam Lampiran E.</font></span></span></td>\n		</tr>\n	</tbody>\n</table>\n\n<p>&nbsp;</p>\n\n<p>&nbsp;&nbsp;<br />\n&nbsp;</p>\n\n<p>&nbsp;</p>\n\n<p style=\"text-align: center;\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><strong>PASAL 8</strong></span></span></p>\n\n<p style=\"text-align: center;\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><strong>ETIKA BISNIS</strong></span></span></p>\n\n<table align=\"left\" border=\"0\" style=\"width: 100%; border-collapse: collapse;\">\n	<tbody>\n		<tr>\n			<td style=\"width: 6.5%; text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><strong>PIHAK KEDUA </strong><font color=\"#000000\">menjamin bahwa <b>PIHAK KEDUA </b>dan/atau <b>PERSONIL PIHAK KEDUA </b>tidak akan memberikan sesuatu kepada siapapun juga, pemberian mana dapat dianggap sebagai suatu tindak pidana berdasarkan UU Nomor 31 tahun 1999 jo UU Nomor 29 tahun 2001 tentang Pemberantasan Tindak Pidana Korupsi beserta perubahannya.</font></span></span></td>\n		</tr>\n	</tbody>\n</table>\n\n<p>&nbsp;</p>\n\n<p style=\"text-align: center;\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><strong>PASAL 9</strong></span></span></p>\n\n<p style=\"text-align: center;\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><strong>KERAHASIAAN</strong></span></span></p>\n\n<table align=\"left\" border=\"0\" style=\"width: 100%; border-collapse: collapse;\">\n	<tbody>\n		<tr>\n			<td style=\"width: 6.5%;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">9.1.</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">Sepanjang <font color=\"#000000\">tidak dinyatakan sebaliknya oleh <b>PIHAK PERTAMA</b>, seluruh data dan informasi dalam bentuk apapun yang menyangkut atau merupakan hasil dari <b>PEKERJAAN </b>merupakan informasi yang bersifat rahasia (&ldquo;<b>Informasi Rahasia</b>&rdquo;).</font></span></span></td>\n		</tr>\n		<tr>\n			<td style=\"width: 6.5%;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">9.2.</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><strong>PIHAK KEDUA</strong>, <font color=\"#000000\">termasuk <b>PERSONIL PIHAK KEDUA, </b>wajib menjaga kerahasiaan dan dilarang untuk mengungkapkan <b>Informasi Rahasia </b>tersebut kepada pihak lain dengan cara apapun tanpa persetujuan tertulis lenih dulu dari <b>PIHAK PERTAMA.</b></font></span></span></td>\n		</tr>\n		<tr>\n			<td style=\"width: 6.5%;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">9.3.</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">Pengungkapan <font color=\"#000000\"><strong>Informasi Rahasia </strong>kepada <b>PERSONIL PIHAK KEDUA, </b>para pengurus perusahaan, kreditur, auditor atau penasehat professional dari <b>PIHAK KEDUA </b>yang perlu mengetahui <b>KONTRAK </b>ini dalam rangka melaksanakan tugas-tugas mereka dapat dilakukan sepanjang mereka juga tunduk pada kewajiban untuk menjaga kerahasiaan yang sama dengan kewajiban <b>PIHAK KEDUA </b>kepada <b>PIHAK PERTAMA.</b></font></span></span></td>\n		</tr>\n		<tr>\n			<td style=\"width: 6.5%;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">9.4.</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><strong>PIHAK KEDUA </strong><font color=\"#000000\">wajib untuk mengambil seluruh langkah yang diperlukan untuk menyimpan secara baik dan melindungi seluruh <b>Informasi Rahasia</b>, termasuk mewajibkan <b>PERSONIL PIHAK KEDUA </b>untuk mentaati suatu ketentuan untuk menjaga kerahasiaan.</font></span></span></td>\n		</tr>\n		<tr>\n			<td style=\"width: 6.5%;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">9.5.</span></span></td>\n			<td valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">Ketentuan dalam PASAL 9.2 tidak berlaku untuk informasi yang:</span></span></td>\n		</tr>\n		<tr>\n			<td style=\"width: 6.5%; text-align: center;\" valign=\"top\">&nbsp;</td>\n			<td valign=\"top\">\n			<table align=\"left\" border=\"0\" style=\"width: 100%; border-collapse: collapse;\">\n				<tbody>\n					<tr>\n						<td style=\"width: 6.5%; text-align: center;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">a)</span></span></td>\n						<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><font color=\"#000000\">sudah menjadi milik umum (<i>public domain</i>) tanpa lebih dulu terjadi pelanggaran ketentuan kerahasiaan dalam <b>KONTRAK </b>ini; atau</font></span></span></td>\n					</tr>\n					<tr>\n						<td style=\"width: 6.5%; text-align: center;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">b)</span></span></td>\n						<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">merupakan <font color=\"#000000\">milik <b>PIHAK KEDUA </b>yang dapat dibuktikan dengan dokumen kepemilikan yang sah yang sudah ada sebelum <b>KONTRAK </b>ini dan informasi tersebut tidak termasuk yang wajib dijaga kerahasiaannya menurut <b>KONTRAK </b>ini; atau</font></span></span></td>\n					</tr>\n					<tr>\n						<td style=\"width: 6.5%; text-align: center;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">c)</span></span></td>\n						<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">wajib diungkapkan karena diisyaratkan oleh undang-undang atau peraturan atau perintah tertulis resmi dari badan public yang memiliki yurisdiksi atas <strong>PEKERJAAN </strong>atau <strong>PIHAK KEDUA</strong>; atau</span></span></td>\n					</tr>\n					<tr>\n						<td style=\"width: 6.5%; text-align: center;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">d)</span></span></td>\n						<td valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">wajib diungkapkan karena perintah lembaga peradilan.</span></span></td>\n					</tr>\n				</tbody>\n			</table>\n			</td>\n		</tr>\n		<tr>\n			<td style=\"width: 6.5%;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">9.6.</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">Jika <font color=\"#000000\"><strong>PIHAK PERTAMA </strong>terikat dalam suatu perjanjian dengan pemilik data/informasi (<i>confidentiality agreement</i>) berkaitan dengan data/informasi yang diberikan oleh <b>PIHAK PERTAMA </b>kepada <b>PIHAK KEDUA</b>, maka <b>PIHAK KEDUA </b>wajib menandatangani suatu perjanjian kerahasiaan tersendiri, baik dengan <b>PIHAK PERTAMA</b> atau pemilik data/informasi itu, yang isinya sesuai dengan <i>confidentiality agreement</i> antara <b>PIHAK PERTAMA </b>dengan pemilik data/informasi tersebut.</font></span></span></td>\n		</tr>\n		<tr>\n			<td style=\"width: 6.5%;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">9.7.</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><strong>PIHAK KEDUA </strong><font color=\"#000000\">wajib mengembalikan seluruh <b>Informasi Rahasia </b>kepada <b>PIHAK PERTAMA</b>, dan memusnahkan dan/atau menghapus seluruh Salinan yang masih dimiliki oleh <b>PIHAK KEDUA </b>pada saat <b>KONTRAK </b>berakhir.</font></span></span></td>\n		</tr>\n		<tr>\n			<td style=\"width: 6.5%;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">9.8.</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">Kewajiban <font color=\"#000000\"><strong>PIHAK KEDUA </strong>tentang kerahasiaan yang diatur dalam <b>KONTRAK </b>ini akan tetap berlaku setelah berakhirnya <b>KONTRAK.</b></font></span></span></td>\n		</tr>\n		<tr>\n			<td style=\"width: 6.5%;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">9.9.</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">Jika <font color=\"#000000\">berdasarkan <b>KONTRAK </b>ini <b>PIHAK KEDUA </b>diizinkan untuk mengungkapkan <b>Informasi Rahasia </b>kepada pihak lain selain <b>PIHAK PERTAMA</b>, <b>PIHAK KEDUA </b>wajib berusaha agar pengungkapan tersebut tidak perlu dilakukan, atau, jika pengungkapan tersebut tetap wajib dilakukan, <b>PIHAK KEDUA </b>wajib berupaya agar pengungkapan tersebut dibatasi seminimal mungkin. <b>PIHAK KEDUA </b>wajib memberitahukan <b>PIHAK PERTAMA </b>secara tertulis mengenai pengungkapan trersebut paling lambat 3 <b>HARI KALENDER </b>sejak tanggal <b>Informasi Rahasia </b>tersebut diungkapkan atau sejak tanggal diterimanya permintaan untuk mengungkapkan <b>Informasi Rahasia </b>itu, mana yang lebih dulu. Dalam pemberitahuan tersebut, <b>PIHAK KEDUA </b>wajib menjelaskan alasan dan kepada siapa pengungkapan akan dilakukan, serta langkah-langkah yang telah atau akan diambil untuk menghindari dan/atau membatasi pengungkapan tersebut.</font></span></span></td>\n		</tr>\n	</tbody>\n</table>\n\n<p>&nbsp;</p>\n\n<p style=\"text-align: center;\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><strong>PASAL 10</strong></span></span></p>\n\n<p style=\"text-align: center;\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><strong>PROGRAM JAMINAN SOSIAL</strong></span></span></p>\n\n<table align=\"left\" border=\"0\" style=\"width: 100%; border-collapse: collapse;\">\n	<tbody>\n		<tr>\n			<td style=\"width: 6.5%;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">10.1.</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">Selama <font color=\"#000000\"><strong>JANGKA WAKTU KONTRAK, PIHAK KEDUA </strong>wajib mengikutsertakan seluruh <b>PERSONEL PIHAK KEDUA </b>dalam Program Jaminan Sosial Ketenagakerjaan (BPJS Ketenagakerjaan) dan Program Jaminan Sosial Kesehatan (BPJS Kesehatan) berdasarkan ketentuan UU No. 24 tahun 2011 serta perubahannya.</font></span></span></td>\n		</tr>\n		<tr>\n			<td style=\"width: 6.5%;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">10.2.</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><strong>PIHAK KEDUA </strong><font color=\"#000000\">wajib menyerahkan kartu kepesertaan BPJS Ketenagakerjaan dan BPJS Kesehatan kepada <b>PERSONEL PIHAK KEDUA </b>serta Salinan tanda bukti pembayaran iuran Jaminan Sosial Ketenagakerjaan dan Jaminan Sosial Kesehatan sesuai ketentuan BPJS Ketenagakerjaan dan BPJS Kesehatan.</font></span></span></td>\n		</tr>\n		<tr>\n			<td style=\"width: 6.5%;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">10.3.</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><strong>PIHAK KEDUA </strong><font color=\"#000000\">wajib sepenuhnya menanggung seluruh biaya pembayaran iuran Jaminan Sosial Ketenagakerjaan dan Jaminan Sosial Kesehatan untuk setiap <b>PERSONEL PIHAK KEDUA </b>sebagaimana dimaksud PASAL 10.1.</font></span></span></td>\n		</tr>\n		<tr>\n			<td style=\"width: 6.5%;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">10.4.</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><strong>PIHAK KEDUA </strong><font color=\"#000000\">dilarang membatalkan atau mengubah ketentuan program jaminan social yang disyaratkan berdasarkan <b>KONTRAK </b>ini tanpa persetujuan tertulis lebih dahulu dari <b>PIHAK PERTAMA.</b></font></span></span></td>\n		</tr>\n	</tbody>\n</table>\n\n<p>&nbsp;</p>\n\n<p style=\"text-align: center;\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><strong>PASAL 11 </strong></span></span></p>\n\n<p style=\"text-align: center;\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><strong>INFORMASI DAN SARANA YANG DISEDIAKAN OLEH PIHAK PERTAMA</strong></span></span></p>\n\n<table align=\"left\" border=\"0\" style=\"width: 100%; border-collapse: collapse;\">\n	<tbody>\n		<tr>\n			<td style=\"width: 6.5%;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">11.1.</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><strong>PIHAK PERTAMA </strong><font color=\"#000000\">akan menyediakan informasi, saran, akses sebagaimana diatur dalam Lampiran B, jika ada, dengan ketentuan bahwa <b>PIHAK PERTAMA </b>tidak menjamin keakuratan dan/atau memadainya informasi, sarana dan akses tersebut dan <b>PIHAK KEDUA </b>bertanggung jawab sepenuhnya atas risiko penggunaan informasi, sarana dan akses itu.</font></span></span></td>\n		</tr>\n		<tr>\n			<td style=\"width: 6.5%;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">11.2.</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">Jika <font color=\"#000000\">disyaratkan oleh peraturan perundangan, <b>PIHAK PERTAMA </b>dapat memberikan bantuan atau rekomendasi yang sewajarnya diperlukan dalam rangka pengurusan izin atau formalitas lain yang berkaitan dengan pelaksanaan <b>PEKERJAAN.</b></font></span></span></td>\n		</tr>\n		<tr>\n			<td style=\"width: 6.5%;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">11.3.</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">Dengan <font color=\"#000000\">tetap tunduk pada peraturan internal <b>PIHAK PERTAMA, PIHAK PERTAMA </b>akan memberikan akses bagi <b>PERSONIL PIHAK KEDUA </b>untuk masuk ke dan berada di <b>LOKASI PEKERJAAN.</b></font></span></span></td>\n		</tr>\n	</tbody>\n</table>\n\n<p>&nbsp;</p>\n\n<p style=\"text-align: center;\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><strong>PASAL 12</strong></span></span></p>\n\n<p style=\"text-align: center;\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><strong>HARGA KONTRAK DAN KETENTUAN PEMBAYARAN</strong></span></span></p>\n\n<table align=\"left\" border=\"0\" style=\"width: 100%; border-collapse: collapse;\">\n	<tbody>\n		<tr>\n			<td style=\"width: 6.5%;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">12.1.</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><strong>HARGA KONTRAK </strong><font color=\"#000000\">dan ketentuan penagihan serta pembayaran adalah seperti disebut dan diatur dalam Lampiran C.</font></span></span></td>\n		</tr>\n		<tr>\n			<td style=\"width: 6.5%;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">12.2.</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><strong>PIHAK PERTAMA </strong><font color=\"#000000\">berhak untuk menolak pembayaran suatu tagihan jika menurut perndapat <b>PIHAK PERTAMA</b>, tagihan tersebut tidak sesuai dengan <b>KONTRAK </b>ini. <b>PIHAK PERTAMA </b>akan memberitahukan penolakan tersebut secara tertulis kepada <b>PIHAK KEDUA.</b></font></span></span></td>\n		</tr>\n		<tr>\n			<td style=\"width: 6.5%;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">12.3.</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">Jika <font color=\"#000000\"><strong>PIHAK PERTAMA </strong>tidak mengirimkan pemberitahuan tertulis tentang penolakan sebagaimana diatur dalam PASAL 12.2 di atas, tagihan <b>PIHAK KEDUA </b>akan dibayar selamabat-lambatnya pada waktu yang disebut dalam Lampiran C.</font></span></span></td>\n		</tr>\n		<tr>\n			<td style=\"width: 6.5%;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">12.4.</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">Seluruh tagihan <font color=\"#000000\"><strong>PIHAK KEDUA </strong>wajib dilengkapi dengan dokumen pendukung sekurang-kurangnya sebagai berikut:</font></span></span></td>\n		</tr>\n		<tr>\n			<td style=\"width: 6.5%; text-align: center;\" valign=\"top\">&nbsp;</td>\n			<td valign=\"top\">\n			<table align=\"left\" border=\"0\" style=\"width: 100%; border-collapse: collapse;\">\n				<tbody>\n					<tr>\n						<td style=\"width: 6.5%; text-align: center;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">a.</span></span></td>\n						<td valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">Fotokopi <strong>KONTRAK;</strong></span></span></td>\n					</tr>\n					<tr>\n						<td style=\"width: 6.5%; text-align: center;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">b.</span></span></td>\n						<td valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><em>Invoice </em>bermaterai;</span></span></td>\n					</tr>\n					<tr>\n						<td style=\"width: 6.5%; text-align: center;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">c.</span></span></td>\n						<td valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">Asli <em>service acceptance</em>/bukti penerimaan hasil pekerjaan;</span></span></td>\n					</tr>\n					<tr>\n						<td style=\"width: 6.5%; text-align: center;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">d.</span></span></td>\n						<td valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">Faktur pajak atau dokumen perpajakan lain sesuai peraturan perpajakan yang berlaku;</span></span></td>\n					</tr>\n					<tr>\n						<td style=\"width: 6.5%; text-align: center;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">e.</span></span></td>\n						<td valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">Salinan bukti pembayaran iuran BPJS Kesehatan dan BPJS Ketenagakerjaan.</span></span></td>\n					</tr>\n				</tbody>\n			</table>\n			</td>\n		</tr>\n		<tr>\n			<td style=\"width: 6.5%;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">12.5.</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">Pembayaran <font color=\"#000000\">tagihan oleh <b>PIHAK PERTAMA </b>tidak meniadakan hak <b>PIHAK PERTAMA </b>untuk di kemudian hari mempersengketakan pembayaran yang telah dilakukan tersebut. Pembayaran tersebut juga bukan merupakan bukti bahwa <b>PIHAK PERTAMA </b>telah menerima hasil atau bagian dari <b>PEKERJAAN </b>atau bukti bahwa <b>PIHAK KEDUA </b>telah melaksanakan seluruh kewajibannya berdasarkan <b>KONTRAK </b>ini.</font></span></span></td>\n		</tr>\n		<tr>\n			<td style=\"width: 6.5%;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">12.6.</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><strong>PIHAK KEDUA </strong><font color=\"#000000\">tidak berhak atas pembayaran lain apapun juga kecuali yang secara tegas disebut dan diatur dalam <b>KONTRAK </b>ini.</font></span></span></td>\n		</tr>\n		<tr>\n			<td style=\"width: 6.5%;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">12.7.</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">Seluruh <font color=\"#000000\">tagihan menurut <b>KONTRAK </b>ini wajib dilakukan dalam mata uang yang disebut dalam Lampiran C. <b>PIHAK EPRTAMA </b>berhak untuk membayar suatu tagihan dengan mata uang yang berbeda dengan mata uang yang digunakan dalam tagihan. Jika <b>PIHAK PERTAMA </b>menggunakan hak tersebut, maka nilai tukar yang akan digunakan adalah nilai tukar yang berlaku pada hari pertama dari bulan dimana tagihan bersangkutan dibayar, kecuali ditentukan lain dalam Lampiran C.</font></span></span></td>\n		</tr>\n	</tbody>\n</table>\n\n<p>&nbsp;</p>\n\n<p style=\"text-align: center;\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><strong>PASAL 13</strong></span></span></p>\n\n<p style=\"text-align: center;\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><strong>PAJAK DAN PUNGUTAN-PUNGUTAN LAINNYA</strong></span></span></p>\n\n<table align=\"left\" border=\"0\" style=\"width: 100%; border-collapse: collapse;\">\n	<tbody>\n		<tr>\n			<td style=\"width: 6.5%; text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">Masing-masing <strong>PIHAK </strong><font color=\"#000000\">akan menanggung dan membayar pajak dan pungutan-pungutan lainnya sesuai ketentuan yang diatur di dalam Lampiran C.</font></span></span></td>\n		</tr>\n	</tbody>\n</table>\n\n<p>&nbsp;</p>\n\n<p style=\"text-align: center;\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><strong>PASAL 14</strong></span></span></p>\n\n<p style=\"text-align: center;\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><strong>SANKSI DAN DENDA</strong></span></span></p>\n\n<table align=\"left\" border=\"0\" style=\"width: 100%; border-collapse: collapse;\">\n	<tbody>\n		<tr>\n			<td style=\"width: 6.5%;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">14.1.</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><strong>PIHAK PERTAMA </strong><font color=\"#000000\">berhak mengenakan sanksi atau denda, seperti diatur dalam Lampiran C, terhadap <b>PIHAK KEDUA </b>apabila <b>PIHAK KEDUA </b>tidak melaksanakan salah satu atau lebih ketentuan <b>KONTRAK </b>ini.</font></span></span></td>\n		</tr>\n		<tr>\n			<td style=\"width: 6.5%;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">14.2.</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><strong>PIHAK PERTAMA </strong><font color=\"#000000\">berhak memotong sanksi atau denda yang berbentuk uang dari pembayaran <b>HARGA KONTRAK.</b></font></span></span></td>\n		</tr>\n		<tr>\n			<td style=\"width: 6.5%;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">14.3.</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">Pengenaan satu <font color=\"#000000\">sanksi atau denda tidak mengecualikan pengenaan sanksi atau denda lainnya dan lebih dari satu sanksi atau denda dapat dijatuhkan sekaligus. Pengenaan denda dan/atau sanksi administrative juga tidak meniadakan hak <b>PIHAK PERTAMA </b>untuk melakukan pumutusan <b>KONTRAK</b>.</font></span></span></td>\n		</tr>\n	</tbody>\n</table>\n\n<p>&nbsp;&nbsp;<br />\n&nbsp;<br />\n&nbsp;</p>\n\n<p>&nbsp;</p>\n\n<p style=\"text-align: center;\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><strong>PASAL 15</strong></span></span></p>\n\n<p style=\"text-align: center;\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><strong>TANGGUNG JAWAB DAN GANTI RUGI</strong></span></span></p>\n\n<table align=\"left\" border=\"0\" style=\"width: 100%; border-collapse: collapse;\">\n	<tbody>\n		<tr>\n			<td style=\"width: 6.5%;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">15.1.</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><strong>PIHAK KEDUA </strong><font color=\"#000000\">bertanggung jawab penuh atas dan karenanya membebaskan dan melindungi <b>PIHAK PERTAMA </b>dari seluruh macam klaim, tuntutan serta putusan yang timbul dari atau berkaitan dengan a) cedera atau kematian <b>PERSONIL PIHAK KEDUA, </b>dan b) rusak atau hilangnya harta benda <b>PERSONIL PIHAK KEDUA, </b>kecuali hal-hal tersebut disebabkan oleh <b>KESENGAJAAN DAN/ATAU KELALAIAN PIHAK PERTAMA</b>. Sehubungan dengan tanggung jawab yang disebut di atas, <b>PIHAK KEDUA </b>akan mengganti rugi (termasuk biaya hukum yang dikeluarkan) <b>PIHAK PERTAMA </b>berkaitan dengan klain, tuntutan serta putusan tersebut.</font></span></span></td>\n		</tr>\n		<tr>\n			<td style=\"width: 6.5%;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">15.2.</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><strong>PIHAK KEDUA </strong><font color=\"#000000\">bertanggung jawab atas dan karenanya membebaskan dan melindungi <b>PIHAK PERTAMA </b>dari seluruh macam klaim, tuntutan serta putusan yang timbul dari atau berkaitan dengan a) cedera atau kematian <b>PIHAK KETIGA</b>, atau b) rusak atau hilangnya harta benda <b>PIHAK KETIGA</b>, kecuali hal-hal tersebut disebabkan oleh <b>KESENGAJAAN DAN/ATAU KELALAIAN PIHAK PERTAMA. </b>Sehubungan dengan tanggung jawab yang disebut di atas, <b>PIHAK KEDUA </b>akan mengganti rugi (termasuk biaya hukum yang dikeluarkan) <b>PIHAK PERTAMA </b>berkaitan dengan klaim, tuntutan serta putusan tersebut.</font></span></span></td>\n		</tr>\n		<tr>\n			<td style=\"width: 6.5%;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">15.3.</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><strong>PIHAK PERTAMA </strong><font color=\"#000000\">bertanggung jawab atas dan karenanya membebaskan dan melindungi <b>PIHAK KEDUA </b>dari seluruh macam klaim, tuntutan serta putusan yang timbul dari atau berkaitan dengan a) cedera atau kematian <b>PERSONIL PIHAK PERTAMA</b>, atau b) rusak atau hilangnya harta benda <b>PERSONIL PIHAK PERTAMA</b>, kecuali hal-hak tersebut disesbabkan oleh <b>KESENGAJAAN DAN/ATAU KELALAIAN PIHAK KEDUA</b>. Sehubungan dengan tanggung jawab yang disebut di atas, <b>PIHAK PERTAMA </b>akan mengganti rugi (termasuk biaya yang hukum dikeluarkan) <b>PIHAK KEDUA </b>berkaitan dengan klaim, tuntutan serta putusan tersebut.</font></span></span></td>\n		</tr>\n		<tr>\n			<td style=\"width: 6.5%;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">15.4.</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><strong>PIHAK PERTAMA </strong><font color=\"#000000\">bertanggung&nbsp; jawab atas dan karenanya membebaskan dan melindungi <b>PIHAK KEDUA </b>dari kerugian yang timbul berkairan dengan rusak atau hilangnya harta benda <b>PIHAK PERTAMA</b>, kecuali kerusakan atau kehilangan itu disebabkan oleh <b>KESENGAJAAN DAN/ATAU KELALAIAN PIHAK KEDUA.</b></font></span></span></td>\n		</tr>\n		<tr>\n			<td style=\"width: 6.5%;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">15.5.</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><strong>PIHAK KEDUA </strong><font color=\"#000000\">bertanggung jawab atas dan karenanya membebaskan dan melindungi <b>PIHAK PERTAMA </b>dari kerugian yang timbul dari atau berkaitan dengan rusak atau hilangnya <b>PERALATAN, MATERIAL </b>serta harta benda <b>PIHAK KEDUA </b>lainnya.</font></span></span></td>\n		</tr>\n		<tr>\n			<td style=\"width: 6.5%;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">15.6.</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><strong>PIHAK KEDUA </strong><font color=\"#000000\">bertanggung jawab atas dan karenanya membebaskan dan melindungi <b>PIHAK PERTAMA </b>dari tuntutan dan kerugian yang timbul dari atau berkaitan tuntutan hak-hak pekerja atau hak perburuhan <b>PERSONIL PIHAK KEDUA</b>, <b>PIHAK PERTAMA </b>berhak untuk meminta <b>PIHAK KEDUA </b>untuk menunjukkan bukti tertulis pemenuhan kewajiban <b>PIHAK KEDUA </b>terhadap <b>PERSONIL PIHAK KEDUA </b>dan <b>PIHAK PERTAMA </b>berhak untuk menahan pembayaran tagihan <b>PIHAK KEDUA </b>jika kewajiban-kewajiban <b>PIHAK KEDUA </b>tersebut belum dipenuhi.</font></span></span></td>\n		</tr>\n		<tr>\n			<td style=\"width: 6.5%;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">15.7.</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><strong>PIHAK KEDUA </strong><font color=\"#000000\">bertanggung jawab atas, dan karenanya membebaskan dan melindungi <b>PIHAK PERTAMA </b>dari tuntutan dan kerugian yang timbul dari atau berkaitan dengan tidak dipenuhinya ketentuan perundangan termasuk dilanggarnya peraturan tentang kelestarian lingkungan hidup dan <b>HAK KEKAYAAN INTELEKTRUAL </b>oleh <b>PIHAK KEDUA.</b></font></span></span></td>\n		</tr>\n		<tr>\n			<td style=\"width: 6.5%;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">15.8.</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">Dalam <font color=\"#000000\">keadaan apapun, tidak ada <b>PIHAK </b>yang wajib bertanggung jawab atas kerugian yang tidak langsung (<i>indirect and consequential damages</i>), seperti misalnya: kehilangan kesempatan bisnis atau kehilangan keuntungan diderita oleh <b>PIHAK </b>yang lainnya.</font></span></span></td>\n		</tr>\n	</tbody>\n</table>\n\n<p>&nbsp;</p>\n\n<p style=\"text-align: center;\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><strong>PASAL 16</strong></span></span></p>\n\n<p style=\"text-align: center;\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><strong>KEADAAN KAHAR</strong></span></span></p>\n\n<table align=\"left\" border=\"0\" style=\"width: 100%; border-collapse: collapse;\">\n	<tbody>\n		<tr>\n			<td style=\"width: 6.5%;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">16.1.</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">Suatu <strong>PIHAK </strong>dibebaskan <font color=\"#000000\">dari kewajibannya berdasarkan <b>KONTRAK </b>ini, jika kewajiban tersebut tidak dapat dilaksanakan karena terjadinya suatu keadaan yang berada di luar kendali yang wajar dari <b>PIHAK </b>tersebut, tidak dapat dihindari meskipun dengan perencanaan yang baik dan tidak dapat diatasi dengan upaya yang wajar (&ldquo;<b>KEADAAN KAHAR</b>&rdquo;).</font></span></span></td>\n		</tr>\n		<tr>\n			<td style=\"width: 6.5%;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">16.2.</span></span></td>\n			<td valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">Kejadian-<font color=\"#000000\">kejadian berikut adalah <b>KEADAAN KAHAR</b>:</font></span></span></td>\n		</tr>\n		<tr>\n			<td style=\"width: 6.5%;\" valign=\"top\">&nbsp;</td>\n			<td valign=\"top\">\n			<table align=\"left\" border=\"0\" style=\"width: 100%; border-collapse: collapse;\">\n				<tbody>\n					<tr>\n						<td style=\"width: 6.5%; text-align: center;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">a)</span></span></td>\n						<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">kerusuhan <font color=\"#000000\">masal, perang saudara, pemberontakan, perebutan kekuasaan, perang dengan negara lain atau terorisme; atau</font></span></span></td>\n					</tr>\n					<tr>\n						<td style=\"width: 6.5%; text-align: center;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">b)</span></span></td>\n						<td valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">gempa <font color=\"#000000\">bumi, bajir, kebakaran, ledakan gunung berapi dan/atau bencana alam lainnya; atau</font></span></span></td>\n					</tr>\n					<tr>\n						<td style=\"width: 6.5%; text-align: center;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">c)</span></span></td>\n						<td valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">sengketa <font color=\"#000000\">industrial atau pemogokan masal yang terjadi di tingkat nasional maupun daerah; atau</font></span></span></td>\n					</tr>\n					<tr>\n						<td style=\"width: 6.5%; text-align: center;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">d)</span></span></td>\n						<td valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">perubahan <font color=\"#000000\">peraturan perundang-undangan nasional maupun daerah secara material.</font></span></span></td>\n					</tr>\n				</tbody>\n			</table>\n			</td>\n		</tr>\n		<tr>\n			<td style=\"width: 6.5%;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">16.3.</span></span></td>\n			<td valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">Suatu <strong>PIHAK </strong>hanya akan dibebaskan dari kewajibannya berdasarkan <strong>KONTRAK </strong>ini dengan alasan <strong>KEADAAN KAHAR</strong> jika:</span></span></td>\n		</tr>\n		<tr>\n			<td style=\"width: 6.5%;\" valign=\"top\">&nbsp;</td>\n			<td valign=\"top\">\n			<table align=\"left\" border=\"0\" style=\"width: 100%; border-collapse: collapse;\">\n				<tbody>\n					<tr>\n						<td style=\"width: 6.5%; text-align: center;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">a)</span></span></td>\n						<td valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">keadaan dimaksud berdampak langsung pada pelaksanaan kewajiba <strong>PIHAK </strong>tersebut, dan</span></span></td>\n					</tr>\n					<tr>\n						<td style=\"width: 6.5%; text-align: center;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">b)</span></span></td>\n						<td valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">tidak ada unsur <strong>KESENGAJAAN DAN/ATAU KELALAIAN </strong>yang dilakukan oleh <strong>PIHAK </strong>tersebut.</span></span></td>\n					</tr>\n				</tbody>\n			</table>\n			</td>\n		</tr>\n		<tr>\n			<td style=\"width: 6.5%;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">16.4.</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><strong>PIHAK </strong><font color=\"#000000\">yang mengalami <b>KEADAAN KAHAR</b> wajib memberitahukan <b>PIHAK </b>lainnya secara lisan selambat-lambatnya dalam waktu 1 x 24 jam sejak terjadi <b>KEADAAN KAHAR</b> yang diikuti dengan pemberitahuan tertulis paling lambat dalam waktu 7 (tujuh) <b>HARI KALENDER </b>setelah tanggal terjadinya <b>KEADAAN KAHAR </b>tersebut. Pemberitahuan itu sekurang-kurangnya wajib menjelaskan jenis <b>KEADAAN KAHAR </b>yang terjadi, perkiraan lamanya <b>KEADAAN KAHAR </b>akan berlangsung dan upaya-upaya penanggulangan yang telah dan akan dilakukan oleh <b>PIHAK </b>yang mengirimkan pemberitahuan.</font></span></span></td>\n		</tr>\n		<tr>\n			<td style=\"width: 6.5%;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">16.5.</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><strong>PIHAK </strong><font color=\"#000000\">yang mengalami <b>KEADAAN KAHAR </b>wajib mengambil langkah-langkah yang diperlukan agar <b>PIHAK </b>tersebut dapat melanjutkan pelaksanaan kewajibannya sesuai <b>KONTRAK.</b></font></span></span></td>\n		</tr>\n		<tr>\n			<td style=\"width: 6.5%;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">16.6.</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">Apabila <font color=\"#000000\">dalam waktu 7 (tujuh) <b>HARI KALENDER </b>sejak terjadinya <b>KEADAAN KAHAR</b>, <b>PIHAK </b>yang mengalami keadaan itu tidak mengirimkan pemberitahuan sesuai dengan PASAL 16.4 di atas, maka <b>KEADAAN KAHAR </b>dianggap tidak pernah terjadi.</font></span></span></td>\n		</tr>\n		<tr>\n			<td style=\"width: 6.5%;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">16.7.</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><strong>PIHAK </strong><font color=\"#000000\">yang menerima pemberitahuan <b>KEADAAN KAHAR</b> dapat menolak mengakui adanya <b>KEADAAN KAHAR </b>selambat-lambatnya 7 (tujuh) <b>HARI KALENDER </b>setelah tanggal diterimanya pemberitahuan sebagaimana dimaksud PASAL 16.4 di atas. Apabila dalam jangka waktu 7 (tujuh) <b>HARI KALENDER </b>tersebut tidak ada penolakan dari <b>PIHAK </b>yang diberitahu, maka <b>PIHAK </b>itu dianggap mengakui adanya suatu <b>KEADAAN KAHAR.</b></font></span></span></td>\n		</tr>\n		<tr>\n			<td style=\"width: 6.5%;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">16.8.</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">Apabila <font color=\"#000000\">adanya <b>KEADAAN KAHAR</b> ditolak untuk diakui oleh <b>PIHAK </b>yang diberitahu, maka <b>PIHAK </b>yang menyatakan <b>KEADAAN KAHAR </b>tersebut wajib tetap melaksanakan kewajibannya sesuai <b>KONTRAK </b>ini.</font></span></span></td>\n		</tr>\n		<tr>\n			<td style=\"width: 6.5%;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">16.9.</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">Jika <font color=\"#000000\"><strong>PIHAK </strong>yang mengalami <b>KEADAAN KAHAR </b>berkeberatan atas penolakan oleh <b>PIHAK </b>yang diberitahu, maka <b>PIHAK </b>yang berkeberatan atas penolakan itu dapat meminta agar keberatannya diselesaikan melalui mekanisme penyelesaian perselisihan sebagaimana diatur dalam <b>KONTRAK </b>ini.</font></span></span></td>\n		</tr>\n		<tr>\n			<td style=\"width: 6.5%;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">16.10.</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">Apabila <font color=\"#000000\">terjadinya <b>KEADAAN KAHAR </b>tersebut diakui oleh <b>PIHAK </b>yang diberitahu, maka <b>PARA PIHAK </b>akan merundingkan perubahan-perubahan yang diperlukan agar <b>KONTRAK </b>dapat tetap dilaksanakan.</font></span></span></td>\n		</tr>\n	</tbody>\n</table>\n\n<p>&nbsp;</p>\n\n<p style=\"text-align: center;\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><strong>PASAL 17</strong></span></span></p>\n\n<p style=\"text-align: center;\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><strong>PEMUTUSAN KONTRAK LEBIH AWAL</strong></span></span></p>\n\n<table align=\"left\" border=\"0\" style=\"width: 100%; border-collapse: collapse;\">\n	<tbody>\n		<tr>\n			<td style=\"width: 6.5%;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">17.1.</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><strong>PARA PIHAK </strong><font color=\"#000000\">sepakat untuk mengesampingkan berlakunya ketentuan Pasal 1266 dan 1267 Kitab Undang-Undang Hukum Perdata terhadap <b>KONTRAK </b>ini berkenaan dengan persyaratan adanya suatu putusan pengadilan untuk pembatalan atau pengakhiran lebih awal suatu perjanjian. <b>PARA PIHAK </b>sepakat bahwa Pasal 1267 Kitab Undang-Undang Hukum Perdata tidak akan ditafsirkan sehingga pengadilan lah yang mempunyai hak untuk menjatuhkan putusan tentang pelaksanaan perjanjian dan/atau pemberian ganti rugi.</font></span></span></td>\n		</tr>\n		<tr>\n			<td style=\"width: 6.5%;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">17.2.</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><strong>PIHAK PERTAMA </strong><font color=\"#000000\">berhak memutuskan <b>KONTRAK </b>secara sepihak dan seketika tanpa kewajiban untuk memberikan ganti rugi kepada <b>PIHAK KEDUA, </b>dengan pemberitahuan tertulis, apabila:</font></span></span></td>\n		</tr>\n		<tr>\n			<td style=\"width: 6.5%; text-align: center;\" valign=\"top\">&nbsp;</td>\n			<td valign=\"top\">\n			<table align=\"left\" border=\"0\" style=\"width: 100%; border-collapse: collapse;\">\n				<tbody>\n					<tr>\n						<td style=\"width: 6.5%;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">a)</span></span></td>\n						<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><strong>PIHAK KEDUA </strong><font color=\"#000000\">melakukan <b>KESENGAJAAN DAN/ATAU KELALAIAN </b>dengan catatan bahwa <b>PIHAK PERTAMINA </b>dapat (namun tidak wajib) secara tertulis memberikan kesempatan kepada <b>PIHAK KEDUA </b>untuk memperbaiki akibat yang ditimbulkan atau memulihkan keadaan yang timbul dari <b>KESENGAJAAN DAN/ATAU KELALAIAN </b>tersebut dan <b>KONTRAK </b>ini putus jika, menurut pandangan <b>PIHAK PERTAMA </b>sendiri, perbaikan atau pemulihan itu tidak berhasil dilakukan oleh <b>PIHAK KEDUA </b>dalam waktu yang diberikan <b>PIHAK PERTAMA</b>; atau</font></span></span></td>\n					</tr>\n					<tr>\n						<td style=\"width: 6.5%;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">b)</span></span></td>\n						<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">Telah <font color=\"#000000\">dimulainya proses kepailitan atau telah diajukannya permohonan Penundaan Kewajiban Pembayaran Utang (&ldquo;<b>PKPU</b>&rdquo;) terhadap <b>PIHAK KEDUA </b>sebagaimana dimaksud dalam Undang-Undang Nomor 36 Tahun 2004 tentang Kepailitan dan PKPU; atau</font></span></span></td>\n					</tr>\n					<tr>\n						<td style=\"width: 6.5%;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">c)</span></span></td>\n						<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><strong>PIHAK PERTAMA </strong><font color=\"#000000\">menjatuhkan sanksi administrative terhadap <b>PIHAK KEDUA </b>yang dapat mempengaruhi pelaksanaan <b>KONTRAK </b>ini;</font></span></span></td>\n					</tr>\n					<tr>\n						<td style=\"width: 6.5%;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">d)</span></span></td>\n						<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">diputuskannya <font color=\"#000000\">kontrak lain antara <b>PIHAK KEDUA </b>dan <b>PIHAK PERTAMA </b>atau <b>AFILIASI PIHAK PERTAMA </b>karena kesalahan <b>PIHAK KEDUA </b>atau</font></span></span></td>\n					</tr>\n					<tr>\n						<td style=\"width: 6.5%;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">e)</span></span></td>\n						<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><strong>PIHAK KEDUA </strong><font color=\"#000000\">atau <b>AFILIASI </b>nya telah atau sedang memperkarakan <b>PIHAK PERTAMA </b>atau <b>AFILIASI </b>nya secara hukum mengenai suatu hal, meskipun tidak berkaitan dengan <b>KONTRAK </b>ini<b>.</b></font></span></span></td>\n					</tr>\n				</tbody>\n			</table>\n			</td>\n		</tr>\n		<tr>\n			<td style=\"width: 6.5%;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">17.3.</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">Pemutusan <strong>KONTRAK </strong><font color=\"#000000\">sebagaimana dimaksud dalam PASAL 17.2 di atas berlaku sejak tanggal surat pemberitahuan tertulis dari <b>PIHAK PERTAMA </b>atau suatu tanggal lain yang disebut dalam surat pemberitahuan tertulis itu.</font></span></span></td>\n		</tr>\n		<tr>\n			<td style=\"width: 6.5%;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">17.4.</span></span></td>\n			<td valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">Jika <font color=\"#000000\">terjadi pemutusan <b>KONTRAK </b>sebagaimana diatur dalam PASAL 17.2 di atas, maka:</font></span></span></td>\n		</tr>\n		<tr>\n			<td style=\"width: 6.5%;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">17.5.</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">Salah <font color=\"#000000\">satu <b>PIHAK </b>dapat memutuskan <b>KONTRAK </b>apabila terjadi <b>KEADAAN KAHAR </b>yang berlangsung lebih dari 7 (tujuh) <b>HARI KALENDER</b>. Jika <b>KONTRAK </b>diputuskan karena <b>KEADAAN KAHAR </b>seperti disebut dalam Pasal ini, maka <b>PIHAK KEDUA </b>dapat menagih dan <b>PIHAK PERTAMA </b>akan membayar hasil <b>PEKERJAAN </b>yang telah dilaksanakan dan/atau <b>MATERIAL </b>yang akan diserahkan oleh <b>PIHAK KEDUA </b>dan diterima dengan baik oleh <b>PIHAK PERTAMA </b>sampai dengan tanggal berlaku pemutusan <b>KONTRAK </b>ini.</font></span></span></td>\n		</tr>\n		<tr>\n			<td style=\"width: 6.5%;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">17.6.</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">Setiap <font color=\"#000000\">saat <b>PIHAK PERTAMA </b>berdasarkan alasan dan/atau pertimbangannya sendiri berhak secara sepihak memutuskan <b>KONTRAK </b>ini dengan pemberitahuan tertulis kepada <b>PIHAK KEDUA</b>, paling lambat 7 (tujuh) <b>HARI KALENDER </b>sebelum tanggal berlaku pemutusan <b>KONTRAK. </b>Jika <b>KONTRAK </b>diputus secara sepihak seperti diatur dalam Pasal ini, <b>PIHAK KEDUA </b>dapat menagih dan <b>PIHAK PERTAMA </b>akan membayar hasil <b>PEKERJAAN </b>yang telah dilaksanakan dan/atau <b>MATERIAL </b>yang telah diserahkan oleh <b>PIHAK KEDUA </b>dan diterima dengan baik oleh <b>PIHAK PERTAMA </b>sampai dengan tanggal berlaku pemutusan <b>KONTRAK </b>ini.</font></span></span></td>\n		</tr>\n		<tr>\n			<td style=\"width: 6.5%;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">17.7.</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">Jika <font color=\"#000000\">terjadi pemutusan <b>KONTRAK, PIHAK KEDUA </b>tetap wajib melaksanakan seluruh ketentuan <b>KONTRAK </b>sampai tanggal berlaku pemutusan <b>KONTRAK </b>sebagaimana diatur di atas.</font></span></span></td>\n		</tr>\n	</tbody>\n</table>\n\n<p style=\"text-align: center;\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">&nbsp;</span></span><br />\n&nbsp;</p>\n\n<div style=\"page-break-after: always\"><span style=\"display: none;\">&nbsp;</span></div>\n\n<p style=\"text-align: right;\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">LAMPIRAN B</span></span></p>\n\n<p style=\"text-align: center;\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><strong>LINGKUP PEKERJAAN</strong></span></span></p>\n\n<p align=\"center\" style=\"margin: 0cm 0cm 10pt; text-align: center;\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><b>PASAL 1</b></span></span></p>\n\n<p align=\"center\" style=\"margin: 0cm 0cm 10pt; text-align: center;\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><b>DASAR PELAKSANAAN PEKERJAAN</b></span></span></p>\n\n<table align=\"left\" style=\"width: 100%; border-collapse: collapse;\">\n	<tbody>\n		<tr>\n			<td style=\"width: 5%; text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">1.1</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">Dasar pelaksanaan <strong>PEKERJAAN </strong>dalam <strong>KONTRAK </strong>adalah :</span></span></td>\n		</tr>\n		<tr>\n			<td style=\"text-align: justify;\" valign=\"top\">&nbsp;</td>\n			<td valign=\"top\">\n			<table align=\"left\" style=\"width: 100%; border-collapse: collapse;\">\n				<tbody>\n					<tr>\n						<td style=\"width: 5%; text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">1.</span></span></td>\n						<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">Nomor pemilihan langsung No.&nbsp;001A-ABO/I03127/2019</span></span></td>\n					</tr>\n					<tr>\n						<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">2.</span></span></td>\n						<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><i>Request For Quotation</i> (RFQ) dari&nbsp;<strong>PIHAK PERTAMA</strong><b>&nbsp;</b>kepada&nbsp;<b>PIHAK KEDUA&nbsp;</b>No.&nbsp;660006233  beserta semua kelengkapannya.</span></span></td>\n					</tr>\n					<tr>\n						<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">3.</span></span></td>\n						<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">Berita Acara Penjelasan (<i>Aanwijzing</i> Kantor) No. BA.&nbsp;- tanggal -.</span></span></td>\n					</tr>\n					<tr>\n						<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">4.</span></span></td>\n						<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">Surat Penawaran dan dokumen-dokumen kelengkapannya yang diserahkan&nbsp;<b>PIHAK KEDUA&nbsp;</b>kepada<b> </b><b>PIHAK PERTAMA </b>No. 039/MS/I/2019<b>&nbsp;</b>tanggal&nbsp;18 Januari 2019 Perihal Penawaran Adminsitrasi/Teknis, untuk selanjutnya disebut SURAT PENAWARAN.</span></span></td>\n					</tr>\n					<tr>\n						<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">5.</span></span></td>\n						<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">Berita Acara Negosiasi Harga (Manual) No. BA.&nbsp;219/I03127/2019-S5&nbsp;tanggal 15 Februari 2019.</span></span></td>\n					</tr>\n					<tr>\n						<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">6.</span></span></td>\n						<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">Surat&nbsp; <b>PIHAK KEDUA&nbsp;</b>kepada <b>PIHAK PERTAMA&nbsp;</b>No.&nbsp;084/MS/II/2019 tanggal&nbsp;02/18/2019 perihal Penawaran Harga - Negosiasi.</span></span></td>\n					</tr>\n					<tr>\n						<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">7.</span></span></td>\n						<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">Surat&nbsp;&nbsp;<b>PIHAK KEDUA&nbsp;</b>kepada<b> </b><b>&nbsp;PIHAK PERTAMA&nbsp;</b>No.&nbsp;085/MS/II/2019 tanggal&nbsp;02/18/2019 perihal Pernyataan Kesanggupan Melaksanakan Pekerjaan.</span></span></td>\n					</tr>\n					<tr>\n						<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">8.</span></span></td>\n						<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">Surat Keputusan Penetapan Pelaksana Pekerjaan Pemilihan Langsung dari <b>PIHAK PERTAMA </b>kepada <b>PIHAK KEDUA </b>No. Kpts-001/F00237/2018-S0 tanggal 02/25/2019.</span></span></td>\n					</tr>\n					<tr>\n						<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">9.</span></span></td>\n						<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">Surat Penunjukan Pemenang dari <b>PIHAK PERTAMA</b>&nbsp;kepada&nbsp;<b>PIHAK KEDUA</b>&nbsp;No.&nbsp;066/I03127/2019-S5 tanggal 02/28/2019.</span></span></td>\n					</tr>\n					<tr>\n						<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">10.</span></span></td>\n						<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">Lingkup Pekerjaan sebagaimana diuraikan lebih rinci dalam RKS / Dokumen Pengadaan, Bill of Quantity, dan Berita Acara Rapat Penjelasan.</span></span></td>\n					</tr>\n					<tr>\n						<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">11.</span></span></td>\n						<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">Semua dokumen yang terkait dengan proses pengadaan <b>PEKERJAAN</b>.</span></span></td>\n					</tr>\n					<tr>\n						<td colspan=\"2\" style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">Dokumen-dokumen tersebut diatas selanjutnya disebut &ldquo;<b>DOKUMEN PENGADAAN</b>&rdquo;</span></span></td>\n					</tr>\n				</tbody>\n			</table>\n\n			<p>&nbsp;</p>\n			</td>\n		</tr>\n		<tr>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">1.2.</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">Apabila terdapat pengertian/penafsiran yang berbeda antara <b>DOKUMEN KONTRAK </b>termasuk perubahan - perubahannya dengan <b>DOKUMEN PENGADAAN</b>, maka ketentuan dalam &nbsp;<b>DOKUMEN KONTRAK</b> yang berlaku sebagai dasar penyelesaian masalah.</span></span></td>\n		</tr>\n		<tr>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">1.3</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">Apabila terdapat pengertian/penafsiran yang berbeda antara dokumen-dokumen yang termasuk dalam <b>DOKUMEN PENGADAAN</b>, maka <b>PERUSAHAAN </b>akan menentukan dokumen mana yang berlaku.</span></span></td>\n		</tr>\n	</tbody>\n</table>\n\n<p>&nbsp;</p>\n\n<p style=\"text-align: center;\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><b>PASAL 2</b></span></span></p>\n\n<p align=\"center\" class=\"CxSpLast\" style=\"text-align: center; margin-right: -2.15pt;\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><b>LINGKUP PEKERJAAN</b></span></span></p>\n\n<table align=\"left\" style=\"width: 100%; border-collapse: collapse;\">\n	<tbody>\n		<tr>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">2.1.&nbsp;&nbsp;&nbsp;</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><strong>PIHAK KEDUA</strong>&nbsp;wajib melaksanakan Jasa Pemeriksaan Kesehatan Harian di Lokasi Operasional MOR VII di <b>LOKASI PEKERJAAN</b> sesuai ketentuan <b>KONTRAK</b> dan <b>DOKUMEN PENGADAAN</b> (selanjutnya disebut &ldquo;<b>PEKERJAAN</b>&rdquo;) dengan lingkup <b>PEKERJAAN</b> meliputi:</span></span></td>\n		</tr>\n		<tr>\n			<td style=\"text-align: justify;\" valign=\"top\">&nbsp;</td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">1. Pekerjaan Pemeriksaan Kesehatan Harian;<br>\n2. Pekerjaan Pelatihan P3K;<br>\n3. Pekerjaan Pengukuran Hygiene Industri Semester..</span></span></td>\n		</tr>\n		<tr>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">2.2.</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><strong>LOKASI PEKERJAAN </strong>ini adalah di&nbsp;seluruh wilayah TBBM, DPPU dan Depot LPG Pertamina MOR VII..</span></span></td>\n		</tr>\n		<tr>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">2.3.</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><b>PARA PIHAK </b>akan menandatangani Berita Acara Serah Terima Pekerjaan (BASTP) setelah&nbsp;<b>PIHAK KEDUA&nbsp;</b>menyelesaikan PEKERJAAN 100 % (Seratus persen) dan diterima dengan baik oleh <b>PIHAK PERTAMA.</b></span></span></td>\n		</tr>\n	</tbody>\n</table>\n\n<p>&nbsp;</p>\n\n<p style=\"text-align: center;\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><b>PASAL 3</b></span></span></p>\n\n<p align=\"center\" style=\"margin: 0cm 0cm 10pt; text-align: center;\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><b>PENERIMAAN ATAS KONDISI PEKERJAAN DAN LOKASI</b></span></span></p>\n\n<table align=\"left\" style=\"width: 100%; border-collapse: collapse;\">\n	<tbody>\n		<tr>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">3.1. &nbsp; &nbsp; &nbsp;&nbsp; &nbsp;</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><font color=\"#000000\"><strong>PIHAK KEDUA</strong> telah memahami dan menerima <b>PEKERJAAN </b>dengan segala resiko yang mungkin muncul dikemudian hari dalam pelaksanaan <b>PEKERJAAN</b>, perhitungan <b>HARGA KONTRAK </b>dan penentuan <b>MASA PENYELESAIAN PEKERJAAN </b>sebagaimana dituangkan dalam Jadwal Pelaksanaan, termasuk mutu dan ketersediaan <b>PERALATAN, MATERIAL </b>dan <b>TENAGA KERJA</b></font>.</span></span></td>\n		</tr>\n		<tr>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">3.2.&nbsp;</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><font color=\"#000000\"><strong>PIHAK KEDUA</strong> mengakui bahwa hal-hal dan resiko sebagaimana dimaksudkan dalam ayat 4.1 dan 4.2 Pasal ini telah diperhitungkan dalam <b>HARGA KONTRAK </b>dan penentuan <b>MASA PENYELESAIAN PEKERJAAN</b>. Sehubungan dengan hal tersebut, <b>PIHAK KEDUA </b>tidak akan atau tidak berhak untuk mengajukan klaim kenaikan terhadap <b>HARGA KONTRAK </b>atau <b>MASA PENYELESAIAN PEKERJAAN</b> baik secara keseluruhan atau sebagian atas perbedaan antara keadaan sebenarnya yang dihadapi <b>PIHAK KEDUA</b> dengan yang diperkirakan sebelumnya oleh <b>PIHAK KEDUA</b></font>.</span></span></td>\n		</tr>\n	</tbody>\n</table>\n\n<p>&nbsp;</p>\n\n<p style=\"text-align: center;\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><b>PASAL 4</b></span></span></p>\n\n<p align=\"center\" style=\"margin: 0cm 0cm 10pt; text-align: center;\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><b>PEKERJAAN TAMBAH/KURANG</b></span></span></p>\n\n<table align=\"left\" style=\"width: 100%; border-collapse: collapse;\">\n	<tbody>\n		<tr>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">4.1.&nbsp;&nbsp;&nbsp;</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">Apabila terjadi penambahan atau pengurangan pekerjaan di luar Lingkup <b>PEKERJAAN </b>sebagaimana Pasal 2 Lampiran B ini, maka penambahan atau pengurangan Pekerjaan tersebut akan dituangkan dalam suatu Addendum yang merupakan satu kesatuan yang tidak terpisahkan dari <b>KONTRAK</b>.</span></span></td>\n		</tr>\n		<tr>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">4.2.</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">Setiap perubahan / penambahan atas Lingkup <b>PEKERJAAN </b>harus didahului dengan persetujuan tertulis dari <b>PIHAK PERTAM</b><strong>A</strong>.</span></span></td>\n		</tr>\n		<tr>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">4.3.</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">Apabila permintaan perubahan tersebut berasal dari <b>PIHAK PERTAMA</b>&nbsp;maka <b>PIHAK&nbsp;PERTAMA</b>&nbsp;akan memberitahukan secara tertulis kepada&nbsp;<b>PIHAK KEDUA</b>&nbsp;dan&nbsp;<b>PIHAK KEDUA</b>&nbsp;wajib memberikan konfirmasi kesanggupan atau ketidaksanggupan secara tertulis dalam waktu 2 (dua) <b>HARI KERJA </b>sejak tanggal surat <b>PIHAK PERTAMA</b>&nbsp;tersebut. Apabila&nbsp;<strong>PIHAK KEDUA</strong>&nbsp;tidak sanggup maka PARA PIHAK sepakat untuk mengakhiri <b>KONTRAK,</b> kemudian akan diadakan perhitungan oleh <b>PIHAK PERTAMA</b>&nbsp;mengenai prestasi&nbsp;<b>PIHAK KEDUA</b>&nbsp;dan pembayaran-pembayaran sebelumnya, serta seluruh hasil <b>PEKERJAAN</b> otomatis menjadi milik <b>PERUSAHAAN</b>.</span></span></td>\n		</tr>\n		<tr>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">4.4.</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">Apabila permintaan perubahan tersebut berasal dari&nbsp;<b>PIHAK KEDUA</b>&nbsp;maka&nbsp;<b>PIHAK KEDUA</b>&nbsp;wajib mengajukan secara tertulis beserta data dan/atau desain ulang kepada <b>PIHAK PERTAMA</b>&nbsp;pada kesempatan pertama dan keputusan ada pada <b>PIHAK PERTAM</b>&nbsp;yang akan disampaikan secara tertulis dalam waktu 2 (dua) <b>HARI KERJA </b>setelah&nbsp;<b>PIHAK PERTAMA</b>&nbsp;menerima surat <b>PIHAK KEDUA</b>. Apabila <b>PIHAK PERTAMA</b>&nbsp;tidak menyetujui usul perubahan tersebut maka&nbsp;<b>PIHAK KEDUA</b>&nbsp;menyelesaikan <b>PEKERJAAN </b>sesuai yang diatur dalam <b>PERJANJIAN </b>ini dan <b>DOKUMEN PENGADAAN</b>.</span></span></td>\n		</tr>\n		<tr>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">4.5.</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">Kondisi dan syarat-syarat untuk penambahan Pekerjaan harus memperhatikan dan memenuhi ketentuan peraturan yang berlaku di lingkungan <b>PIHAK PERTAMA</b>.</span></span></td>\n		</tr>\n		<tr>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">4.6.</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">Bilamana terdapat kerja tambah/kurang yang telah disetujui oleh <b>PIHAK PERTAM</b>&nbsp;dan mengakibatkan perubahan <b>HARGA KONTRAK</b>, maka akan diperhitungkan dan dibayarkan oleh <b>PIHAK PERTAMA</b>&nbsp;setelah adanya BASTP dengan disertai bukti-bukti yang disetujui oleh <b>PERUSAHAAN</b>.</span></span></td>\n		</tr>\n	</tbody>\n</table>\n\n<p>&nbsp;</p>\n\n<p style=\"text-align: center;\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><b>PASAL 5</b></span></span></p>\n\n<p align=\"center\" style=\"margin: 0cm 0cm 10pt; text-align: center;\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><b>PERALATAN DAN MATERIAL</b></span></span></p>\n\n<table align=\"left\" style=\"width: 100%; border-collapse: collapse;\">\n	<tbody>\n		<tr>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">5.1.&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><b>PIHAK KEDUA&nbsp;</b>wajib menyediakan dan mengadakan <b>PERALATAN</b> dan <b>MATERIAL </b>sesuai dengan <b>DOKUMEN PENGADAAN</b>, spesifikasi, mutu/kualitas, persyaratan teknis atau persyaratan lain untuk pelaksanaan <b>PEKERJAAN</b>.</span></span></td>\n		</tr>\n		<tr>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">5.2.</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><b>PIHAK PERTAMA&nbsp;</b>akan memeriksa seluruh <b>PERALATAN</b> dan <b>MATERIAL </b>yang akan digunakan oleh&nbsp;<b>PIHAK KEDUA&nbsp;</b>untuk pelaksanaan <b>PEKERJAAN. </b>Apabila <b>PERALATAN</b> dan <b>MATERIAL </b>tersebut telah sesuai dengan spesifikasi dan layak untuk pelaksanaan <b>PEKERJAAN </b>sebagaimana dimaksud ayat 6.1 Pasal ini, maka akan dibuatkan Berita Acara Pemeriksaan yang ditandatangani oleh <b>PARA PIHAK</b>.</span></span></td>\n		</tr>\n		<tr>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">5.3.</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">Apabila <b>PERALATAN</b> dan/atau <b>MATERIAL </b>yang dipergunakan untuk <b>PEKERJAAN </b>diharuskan atau diperlukan persyaratan sertifikasi teknis, jaminan kualitas, garansi atau jaminan vendor/supplier, test/pengujian khusus, atau sertifikasi lain yang ditentukan <b>PIHAK PERTAMA&nbsp;</b>atau berdasarkan suatu peraturan, maka&nbsp;<strong>PIHAK KEDUA</strong><b>&nbsp;</b>atas beban dan biaya&nbsp;<b>PIHAK KEDUA</b>&nbsp;wajib untuk memenuhi persyaratan tersebut.</span></span></td>\n		</tr>\n		<tr>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">5.4.</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">Apabila diperlukan penambahan <b>PERALATAN</b> dan/atau <b>MATERIAL </b>akibat kesalahan&nbsp;<b>PIHAK KEDUA</b>&nbsp;yang mengakibatkan adanya perubahan lingkup <b>PEKERJAAN </b>untuk penyelesaian seluruh <b>PEKERJAAN</b>, maka hal tersebut akan menjadi beban dan tanggung jawab&nbsp;<b>PIHAK KEDUA</b>&nbsp;tanpa tambahan biaya dari <b>PIHAK PERTAMA</b>.</span></span></td>\n		</tr>\n		<tr>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">5.5.</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">Apabila dalam suatu keadaan atau karena alasan tertentu yang bersifat sangat teknis dalam pelaksanaan <b>PEKERJAAN </b>mengakibatkan terjadinya perubahan, penambahan atau pengurangan <b>PERALATAN</b> dan/atau <b>MATERIAL</b>, maka perubahan, penambahan atau pengurangan <b>PERALATAN</b> dan/atau <b>MATERIAL </b>tersebut harus mendapatkan persetujuan tertulis <b>PIHAK PERTAMA</b>.</span></span></td>\n		</tr>\n		<tr>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">5.6.&nbsp;&nbsp;</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><b>PIHAK KEDUA</b>&nbsp;dalam pelaksanaan <b>PEKERJAAN</b> wajib untuk secara maksimal mengutamakan penggunaan <b>PERALATAN</b> dan <b>MATERIAL </b>produksi dalam negeri sesuai standar atau spesifikasi yang ditentukan sesuai peraturan yang berlaku, kecuali ditentukan lain dan diperlukan spesifikasi khusus.</span></span></td>\n		</tr>\n		<tr>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">5.7.</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><b>PIHAK KEDUA</b>&nbsp;wajib menyerahterimakan kepemilikan atas <b>MATERIAL </b>yang diadakan oleh&nbsp;<b>PIHAK KEDUA</b>&nbsp;untuk pelaksanaan <b>PEKERJAAN</b> kepada&nbsp;<b>PIHAK PERTAMA</b>&nbsp;setelah sampai di <b>LOKASI PEKERJAAN.</b> Namun selanjutnya&nbsp;<b>PIHAK KEDUA</b>&nbsp;tetap bertanggung jawab penuh dan atas bebannya sendiri untuk perawatan, keamanan dan penjagaan terhadap <b>MATERIAL </b>tersebut sampai dengan <b>TANGGAL PENYERAHAN PEKERJAAN</b>, sehingga kehilangan, kerusakan, penurunan mutu/kualitas dari <b>MATERIAL </b>tersebut merupakan tanggung jawab <b>PIHAK KEDUA.</b></span></span></td>\n		</tr>\n	</tbody>\n</table>\n\n<p>&nbsp;</p>\n\n<p style=\"text-align: center;\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><b>PASAL 6</b></span></span></p>\n\n<p align=\"center\" style=\"margin: 0cm 0cm 10pt; text-align: center;\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><b>METODE PELAKSANAAN PEKERJAAN</b></span></span></p>\n\n<table align=\"left\" style=\"width: 100%; border-collapse: collapse;\">\n	<tbody>\n		<tr>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">6.1.&nbsp;&nbsp;&nbsp;&nbsp;</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><b>PIHAK KEDUA</b>&nbsp;wajib melaksanakan <b>PEKERJAAN</b> ini sesuai dengan revisi-revisi terakhir dan penemuan-penemuan terbaru atau cara-cara pelaksanaan praktis yang sesuai dengan engineering dan desain serta Jadwal Pelaksanaan.</span></span></td>\n		</tr>\n		<tr>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">6.2.</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">Setiap saat&nbsp;<b>PIHAK KEDUA</b>&nbsp;harus menjaga kebersihan, menjaga jangan sampai ada penimbunan bahan-bahan atau material-material yang sudah tak terpakai atau kotoran termasuk didalam bangunan-bangunan sesuai dengan syarat-syarat pelaksanaan kerja yang baik.</span></span></td>\n		</tr>\n		<tr>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">6.3.</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><b>PIHAK KEDUA</b>&nbsp;harus menjaga dan melindungi kondisi <b>LOKASI PEKERJAAN</b>, termasuk semua fasilitas-fasilitas yang sudah ada ditempat atau didekat <b>LOKASI PEKERJAAN, </b>semua tanaman, seperti pohon-pohon, semak-semak, rumput-rumput, agar tetap dalam keadaan baik. <b>PIHAK KEDUA</b>, atas biayanya sendiri, wajib memperbaiki jika terjadi kerusakan-kerusakan akibat kesalahan atau kelalaian <strong>PIHAK KEDUA</strong>.</span></span></td>\n		</tr>\n		<tr>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">6.4.</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><strong>PIHAK KEDUA</strong>&nbsp;wajib melakukan tindakan-tindakan untuk mencegah terjadinya pencemaran lingkungan, termasuk laut, air, udara dan tanah, yang timbul akibat pelaksanaan <b>PEKERJAAN</b>.&nbsp;<b>PIHAK KEDUA</b>&nbsp;bertanggung jawab penuh dan atas bebannya sendiri atas pencemaran lingkungan yang terjadi akibat pelaksanaan <b>PEKERJAAN </b>termasuk penanganannya.</span></span></td>\n		</tr>\n		<tr>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">6.5.</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><b>PIHAK KEDUA</b>&nbsp;harus menggunakan peralatan-peralatan keselamatan kerja, melaksanakan peraturan-peraturan keselamatan kerja, dan melakukan semua tanggung jawabnya sesuai dengan ketentuan-ketentuan umum Industri, Konstruksi International dan dengan cara-cara yang tidak membahayakan kesehatan, jiwa manusia, serta mencegah terjadinya kerusakan-kerusakan harta benda.</span></span></td>\n		</tr>\n	</tbody>\n</table>\n\n<p>&nbsp;</p>\n\n<p style=\"text-align: center;\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">&nbsp; <b>PASAL 7</b></span></span></p>\n\n<p align=\"center\" style=\"margin: 0cm 0cm 10pt; text-align: center;\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><b>FORMALITAS DAN PERIJINAN</b></span></span></p>\n\n<table align=\"left\" style=\"width: 100%; border-collapse: collapse;\">\n	<tbody>\n		<tr>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">7.1.&nbsp; &nbsp;&nbsp; &nbsp;&nbsp;</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">Apabila dalam rangka penyelesaian <b>PEKERJAAN, </b>diperlukan uji coba, test/pengujian khusus, sertifikasi, pengesahan atau formalitas dari instansi yang berwenang, maka&nbsp;<b>PIHAK KEDUA</b>&nbsp;atas beban dan biaya&nbsp;<b>PIHAK KEDUA</b>&nbsp;sendiri wajib untuk memenuhi persyaratan tersebut.</span></span></td>\n		</tr>\n		<tr>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">7.2.</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">Apabila didalam pelaksanaan <b>PEKERJAAN,</b> diperlukan perijinan dari Instansi Pemerintah, maka <b>PIHAK PERTAMA</b>&nbsp;akan menyelesaikannya dan segala biaya yang timbul untuk perijinan diatas menjadi beban <b>PIHAK PERTAMA</b>. Dalam hal ini,&nbsp;<b>PIHAK KEDUA</b>&nbsp;wajib membantu <b>PERUSAHAAN</b> dalam pengurusan perijinan tersebut.</span></span></td>\n		</tr>\n		<tr>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">7.3.</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><b>PIHAK KEDUA</b>&nbsp;harus mendapatkan dan menyimpan semua surat-surat registrasi, lisensi-lisensi dan perijinan dimana hal tersebut diperlukan menurut hukum yang berlaku, dan&nbsp;<strong>PIHAK KEDUA</strong>&nbsp;wajib menyerahkan kepada <b>PIHAK PERTAMA</b>&nbsp;jika diminta / dibutuhkan.</span></span></td>\n		</tr>\n	</tbody>\n</table>\n\n<p>&nbsp;</p>\n\n<p style=\"margin: 0cm 0cm 10pt; text-align: right;\">&nbsp;</p>\n\n<div style=\"page-break-after: always\"><span style=\"display: none;\">&nbsp;</span></div>\n\n<p style=\"margin: 0cm 0cm 10pt; text-align: right;\"><br />\n<span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">LAMPIRAN C</span></span></p>\n\n<p align=\"center\" style=\"margin: 0cm 0cm 10pt; text-align: center;\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><b>HARGA KONTRAK DAN KETENTUAN PEMBAYARAN</b></span></span></p>\n\n<p align=\"center\" style=\"margin: 0cm 0cm 10pt; text-align: center;\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><b>PASAL 1</b></span></span></p>\n\n<p align=\"center\" style=\"margin: 0cm 0cm 10pt; text-align: center;\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><b>HARGA KONTRAK DAN HARGA SEWA</b></span></span></p>\n\n<table align=\"left\" style=\"width: 100%; border-collapse: collapse;\">\n	<tbody>\n		<tr>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">1.1.&nbsp; &nbsp;&nbsp; &nbsp;&nbsp;</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><strong>HARGA KONTRAK </strong>atas pelaksanaan seluruh <strong>PEKERJAAN </strong>ini adalah sebesar <strong>Rp923.108.756,- (Sembilan ratus dua puluh tiga juta seratus delapan ribu tujuh ratus lima puluh enam rupiah)</strong> belum termasuk PPN 10%.</span></span></td>\n		</tr>\n		<tr>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">1.2.</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><strong>HARGA KONTRAK </strong>tersebut dalam ayat 1.1 Pasal ini telah termasuk pajak-pajak, perijinan-perijinan di luar perijinan dari instansi pemerintah dan pembayaran-pembayaran yang merupakan kewajiban&nbsp;<strong>PIHAK KEDUA&nbsp;</strong>kecuali PPN.</span></span></td>\n		</tr>\n	</tbody>\n</table>\n\n<p>&nbsp;</p>\n\n<p style=\"text-align: center;\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><b>PASAL 2</b></span></span></p>\n\n<p align=\"center\" style=\"margin: 0cm 0cm 10pt; text-align: center;\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><b>TATA CARA PEMBAYARAN</b></span></span></p>\n\n<table align=\"left\" style=\"width: 100%; border-collapse: collapse;\">\n	<tbody>\n		<tr>\n			<td style=\"width: 5%; text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">2.1.</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><font color=\"#000000\">Pembayaran kepada <b>PIHAK KEDUA</b> berdasarkan <b>KONTRAK</b> ini akan dilakukan setiap bulan sesuai dengan tagihan yang disampaikan oleh <b>PIHAK KEDUA</b> kepada <b>PIHAK PERTAMA.</b> Pembayaran material dan peralatan kepada <b>PIHAK KEDUA</b> berdasarkan kontrak ini akan dilakukan sesuai dengan material dan peralatan yang digunakan berdasarkan harga material dan peralatan yang sudah disepakati <b>PIHAK PERTAMA</b> dan <b>PIHAK KEDUA.</b></font></span></span></td>\n		</tr>\n	</tbody>\n</table>\n\n<table align=\"left\" style=\"width: 100%; border-collapse: collapse;\">\n	<tbody>\n		<tr>\n			<td style=\"width: 5%; text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">2.2.</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><font color=\"#000000\"><strong>PIHAK KEDUA </strong>wajib menagihkan kepada <b>PIHAK PERTAMA</b> untuk pekerjaan yang telah dilaksanakan dan telah diterima dengan baik oleh <b>PIHAK PERTAMA </b>selambat-lambatnya 90 (Sembilan puluh) hari kalender setelah berakhirnya <b>JANGKA WAKTU KONTRAK</b>. Apabila <b>PIHAK KEDUA </b>menagih di luar jangka waktu tersebut, maka <b>PIHAK PERTAMA </b>tidak wajib menjamin likuiditas pembayaran kepada <b>PIHAK KEDUA</b>.</font></span></span></td>\n		</tr>\n	</tbody>\n</table>\n\n<table align=\"left\" style=\"width: 100%; border-collapse: collapse;\">\n	<tbody>\n		<tr>\n			<td style=\"width: 5%; text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">2.3.</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><font color=\"#000000\">Apabila <b>PIHAK KEDUA </b>terlambat menagih kepada <b>PIHAK PERTAMA </b>yang mengakibatkan adanya denda pajak maka denda pajak dimaksud menjadi tanggungjawab <b>PIHAK KEDUA </b>yang akan dilakukan pemotongan langsung pada tagihan <b>PIHAK KEDUA.</b></font></span></span></td>\n		</tr>\n	</tbody>\n</table>\n\n<table align=\"left\" style=\"width: 100%; border-collapse: collapse;\">\n	<tbody>\n		<tr>\n			<td style=\"width: 5%; text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">2.4.</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><font color=\"#000000\"><strong>PIHAK PERTAMA</strong> akan melakukan pembayaran sebagaimana tersebut pada ayat 2.1 Pasal ini, apabila <b>PIHAK KEDUA </b>telah melampirkan dokumen yang merupakan dokumen-dokumen sebagai berikut: </font></span></span></td>\n		</tr>\n		<tr>\n			<td style=\"width: 5%; text-align: justify;\" valign=\"top\">&nbsp;</td>\n			<td style=\"text-align: justify;\" valign=\"top\">\n			<table align=\"left\" border=\"0\" style=\"width: 100%; border-collapse: collapse;\">\n				<tbody>\n					<tr>\n						<td style=\"width: 6.5%;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">a.</span></span></td>\n						<td valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><em>Purchase Order</em>(PO)</span></span></td>\n					</tr>\n					<tr>\n						<td style=\"width: 6.5%;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">b.</span></span></td>\n						<td valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">Fotokopi <strong>KONTRAK</strong></span></span></td>\n					</tr>\n					<tr>\n						<td style=\"width: 6.5%;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">c.</span></span></td>\n						<td valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><em>Invoice</em> Asli bermaterai</span></span></td>\n					</tr>\n					<tr>\n						<td style=\"width: 6.5%;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">d.</span></span></td>\n						<td valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">Asli Kwitansi bermaterai cukup</span></span></td>\n					</tr>\n					<tr>\n						<td style=\"width: 6.5%;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">e.</span></span></td>\n						<td valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">Specimen tandatangan direktur <strong>PIHAK KEDUA</strong></span></span></td>\n					</tr>\n					<tr>\n						<td style=\"width: 6.5%;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">f.</span></span></td>\n						<td valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><em>Copy </em>KTP Direktur <strong>PIHAK KEDUA</strong></span></span></td>\n					</tr>\n					<tr>\n						<td style=\"width: 6.5%;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">g.</span></span></td>\n						<td valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">Berita Acara Penyerahan Material dan Peralatan</span></span></td>\n					</tr>\n					<tr>\n						<td style=\"width: 6.5%;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">h.</span></span></td>\n						<td valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">Berita Acara Serah Terima Pekerjaan yang diterbitkan setiap bulan</span></span></td>\n					</tr>\n					<tr>\n						<td style=\"width: 6.5%;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">i.</span></span></td>\n						<td valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">Asli <em>service acceptance</em></span></span></td>\n					</tr>\n					<tr>\n						<td style=\"width: 6.5%;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">j.</span></span></td>\n						<td valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">Fotokopi pembayaran upah tenaga kerja <strong>PIHAK KEDUA </strong>(slip gaji yang telah dibayarkan)</span></span></td>\n					</tr>\n					<tr>\n						<td style=\"width: 6.5%;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">k.</span></span></td>\n						<td valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">Tanda <font color=\"#000000\">terima perlengkapan kerja diketahui pengawas pekerjaan dan melampirkan dokumentasi (foto) untuk masing-masing perlengkapan kerja (khusus untuk sepatu safety dan helm agar dilampirkan spesifikasinya dan nota pembelian resmi dari Vendor List HSSE PT.Pertamina)</font></span></span></td>\n					</tr>\n				</tbody>\n			</table>\n			</td>\n		</tr>\n		<tr>\n			<td style=\"text-align: justify;\" valign=\"top\">&nbsp;</td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><font color=\"#000000\">Tagihan asli diserahkan kepada <b>PIHAK PERTAMA</b>, cq. <b>Area Finance MOR VII</b></font></span></span></td>\n		</tr>\n	</tbody>\n</table>\n\n<table align=\"left\" style=\"width: 100%; border-collapse: collapse;\">\n	<tbody>\n		<tr>\n			<td style=\"width: 5%; text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">2.5.</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><font color=\"#000000\">Khusus faktur pajak, <b>PIHAK KEDUA </b>wajib mencantumkan tanggal faktur pajak dan tanggal invoice sesuai dengan penyerahan <b>PEKERJAAN </b>dan menyampaikannya pada bulan yang sama. Faktur pajak dan invoice harus diisi secara lengkap dan benar, apabila tanggal faktur pajak tidak memenuhi ketentuan tersebut, <b>PIHAK PERTAMA </b>berhak menolak/mengembalikan berkas invoice.</font></span></span></td>\n		</tr>\n	</tbody>\n</table>\n\n<table align=\"left\" style=\"width: 100%; border-collapse: collapse;\">\n	<tbody>\n		<tr>\n			<td style=\"width: 5%; text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">2.6.</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><font color=\"#000000\">Apabila setelah invoice diterima namun ternyata masih diperlukan perbaikan / penambahan kelengkapan dokumen dan penyampaian dokumen tersebut berbeda bulan dengan bulan pada faktur pajak dan invoice, <b>PIHAK KEDUA </b>wajib kembali merevisi dan menyampaikan kepada <b>PIHAK PERTAMA </b>faktur pajak pengganti dan invoice dengan bulan yang sama dengan bulan penyampaian dokumen perbaikan tersebut. Apabila <b>PIHAK PERTAMA </b>mempersoalkan suatu barang/jasa atau jumlah yang ditagih, <b>PIHAK PERTAMA </b>akan memberitahukan secara tertulis kepada <b>PIHAK KEDUA </b>tentang barang/jasa atau jumlah yang diperselisihkan tersebut dengan merinci alasannya. Tagihan semula yang diajukan <b>PIHAK KEDUA </b>dianggap batal dan <b>PIHAK KEDUA </b>wajib membuat tagihan yang baru untuk jumlah yang tidak diperselisihkan, sedangkan jumlah yang diperselisihkan akan diselesaikan kemudian</font></span></span></td>\n		</tr>\n	</tbody>\n</table>\n\n<table align=\"left\" style=\"width: 100%; border-collapse: collapse;\">\n	<tbody>\n		<tr>\n			<td style=\"width: 5%; text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">2.7.&nbsp;</span></span><br />\n			&nbsp;</td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><strong>PIHAK PERTAMA </strong><font color=\"#000000\">wajib membayar tagihan <b>PIHAK KEDUA </b>dalam waktu maksimal 30 (tiga puluh) <b>HARI KALENDER </b>setelah dokumen diterima secara lengkap dan benar. Segala keterlambatan atau penundaan pembayaran tagihan dan <b>PIHAK PERTAMA </b>dengan alasan apapun tidak menghilangkan kewajiban <b>PIHAK KEDUA </b>untuk memenuhi pembayaran upah, tunjangan hari raya keagamaan, tunjangan lembur, biaya perjalanan dinas serta tunjangan lainnya yang menjadi hak tenaga kerja yang dipekerjakan pada lingkungan <b>PIHAK PERTAMA </b>maupun biaya-biaya yang menjadi kewajiban <b>PIHAK KEDUA </b>kepada pihak ketiga lainnya. <b>PIHAK KEDUA </b>dalam hal ini tidak berhak menuntut <b>PIHAK PERTAMA </b>untuk terlebih dahulu memenuhi kewajibannya dan <b>PIHAK KEDUA </b>membebaskan <b>PIHAK PERTAMA </b>dari segala tuntutan pihak lainnya sebagai akibat tidak dipenuhinya kewajiban <b>PIHAK KEDUA </b>tersebut.</font></span></span></td>\n		</tr>\n	</tbody>\n</table>\n\n<table align=\"left\" style=\"width: 100%; border-collapse: collapse;\">\n	<tbody>\n		<tr>\n			<td style=\"width: 5%; text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">2.8.</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">Pembayaran <strong>HARGA KONTRAK </strong>akan dilaksanakan oleh <strong>PIHAK PERTAMA </strong>kepada&nbsp;<strong>PIHAK KEDUA&nbsp;</strong>melalui transfer ke rekening sebagai berikut :</span></span></td>\n		</tr>\n		<tr>\n			<td style=\"width: 5%; text-align: justify;\" valign=\"top\">&nbsp;</td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><strong>PT. Bank Mandiri (Persero) Tbk Branch Makassar Sulawesi</strong></span></span></td>\n		</tr>\n		<tr>\n			<td style=\"width: 5%; text-align: justify;\" valign=\"top\">&nbsp;</td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">Nama Perusahaan&nbsp; &nbsp;:&nbsp;PT. Mitrasejati Yakapepe</span></span></td>\n		</tr>\n		<tr>\n			<td style=\"width: 5%; text-align: justify;\" valign=\"top\">&nbsp;</td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">No. Rekening&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;:&nbsp;<strong>152-00-0215549-3</strong></span></span></td>\n		</tr>\n		<tr>\n			<td style=\"width: 5%; text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">2.9.</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><strong>PIHAK KEDUA </strong><font color=\"#000000\">dapat mengusulkan perubahan rekening Bank dengan cara mengajukan permohonan tertulis kepada <b>PIHAK PERTAMA. </b>Permohonan perubahan rekening Bank tersebut harus ditandatangani oleh penandatanganan kontrak awal/pejabat yang berwenang yang mempunyai otorisasi sesuai dengan Anggaran Dasar <b>PIHAK KEDUA </b>dan Akta perubahannya yang terakhir</font>.</span></span></td>\n		</tr>\n	</tbody>\n</table>\n\n<table align=\"left\" style=\"width: 100%; border-collapse: collapse;\">\n	<tbody>\n		<tr>\n			<td style=\"width: 5%; text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">2.10.</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">Pembayaran <font color=\"#000000\">melalui rekening Bank yang baru, dilaksanakan setelah mendapat persetujuan tertulis dari penandatanganan <b>KONTRAK </b>yang mewakili <b>PIHAK PERTAMA </b>atau pejabat yang berwenang. Selanjutnya perubahan rekening Bank akan dituangkan dalam bentuk Amandemen.</font></span></span></td>\n		</tr>\n	</tbody>\n</table>\n\n<table align=\"left\" style=\"width: 100%; border-collapse: collapse;\">\n	<tbody>\n		<tr>\n			<td style=\"width: 5%; text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">2.11.</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">Pembayaran <font color=\"#000000\">dilakukan dalam mata uang sesuai dengan ketentuan nilai <b>KONTRAK </b>sebagaimana dimaksud dalam Pasal 1 Lampiran C. Apabila <b>PIHAK PERTAMA </b>membayar dengan mata uang yang berbeda, maka nilai tukar yang akan digunakan adalah nilai tukar yang akan digunakan adaah nilai tukar yang berlaku pada hari pertama dari bulan dimana tagihan bersangkutan di bayar dengan kurs tengah Bank Indonesia.</font></span></span></td>\n		</tr>\n	</tbody>\n</table>\n\n<table align=\"left\" style=\"width: 100%; border-collapse: collapse;\">\n	<tbody>\n		<tr>\n			<td style=\"width: 5%; text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">2.12.</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><strong>PIHAK PERTAMA </strong><font color=\"#000000\">berhak memotong tagihan <b>PIHAK KEDUA </b>atas seluruh hutang dan kewajiban lain <b>PIHAK KEDUA </b>kepada <b>PIHAK PERTAMA </b>yang timbul dari <b>KONTRAK </b>ini maupun kontrak lain.</font></span></span></td>\n		</tr>\n	</tbody>\n</table>\n\n<p>&nbsp;</p>\n\n<table align=\"left\" style=\"width: 100%; border-collapse: collapse;\">\n</table>\n\n<p style=\"text-align: center;\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><strong>PASAL 3</strong></span></span></p>\n\n<p align=\"center\" style=\"margin: 0cm 0cm 10pt; text-align: center;\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><strong>KETENTUAN DENDA / SANKSI</strong></span></span></p>\n\n<table align=\"left\" style=\"width: 100%; border-collapse: collapse;\">\n	<tbody>\n		<tr>\n			<td style=\"width: 5%; text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">3.1.</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">Apabila&nbsp;<strong>PIHAK KEDUA&nbsp;</strong>tidak dapat menyelesaikan <strong>PEKERJAAN </strong>sesuai dengan <strong>MASA </strong><strong>PENYELESAIAN PEKERJAAN</strong>, maka untuk setiap hari keterlambatan,&nbsp;<strong>PIHAK KEDUA&nbsp;</strong>akan dikenakan denda keterlambatan sebesar 1<sup>o</sup>/<sub>oo</sub> (satu permil) per hari dari seluruh <strong>HARGA KONTRAK </strong>dengan nilai maksimum denda adalah sebesar 5% (lima persen) dari seluruh <b>HARGA KONTRAK</b>.</span></span></td>\n		</tr>\n		<tr>\n			<td style=\"width: 5%; text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">3.2.</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">Apabila denda sebagaimana ayat 4.1 Pasal ini telah mencapai nilai maksimum, <strong>PIHAK PERTAMA </strong>secara sepihak berhak untuk memutus <strong>KONTRAK </strong>ini.</span></span></td>\n		</tr>\n		<tr>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">3.3.</span></span></td>\n			<td valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">Apabila terjadi keterlambatan kemajuan pelaksanaan <strong>PEKERJAAN </strong>dari Jadwal Pelaksanaan sebagai berikut :</span></span></td>\n		</tr>\n		<tr>\n			<td style=\"text-align: justify;\" valign=\"top\">&nbsp;</td>\n			<td valign=\"top\">\n			<table align=\"left\" style=\"width: 100%; border-collapse: collapse;\">\n				<tbody>\n					<tr>\n						<td style=\"width: 5%; text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">a.</span></span></td>\n						<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">20 % (Dua puluh persen) atau lebih pada rencana pelaksanaan dibawah 70% (tujuh puluh persen); atau</span></span></td>\n					</tr>\n					<tr>\n						<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">b.</span></span></td>\n						<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">10 % (Sepuluh persen) atau lebih pada rencana pelaksanaan di atas 70% (Tujuh puluh persen)</span></span></td>\n					</tr>\n					<tr>\n						<td colspan=\"2\" style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">maka&nbsp;<strong>PIHAK KEDUA&nbsp;</strong>akan diberikan Surat Peringatan I (pertama) oleh <strong>PIHAK PERTAMA</strong>. Apabila&nbsp;<strong>PIHAK KEDUA&nbsp;</strong>tidak melakukan langkah-langkah perbaikan yang memadai sehingga diperkirakan <strong>PEKERJAAN </strong>akan mengalami keterlambatan melewati 15 (Lima belas) hari kalender (barlarut-larut), maka&nbsp;<strong>PIHAK KEDUA&nbsp;</strong>akan diberikan Surat Peringatan II (dua) oleh <strong>PIHAK PERTAMA</strong>.</span></span></td>\n					</tr>\n				</tbody>\n			</table>\n\n			<p>&nbsp;</p>\n			</td>\n		</tr>\n		<tr>\n			<td style=\"width: 5%; text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">3.4.</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">Jika peringatan tertulis dari&nbsp;<strong>PIHAK PERTAMA&nbsp;</strong>atas segala kelalaian&nbsp;<strong>PIHAK KEDUA&nbsp;</strong>dalam mentaati segala peraturan yang berlaku dan syarat-syarat yang tercantum dalam&nbsp;<strong>KONTRAK&nbsp;</strong>ini telah 2 (dua) kali diberikan, tetapi masih belum ditaati atau belum terdapat kemajuan dalam jangka waktu 7 (tujuh) hari kalender, maka&nbsp;<strong>PIHAK PERTAMA&nbsp;</strong>berhak secara sepihak memutuskan&nbsp;<strong>KONTRAK</strong>. Setelah pemutusan&nbsp;<strong>KONTRAK</strong>, akan diadakan perhitungan oleh&nbsp;<strong>PIHAK PERTAMA&nbsp;</strong>mengenai prestasi dan pembayaran-pembayaran sebelumnya. Dalam hal ini&nbsp;<strong>PIHAK KEDUA&nbsp;</strong>tidak dapat menuntut ganti rugi apapun juga dan keputusan mutlak ditangan&nbsp;<strong>PIHAK PERTAMA</strong>.</span></span></td>\n		</tr>\n		<tr>\n			<td style=\"width: 5%; text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">3.5.</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">Jika terjadi pemutusan&nbsp;<strong>KONTRAK&nbsp;</strong>sebagaimana dimaksud pada Pasal ini, maka seluruh hasil&nbsp;<strong>PEKERJAAN&nbsp;PIHAK KEDUA&nbsp;</strong>otomatis menjadi milik&nbsp;<strong>PIHAK PERTAMA</strong>.<strong>&nbsp;</strong></span></span></td>\n		</tr>\n	</tbody>\n</table>\n\n<p>&nbsp;</p>\n\n<p style=\"text-align: center;\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">&nbsp;&nbsp;<strong>PASAL 4</strong></span></span></p>\n\n<p align=\"center\" style=\"margin: 0cm 0cm 10pt; text-align: center;\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><strong>KETENTUAN PAJAK DAN PUNGUTAN LAINNYA</strong></span></span></p>\n\n<table align=\"left\" style=\"width: 100%; border-collapse: collapse;\">\n	<tbody>\n		<tr>\n			<td style=\"width: 5%; text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">4.1.</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">Semua pajak, bea materai, retribusi dan / atau pungutan lain berdasarkan ketentuan peraturan perundang-undangan yang berlaku yang timbul sehubungan dengan pelaksanaan <strong>KONTRAK&nbsp;</strong>ini menjadi beban dan tanggung jawab <strong>PIHAK KEDUA</strong>, kecuali Pajak Pertambahan Nilai (PPN) menjadi beban dan tanggung jawab <strong>PIHAK PERTAMA</strong>.</span></span></td>\n		</tr>\n		<tr>\n			<td style=\"width: 5%; text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">4.2.</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">Pungutan Pajak Pertambahan Nilai (PPN) yang dikenakan / terhutang sebagaimana dimaksud dalam Pasal ini akan disetorkan langsung oleh <strong>PIHAK PERTAMA&nbsp;</strong>kepada Kas Negara dalam jangka waktu yang telah ditetapkan oleh perundang-undangan / peraturan yang berlaku.</span></span></td>\n		</tr>\n		<tr>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">4.3.</span></span></td>\n			<td valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><strong>PIHAK PERTAMA&nbsp;</strong>berhak memungut Pajak Penghasilan (PPh) dari pembayaran kepada&nbsp;<strong>PIHAK KEDUA&nbsp;</strong>sesuai dengan ketentuan peraturan perundang-undangan yang berlaku, kecuali terdapat pengecualian / pembebasan pajak dari pihak yang berwenang sehubungan dengan pelaksanaan&nbsp;<strong>PEKERJAAN&nbsp;</strong>berdasarkan&nbsp;<strong>KONTRAK&nbsp;</strong>ini.</span></span></td>\n		</tr>\n		<tr>\n			<td style=\"width: 5%; text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">4.4.</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">Bilamana diperlukan oleh salah satu&nbsp;<strong>PIHAK&nbsp;</strong>atau instansi yang berwenang untuk kepentingan administrasi atau audit, maka baik&nbsp;<strong>PIHAK PERTAMA&nbsp;</strong>maupun&nbsp;<strong>PIHAK KEDUA&nbsp;</strong>akan memberikan bukti-bukti pembayaran yang berkaitan dengan pajak, retribusi dan / atau pungutan lain sebagaimana dimaksud pada ayat 4.1 Pasal ini.</span></span></td>\n		</tr>\n	</tbody>\n</table>\n\n<p style=\"text-align: right;\">&nbsp;</p>\n\n<div style=\"page-break-after: always\"><span style=\"display: none;\">&nbsp;</span></div>\n\n<p style=\"text-align: right;\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">LAMPIRAN D</span></span></p>\n\n<p align=\"center\" style=\"margin: 0cm 0cm 10pt; text-align: center;\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><b>PASAL 1</b></span></span></p>\n\n<p align=\"center\" style=\"margin: 0cm 0cm 10pt; text-align: center;\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><strong>JANGKA WAKTU PEKERJAAN</strong></span></span></p>\n\n<table align=\"left\" style=\"width: 100%; border-collapse: collapse;\">\n	<tbody>\n		<tr>\n			<td style=\"width: 6.5%;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">1.1.</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><strong>MASA PENYELESAIAN PEKERJAAN&nbsp;</strong>yang disepakati oleh&nbsp;<strong>PARA PIHAK&nbsp;</strong>adalah selama&nbsp;365 <b>HARI KALENDER&nbsp;</b>terhitung sejak tanggal <strong>1 Maret 2019 </strong>(&quot;<strong>TANGGAL DIMULAINYA PEKERJAAN</strong>&quot;).</span></span></td>\n		</tr>\n		<tr>\n			<td style=\"width: 6.5%;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">1.2.</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><strong>PIHAK PERTAMA </strong>dapat setiap waktu memerintahkan <strong>PIHAK KEDUA </strong>untuk menunda pelaksanaan <strong>PEKERJAAN </strong>yang lamanya akan dituangkan secara tertulis di dalam berita acara penundaan <strong>PEKERJAAN </strong>/ Pemberitahuan.</span></span></td>\n		</tr>\n	</tbody>\n</table>\n\n<p>&nbsp;</p>\n\n<p style=\"text-align: center;\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">&nbsp;&nbsp;<strong>PASAL 2</strong></span></span></p>\n\n<p align=\"center\" style=\"margin: 0cm 0cm 10pt; text-align: center;\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><strong>JANGKA WAKTU KONTRAK</strong></span></span></p>\n\n<table align=\"left\" style=\"width: 100%; border-collapse: collapse;\">\n	<tbody>\n		<tr>\n			<td style=\"width: 5%; text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><strong>KONTRAK&nbsp;</strong>ini berlaku sejak&nbsp;<strong>TANGGAL DIMULAINYA PEKERJAAN&nbsp;</strong>sampai dengan ditandatanganinya <strong>Berita Acara Penyelesaikan Pekerjaan </strong>untuk bulan ke 12 (Dua belas) oleh <strong>PARA PIHAK</strong>.</span></span></td>\n		</tr>\n	</tbody>\n</table>\n\n<p>&nbsp;</p>\n\n<div style=\"page-break-after: always\"><span style=\"display: none;\">&nbsp;</span></div>\n\n<p style=\"text-align: right;\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">LAMPIRAN E</span></span></p>\n\n<p align=\"center\" style=\"margin: 0cm 0cm 10pt; text-align: center;\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><strong>HEALTH, SAFETY, ENVIRONMENT (HSE) DAN&nbsp;</strong></span></span></p>\n\n<p align=\"center\" style=\"margin: 0cm 0cm 10pt; text-align: center;\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><strong>CONTRACTOR SAFETY MANAGEMENT SYSTEM (CSMS)</strong></span></span></p>\n\n<p align=\"center\" style=\"margin: 0cm 0cm 10pt; text-align: center;\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><strong>PASAL 1</strong></span></span></p>\n\n<p align=\"center\" style=\"margin: 0cm 0cm 10pt; text-align: center;\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><strong>ASPEK KESELAMATAN KERJA</strong></span></span></p>\n\n<table align=\"left\" style=\"width: 100%; border-collapse: collapse;\">\n	<tbody>\n		<tr>\n			<td style=\"width: 6.5%;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">1.1</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><strong>PARA PIHAK </strong>berkomitmen untuk mewujudkan <em>zero incident</em> (tidak ada kecelakaan kerja, kebakaran, kerusakan asset, penyakit akibat kerja (PAK) dan pencemaran lingkungan).</span></span></td>\n		</tr>\n	</tbody>\n</table>\n\n<table align=\"left\" style=\"width: 100%; border-collapse: collapse;\">\n	<tbody>\n		<tr>\n			<td style=\"width: 6.5%; text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">1.2.</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">Dalam rangka mengurangi resiko kecelakaan kerja dan bahaya lain (kebakaran, ledakan, pencemaran lingkungan, dll) pihak <strong>KONTRAKTOR </strong>harus mematuhi persyaratan / peraturan perundang-undangan yang berlaku, antra lain :</span></span></td>\n		</tr>\n	</tbody>\n</table>\n\n<table align=\"left\" style=\"width: 100%; border-collapse: collapse;\">\n	<tbody>\n		<tr>\n			<td style=\"width: 6.5%; text-align: justify;\" valign=\"top\">&nbsp;</td>\n			<td valign=\"top\">\n			<table align=\"left\" style=\"width: 100%; border-collapse: collapse;\">\n				<tbody>\n					<tr>\n						<td style=\"width: 6.5%; text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">1.2.1</span></span></td>\n						<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">UU No. 1 Th. 1970 tentang Keselamatan Kerja.</span></span></td>\n					</tr>\n				</tbody>\n			</table>\n			</td>\n		</tr>\n		<tr>\n			<td style=\"width: 6.5%; text-align: justify;\" valign=\"top\">&nbsp;</td>\n			<td valign=\"top\">\n			<table align=\"left\" style=\"width: 100%; border-collapse: collapse;\">\n				<tbody>\n					<tr>\n						<td style=\"width: 6.5%; text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">1.2.2</span></span></td>\n						<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">Peraturan Menteri Tenaga Kerja dan Transmigrasi No. 01 Th. 1980 tentang Keselamatan dan Kesehatan Kerja pada Konstruksi Bangunan.</span></span></td>\n					</tr>\n				</tbody>\n			</table>\n			</td>\n		</tr>\n		<tr>\n			<td style=\"width: 6.5%; text-align: justify;\" valign=\"top\">&nbsp;</td>\n			<td valign=\"top\">\n			<table align=\"left\" style=\"width: 100%; border-collapse: collapse;\">\n				<tbody>\n					<tr>\n						<td style=\"width: 6.5%; text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">1.2.3</span></span></td>\n						<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">Peraturan Pemerintah dan MIGAS lainnya yang terkait dengan Keselamatan, Kesehatan Kerja dan Lindungan Lingkungan di tempat kerja.</span></span></td>\n					</tr>\n				</tbody>\n			</table>\n			</td>\n		</tr>\n		<tr>\n			<td style=\"width: 6.5%; text-align: justify;\" valign=\"top\">&nbsp;</td>\n			<td valign=\"top\">\n			<table align=\"left\" style=\"width: 100%; border-collapse: collapse;\">\n				<tbody>\n					<tr>\n						<td style=\"width: 6.5%; text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">1.2.4</span></span></td>\n						<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">Panduan K3LL Pertamina Direktorat Marketing &amp; Trading Tahun 2008.</span></span></td>\n					</tr>\n				</tbody>\n			</table>\n			</td>\n		</tr>\n		<tr>\n			<td style=\"width: 6.5%; text-align: justify;\" valign=\"top\">&nbsp;</td>\n			<td valign=\"top\">\n			<table align=\"left\" style=\"width: 100%; border-collapse: collapse;\">\n				<tbody>\n					<tr>\n						<td style=\"width: 6.5%; text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">1.2.5</span></span></td>\n						<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">Kebijakan Keselamatan, Kesehatan Kerja dan Lindungan Lingkungan yang ditetapkan sesuai ketentuan yang berlaku dan <strong>PERUSAHAAN.</strong></span></span></td>\n					</tr>\n				</tbody>\n			</table>\n			</td>\n		</tr>\n	</tbody>\n</table>\n\n<table align=\"left\" style=\"width: 100%; border-collapse: collapse;\">\n	<tbody>\n		<tr>\n			<td style=\"width: 6.5%; text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">1.3.</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">Standar Keselamatan Kerja (<em>Safety Standard</em>)</span></span></td>\n		</tr>\n	</tbody>\n</table>\n\n<table align=\"left\" style=\"width: 100%; border-collapse: collapse;\">\n	<tbody>\n		<tr>\n			<td style=\"width: 6.5%; text-align: justify;\" valign=\"top\">&nbsp;</td>\n			<td valign=\"top\">\n			<table align=\"left\" style=\"width: 100%; border-collapse: collapse;\">\n				<tbody>\n					<tr>\n						<td style=\"width: 6.5%; text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">1.3.1</span></span></td>\n						<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><strong>KONTRAKTOR </strong>harus mematuhi semua Pedoman Keselamatan Kerja, Peraturan dan Standar K3 (Keselamatan dan Kesehatan Kerja) yang berlaku di <strong>PERUSAHAAN.</strong></span></span></td>\n					</tr>\n				</tbody>\n			</table>\n			</td>\n		</tr>\n		<tr>\n			<td style=\"width: 6.5%; text-align: justify;\" valign=\"top\">&nbsp;</td>\n			<td valign=\"top\">\n			<table align=\"left\" style=\"width: 100%; border-collapse: collapse;\">\n				<tbody>\n					<tr>\n						<td style=\"width: 6.5%; text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">1.3.2</span></span></td>\n						<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">Pedoman Keselamatan Kerja, Peraturan dan Standar K3 yang dipergunakan <strong>KONTRAKTOR </strong>selain tersebut pada item 1.3.1 harus mendapat persetujuan dari <strong>PERUSAHAAN </strong>melalui bagian HSSE.</span></span></td>\n					</tr>\n				</tbody>\n			</table>\n			</td>\n		</tr>\n		<tr>\n			<td style=\"width: 6.5%; text-align: justify;\" valign=\"top\">&nbsp;</td>\n			<td valign=\"top\">\n			<table align=\"left\" style=\"width: 100%; border-collapse: collapse;\">\n				<tbody>\n					<tr>\n						<td style=\"width: 6.5%; text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">1.3.3</span></span></td>\n						<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><strong>KONTRAKTOR </strong>harus melaksanakan HSE Plan dan revisinya (jika ada) yang sudah disepakati bersama dengan Perusahaan dan menjadi bagian dalam evaluasi akhir kinerja HSE <strong>KONTRAKTOR.</strong></span></span></td>\n					</tr>\n				</tbody>\n			</table>\n			</td>\n		</tr>\n	</tbody>\n</table>\n\n<table align=\"left\" style=\"width: 100%; border-collapse: collapse;\">\n	<tbody>\n		<tr>\n			<td style=\"width: 6.5%; text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">1.4.</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">Petugas Keselamatan Kerja <strong>KONTRAKTOR</strong></span></span></td>\n		</tr>\n	</tbody>\n</table>\n\n<table align=\"left\" style=\"width: 100%; border-collapse: collapse;\">\n	<tbody>\n		<tr>\n			<td style=\"width: 6.5%; text-align: justify;\" valign=\"top\">&nbsp;</td>\n			<td valign=\"top\">\n			<table align=\"left\" style=\"width: 100%; border-collapse: collapse;\">\n				<tbody>\n					<tr>\n						<td style=\"width: 6.5%; text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">1.4.1</span></span></td>\n						<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><strong>KONTRAKTOR </strong>harus menyediakan petugas yang cukup ahli dan kompeten di bidang Keselamatan Kerja yang dibuktikan dengan Sertifikat Pelatihan dan atau Sertifikasi Profesi (internal maupun eksternal) untuk mengawasi serta melaksanakan program Keselamatan Kerja dan Lindungan Lingkungan dan memiliki wewenang untuk memenuhi rekomendasi terkait aspek HSE, tanpa mengurangi tanggung jawab Manager Proyek <strong>KONTRAKTOR </strong>agar terpelihara lingkungan kerja yang aman dan selamat.</span></span></td>\n					</tr>\n				</tbody>\n			</table>\n			</td>\n		</tr>\n		<tr>\n			<td style=\"width: 6.5%; text-align: justify;\" valign=\"top\">&nbsp;</td>\n			<td valign=\"top\">\n			<table align=\"left\" style=\"width: 100%; border-collapse: collapse;\">\n				<tbody>\n					<tr>\n						<td style=\"width: 6.5%; text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">1.4.2</span></span></td>\n						<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">Personil tersebut pada item 1.4.1 di atas harus mengetahui, mengikuti dan mematuhi semua Pedoman Keselamatan Kerja, Peraturan dan Standar K3 yang berlaku di <strong>PERUSAHAAN.</strong></span></span></td>\n					</tr>\n				</tbody>\n			</table>\n			</td>\n		</tr>\n		<tr>\n			<td style=\"width: 6.5%; text-align: justify;\" valign=\"top\">&nbsp;</td>\n			<td valign=\"top\">\n			<table align=\"left\" style=\"width: 100%; border-collapse: collapse;\">\n				<tbody>\n					<tr>\n						<td style=\"width: 6.5%; text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">1.4.3</span></span></td>\n						<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">Kebutuhan jumlah tenaga / petugas HSE terhadap jumlah tenaga kerja ditetapkan minimal sebagai berikut :<br />\n						&lt; 50 pekerja&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; - 1 orang<br />\n						5 - 200 pekerja&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;- 1 orang tetap<br />\n						200 - 400 pekerja&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; - 2 orang tetap<br />\n						Petugas Keselamatan Kerja / HSE harus mempunyai kompetensi yang sesuai, dibuktikan dengan Sertifikat Pelatihan baik internal maupun eksternal. Setiap kenaikan 200 orang pekerja ditambah 1 orang Petugas Keselamatan Kerja.<br />\n						Penambahan Petugas Keselamatan Kerja dapat disesuaikan dengan resiko pekerjaannya.</span></span></td>\n					</tr>\n				</tbody>\n			</table>\n			</td>\n		</tr>\n	</tbody>\n</table>\n\n<table align=\"left\" style=\"width: 100%; border-collapse: collapse;\">\n	<tbody>\n		<tr>\n			<td style=\"width: 6.5%; text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">1.5.</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">Pembinaan</span></span></td>\n		</tr>\n	</tbody>\n</table>\n\n<table align=\"left\" style=\"width: 100%; border-collapse: collapse;\">\n	<tbody>\n		<tr>\n			<td style=\"width: 6.5%; text-align: justify;\" valign=\"top\">&nbsp;</td>\n			<td valign=\"top\">\n			<table align=\"left\" style=\"width: 100%; border-collapse: collapse;\">\n				<tbody>\n					<tr>\n						<td style=\"width: 6.5%; text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">1.5.1</span></span></td>\n						<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><strong>KONTRAKTOR </strong>harus menyediakan pembinaan mengenai Keselamatan Kerja, Pemadam Kebakaran dan Lindungan Lingkungan kepada setiap pekerjanya secara rutin sesuai dengan sifat pekerjaan dan lingkungan kerja masing-masing.</span></span></td>\n					</tr>\n				</tbody>\n			</table>\n			</td>\n		</tr>\n		<tr>\n			<td style=\"width: 6.5%; text-align: justify;\" valign=\"top\">&nbsp;</td>\n			<td valign=\"top\">\n			<table align=\"left\" style=\"width: 100%; border-collapse: collapse;\">\n				<tbody>\n					<tr>\n						<td style=\"width: 6.5%; text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">1.5.2</span></span></td>\n						<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">Pembinaan mengenai Keselamatan Kerja meliputi <em>Safety Talk, Safety Training, Safety Committee, Safety Promotion </em>serta usaha-usaha lain untuk meningkatkan <em>Safety Mindedness </em>pada pekerja.</span></span></td>\n					</tr>\n				</tbody>\n			</table>\n			</td>\n		</tr>\n		<tr>\n			<td style=\"width: 6.5%; text-align: justify;\" valign=\"top\">&nbsp;</td>\n			<td valign=\"top\">\n			<table align=\"left\" style=\"width: 100%; border-collapse: collapse;\">\n				<tbody>\n					<tr>\n						<td style=\"width: 6.5%; text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">1.5.3</span></span></td>\n						<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><strong>PERUSAHAAN </strong>melalui Fungsi HSSE atau Operation Head akan turut serta mengawasi berlangsungnya kegiatan yang dilaksanakan <strong>KONTRAKTOR.</strong></span></span></td>\n					</tr>\n				</tbody>\n			</table>\n			</td>\n		</tr>\n	</tbody>\n</table>\n\n<table align=\"left\" style=\"width: 100%; border-collapse: collapse;\">\n	<tbody>\n		<tr>\n			<td style=\"width: 6.5%; text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">1.6.</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">Tim Komite HSE <strong>KONTRAKTOR</strong></span></span></td>\n		</tr>\n	</tbody>\n</table>\n\n<table align=\"left\" style=\"width: 100%; border-collapse: collapse;\">\n	<tbody>\n		<tr>\n			<td style=\"width: 6.5%; text-align: justify;\" valign=\"top\">&nbsp;</td>\n			<td valign=\"top\">\n			<table align=\"left\" style=\"width: 100%; border-collapse: collapse;\">\n				<tbody>\n					<tr>\n						<td style=\"width: 6.5%; text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">1.6.1</span></span></td>\n						<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><strong>KONTRAKTOR </strong>yang mempunyai jangka waktu kontrak lebih dari 1 (satu) tahun harus membentuk Komite HSE <strong>KONTRAKTOR </strong>yang beranggotakan setiap unsur kegiatan yang ada dalam Organisasi <strong>KONTRAKTOR </strong>yang diberi wewenang penuh mewakili bidang / departemennya.</span></span></td>\n					</tr>\n				</tbody>\n			</table>\n			</td>\n		</tr>\n		<tr>\n			<td style=\"width: 6.5%; text-align: justify;\" valign=\"top\">&nbsp;</td>\n			<td valign=\"top\">\n			<table align=\"left\" style=\"width: 100%; border-collapse: collapse;\">\n				<tbody>\n					<tr>\n						<td style=\"width: 6.5%; text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">1.6.2</span></span></td>\n						<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">Komite HSE <strong>KONTRAKTOR </strong>tersebut harus melaksanakan rapat secara berkala satu bulan sekali guna membahas semua aspek Keselamatan, Kesehatan Kerja, Kebakaran dan Lindungan Lingkungan yang timbul selama kegiatan konstruksi dan kemudian memberikan saran-saran untuk menciptakan lingkungan kerja yang aman dan selamat, hasil pembahasan rapat rutin harus dilaporkan kepada <strong>PERUSAHAAN </strong>sesuai dengan kontrak kinerja <strong>KONTRAKTOR </strong>(KPI).</span></span></td>\n					</tr>\n				</tbody>\n			</table>\n			</td>\n		</tr>\n		<tr>\n			<td style=\"width: 6.5%; text-align: justify;\" valign=\"top\">&nbsp;</td>\n			<td valign=\"top\">\n			<table align=\"left\" style=\"width: 100%; border-collapse: collapse;\">\n				<tbody>\n					<tr>\n						<td style=\"width: 6.5%; text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">1.6.3</span></span></td>\n						<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">Setiap bulan <strong>KONTRAKTOR </strong>harus menyampaikan laporan tertulis kepada <strong>PERUSAHAAN </strong>melalui Fungsi HSE tentang kegiatan yang dilakukan oleh Komite HSE <strong>KONTRAKTOR </strong>tesebut pada item 1.6.1 dan hasil pemenuhan kontrak kinerja <strong>KONTRAKTOR</strong> (KPI).</span></span></td>\n					</tr>\n				</tbody>\n			</table>\n			</td>\n		</tr>\n		<tr>\n			<td style=\"width: 6.5%; text-align: justify;\" valign=\"top\">&nbsp;</td>\n			<td valign=\"top\">\n			<table align=\"left\" style=\"width: 100%; border-collapse: collapse;\">\n				<tbody>\n					<tr>\n						<td style=\"width: 6.5%; text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">1.6.4</span></span></td>\n						<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">Komite HSE <strong>KONTRAKTOR </strong>tersebut mengundang <strong>PERUSAHAAN </strong>melalui Fungsi HSSE dalam setiap pelaksanaan rapat pembahasan.</span></span></td>\n					</tr>\n				</tbody>\n			</table>\n			</td>\n		</tr>\n	</tbody>\n</table>\n\n<table align=\"left\" style=\"width: 100%; border-collapse: collapse;\">\n	<tbody>\n		<tr>\n			<td style=\"width: 6.5%; text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">1.7.</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">Daerah Kerja <strong>KONTRAKTOR </strong></span></span></td>\n		</tr>\n	</tbody>\n</table>\n\n<table align=\"left\" style=\"width: 100%; border-collapse: collapse;\">\n	<tbody>\n		<tr>\n			<td style=\"width: 6.5%; text-align: justify;\" valign=\"top\">&nbsp;</td>\n			<td valign=\"top\">\n			<table align=\"left\" style=\"width: 100%; border-collapse: collapse;\">\n				<tbody>\n					<tr>\n						<td style=\"width: 6.5%; text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">1.7.1</span></span></td>\n						<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><strong>KONTRAKTOR </strong>bertanggungjawab terhadap aspek Keselamatan Kerja seluruh tenaga kerjanya, serta sarana dan lingkungan kerja yang berada di dalam daerah kerjanya.</span></span></td>\n					</tr>\n				</tbody>\n			</table>\n			</td>\n		</tr>\n		<tr>\n			<td style=\"width: 6.5%; text-align: justify;\" valign=\"top\">&nbsp;</td>\n			<td valign=\"top\">\n			<table align=\"left\" style=\"width: 100%; border-collapse: collapse;\">\n				<tbody>\n					<tr>\n						<td style=\"width: 6.5%; text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">1.7.2</span></span></td>\n						<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">Batas daerah kerja <strong>KONTRAKTOR </strong>selama kegiatan kerja adalah daerah yang ditentukan oleh <strong>PERUSAHAAN </strong>bersama <strong>KONTRAKTOR </strong>sesuai dengan kontraknya.</span></span></td>\n					</tr>\n				</tbody>\n			</table>\n			</td>\n		</tr>\n	</tbody>\n</table>\n\n<table align=\"left\" style=\"width: 100%; border-collapse: collapse;\">\n	<tbody>\n		<tr>\n			<td style=\"width: 6.5%; text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">1.8.</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">Lalu Lintas Jalan</span></span></td>\n		</tr>\n	</tbody>\n</table>\n\n<table align=\"left\" style=\"width: 100%; border-collapse: collapse;\">\n	<tbody>\n		<tr>\n			<td style=\"width: 6.5%; text-align: justify;\" valign=\"top\">&nbsp;</td>\n			<td valign=\"top\">\n			<table align=\"left\" style=\"width: 100%; border-collapse: collapse;\">\n				<tbody>\n					<tr>\n						<td style=\"width: 6.5%; text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">1.8.1</span></span></td>\n						<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">Semua pengemudi kendaraan <strong>KONTRAKTOR </strong>/ <strong>SUB KONTRAKTOR </strong>harus memenuhi peraturan lalu lintas yang dikeluarkan oleh <strong>PERUSAHAAN</strong></span></span></td>\n					</tr>\n				</tbody>\n			</table>\n			</td>\n		</tr>\n		<tr>\n			<td style=\"width: 6.5%; text-align: justify;\" valign=\"top\">&nbsp;</td>\n			<td valign=\"top\">\n			<table align=\"left\" style=\"width: 100%; border-collapse: collapse;\">\n				<tbody>\n					<tr>\n						<td style=\"width: 6.5%; text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">1.8.2</span></span></td>\n						<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">Seluruh <strong>KONTRAKTOR </strong>yang mengemudikan kendaraan harus memiliki :</span></span></td>\n					</tr>\n				</tbody>\n			</table>\n			</td>\n		</tr>\n		<tr>\n			<td style=\"width: 6.5%; text-align: justify;\" valign=\"top\">&nbsp;</td>\n			<td valign=\"top\">\n			<table align=\"left\" style=\"width: 100%; border-collapse: collapse;\">\n				<tbody>\n					<tr>\n						<td style=\"width: 6.5%; text-align: justify;\" valign=\"top\">&nbsp;</td>\n						<td valign=\"top\">\n						<table align=\"left\" style=\"width: 100%; border-collapse: collapse;\">\n							<tbody>\n								<tr>\n									<td style=\"width: 6.5%; text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">1.8.2.1&nbsp; &nbsp;&nbsp;</span></span></td>\n									<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">Surat Ijin Mengemudi Kepolisian Negara RI atau Surat Ijin Mengemudi Internasional (untuk pekerja asing bila ada) sesuai dengan jenis kendaraan yang dikemudkan</span></span></td>\n								</tr>\n							</tbody>\n						</table>\n						</td>\n					</tr>\n				</tbody>\n			</table>\n			</td>\n		</tr>\n		<tr>\n			<td style=\"width: 6.5%; text-align: justify;\" valign=\"top\">&nbsp;</td>\n			<td valign=\"top\">\n			<table align=\"left\" style=\"width: 100%; border-collapse: collapse;\">\n				<tbody>\n					<tr>\n						<td style=\"width: 6.5%; text-align: justify;\" valign=\"top\">&nbsp;</td>\n						<td valign=\"top\">\n						<table align=\"left\" style=\"width: 100%; border-collapse: collapse;\">\n							<tbody>\n								<tr>\n									<td style=\"width: 6.5%; text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">1.8.2.2</span></span></td>\n									<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">Surat Ijin Mengemudi dalam daerah Terminal BBM (SIM Khusus) yang dikeluarkan oleh HSSE PERTAMINA akan diatur oleh HSE Terminal BBM, DPPU &amp; Depot LPG</span></span></td>\n								</tr>\n							</tbody>\n						</table>\n						</td>\n					</tr>\n				</tbody>\n			</table>\n			</td>\n		</tr>\n		<tr>\n			<td style=\"width: 6.5%; text-align: justify;\" valign=\"top\">&nbsp;</td>\n			<td valign=\"top\">\n			<table align=\"left\" style=\"width: 100%; border-collapse: collapse;\">\n				<tbody>\n					<tr>\n						<td style=\"width: 6.5%; text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">1.8.3</span></span></td>\n						<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">Prosedur dan tata cara memperoleh SIM Khusus sesuai dengan peraturan lalu lintas jalan <strong>PERUSAHAAN.</strong></span></span></td>\n					</tr>\n				</tbody>\n			</table>\n			</td>\n		</tr>\n		<tr>\n			<td style=\"width: 6.5%; text-align: justify;\" valign=\"top\">&nbsp;</td>\n			<td valign=\"top\">\n			<table align=\"left\" style=\"width: 100%; border-collapse: collapse;\">\n				<tbody>\n					<tr>\n						<td style=\"width: 6.5%; text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">1.8.4</span></span></td>\n						<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">Setiap kecelakaan yang menimpa asset PERTAMINA atau yang menimpa kendaraan PERTAMINA harus dilaporkan kepada <strong>PERUSAHAAN </strong>melalui HSE Lokasi sesuai dengan prosedur dan tata cara yang berlaku di <strong>PERUSAHAAN.</strong></span></span></td>\n					</tr>\n				</tbody>\n			</table>\n			</td>\n		</tr>\n		<tr>\n			<td style=\"width: 6.5%; text-align: justify;\" valign=\"top\">&nbsp;</td>\n			<td valign=\"top\">\n			<table align=\"left\" style=\"width: 100%; border-collapse: collapse;\">\n				<tbody>\n					<tr>\n						<td style=\"width: 6.5%; text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">1.8.5</span></span></td>\n						<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">Setiap kendaraan dan peralatan yang akan masuk daerah Terminal BBM, DPPU &amp; Depot LPG harus diperiksa mengenai aspek keselamatan oleh Fungsi Sekuriti, HSE dan Fungsi terkait.</span></span></td>\n					</tr>\n				</tbody>\n			</table>\n			</td>\n		</tr>\n	</tbody>\n</table>\n\n<table align=\"left\" style=\"width: 100%; border-collapse: collapse;\">\n	<tbody>\n		<tr>\n			<td style=\"width: 6.5%; text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">1.9.</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">Merokok</span></span></td>\n		</tr>\n	</tbody>\n</table>\n\n<table align=\"left\" style=\"width: 100%; border-collapse: collapse;\">\n	<tbody>\n		<tr>\n			<td style=\"width: 6.5%; text-align: justify;\" valign=\"top\">&nbsp;</td>\n			<td valign=\"top\">\n			<table align=\"left\" style=\"width: 100%; border-collapse: collapse;\">\n				<tbody>\n					<tr>\n						<td style=\"width: 6.5%; text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">1.9.1</span></span></td>\n						<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">Merokok dalam daerah Terminal BBM, DPPU &amp; Depot LPG hanya diijinkan pada tempat-tempat tertentu yang diberi tanda khusus / ditetapkan oleh <strong>PERUSAHAAN.</strong></span></span></td>\n					</tr>\n				</tbody>\n			</table>\n			</td>\n		</tr>\n		<tr>\n			<td style=\"width: 6.5%; text-align: justify;\" valign=\"top\">&nbsp;</td>\n			<td valign=\"top\">\n			<table align=\"left\" style=\"width: 100%; border-collapse: collapse;\">\n				<tbody>\n					<tr>\n						<td style=\"width: 6.5%; text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">1.9.2</span></span></td>\n						<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><strong>KONTRAKTOR </strong>bertanggungjawab untuk mengawasi penggunaan semua tempat merokok seperti tersebut pada item 1.9.1 oleh pekerjanya masing-masing.</span></span></td>\n					</tr>\n				</tbody>\n			</table>\n			</td>\n		</tr>\n		<tr>\n			<td style=\"width: 6.5%; text-align: justify;\" valign=\"top\">&nbsp;</td>\n			<td valign=\"top\">\n			<table align=\"left\" style=\"width: 100%; border-collapse: collapse;\">\n				<tbody>\n					<tr>\n						<td style=\"width: 6.5%; text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">1.9.3</span></span></td>\n						<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">Dilarang merokok dalam kendaraan di dalam area Terminal BBM, DPPU &amp; Depot LPG dan di daerah kerjanya.</span></span></td>\n					</tr>\n				</tbody>\n			</table>\n			</td>\n		</tr>\n		<tr>\n			<td style=\"width: 6.5%; text-align: justify;\" valign=\"top\">&nbsp;</td>\n			<td valign=\"top\">\n			<table align=\"left\" style=\"width: 100%; border-collapse: collapse;\">\n				<tbody>\n					<tr>\n						<td style=\"width: 6.5%; text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">1.9.4</span></span></td>\n						<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">Dilarang membawa rokok, korek api dan alat pembuat api lainnya bagi setiap orang yang memasuki daerah Terminal BBM, DPPU &amp; Depot LPG.</span></span></td>\n					</tr>\n				</tbody>\n			</table>\n			</td>\n		</tr>\n		<tr>\n			<td style=\"width: 6.5%; text-align: justify;\" valign=\"top\">&nbsp;</td>\n			<td valign=\"top\">\n			<table align=\"left\" style=\"width: 100%; border-collapse: collapse;\">\n				<tbody>\n					<tr>\n						<td style=\"width: 6.5%; text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">1.9.5</span></span></td>\n						<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">Apabila ada bunyi tanda bahaya, maka semua kegiatan merokok harus dihentikan.</span></span></td>\n					</tr>\n				</tbody>\n			</table>\n			</td>\n		</tr>\n		<tr>\n			<td style=\"width: 6.5%; text-align: justify;\" valign=\"top\">&nbsp;</td>\n			<td valign=\"top\">\n			<table align=\"left\" style=\"width: 100%; border-collapse: collapse;\">\n				<tbody>\n					<tr>\n						<td style=\"width: 6.5%; text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">1.9.6</span></span></td>\n						<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">Pelanggaran merokok di dalam lokasi akan mendapatkan sanksi tegas sesuai aturan <strong>PERUSAHAAN</strong>.</span></span></td>\n					</tr>\n				</tbody>\n			</table>\n			</td>\n		</tr>\n	</tbody>\n</table>\n\n<table align=\"left\" style=\"width: 100%; border-collapse: collapse;\">\n	<tbody>\n		<tr>\n			<td style=\"width: 6.5%; text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">1.10.</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">Ijin Kerja</span></span></td>\n		</tr>\n	</tbody>\n</table>\n\n<table align=\"left\" style=\"width: 100%; border-collapse: collapse;\">\n	<tbody>\n		<tr>\n			<td style=\"width: 6.5%; text-align: justify;\" valign=\"top\">&nbsp;</td>\n			<td valign=\"top\">\n			<table align=\"left\" style=\"width: 100%; border-collapse: collapse;\">\n				<tbody>\n					<tr>\n						<td style=\"width: 6.5%; text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">1.10.1</span></span></td>\n						<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">Semua pekerjaan yang dilakukan oleh <strong>KONTRAKTOR / SUB KONTRAKTOR </strong>di dalam dan di luar daerah Terminal BBM, DPPU &amp; Depot LPG harus dilengkapi dengan Ijin Kerja.</span></span></td>\n					</tr>\n				</tbody>\n			</table>\n			</td>\n		</tr>\n		<tr>\n			<td style=\"width: 6.5%; text-align: justify;\" valign=\"top\">&nbsp;</td>\n			<td valign=\"top\">\n			<table align=\"left\" style=\"width: 100%; border-collapse: collapse;\">\n				<tbody>\n					<tr>\n						<td style=\"width: 6.5%; text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">1.10.2</span></span></td>\n						<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">Tata cara dan prosedur untuk memperoleh ijin kerja tersebut sesuai dengan ketentuan yang berlaku di <strong>PERUSAHAAN</strong>.</span></span></td>\n					</tr>\n				</tbody>\n			</table>\n			</td>\n		</tr>\n		<tr>\n			<td style=\"width: 6.5%; text-align: justify;\" valign=\"top\">&nbsp;</td>\n			<td valign=\"top\">\n			<table align=\"left\" style=\"width: 100%; border-collapse: collapse;\">\n				<tbody>\n					<tr>\n						<td style=\"width: 6.5%; text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">1.10.3</span></span></td>\n						<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">Setiap saat bila di daerah kerja <strong>KONTRAKTOR / SUB KONTRAKTOR </strong>terjadi keadaan yang dianggap membahayakan atau berpotensi bahaya karena <strong>KONTRAKTOR / SUB KONTRAKTOR </strong>tidak melaksanakan PEKERJAAN secara aman, setiap Pekerjaan <strong>PERUSAHAAN </strong>/ petugas HSE lokasi yang berwenang dapat memerintahkan <strong>KONTRAKTOR </strong>untuk menghentikan pekerjaan yang sedang berlangsung sesegera mungkin, memindahkan personil atau material yang tidak sesuai dengan kententuan aspek HSE dari <strong>LOKASI PEKERJAAN </strong>dan mengambil tindakan apapun yang dianggap perlu oleh <strong>PERUSAHAAN </strong>sesuai ketentuan Prosedur Ijin Kerja Aman untuk memastikan bahwa <strong>KONTRAKTOR </strong>melaksanakan <strong>PEKERJAAN </strong>secara aman. Setiap dan semua biaya yang diakibatkan keterlambatan jadwal yang ditimbulkan dari kegagalan untuk melakukan <strong>PEKERJAAN </strong>dengan cara yang aman sesuai dengan <strong>KONTRAK </strong>akan ditanggung oleh <strong>KONTRAKTOR</strong>.</span></span></td>\n					</tr>\n				</tbody>\n			</table>\n			</td>\n		</tr>\n		<tr>\n			<td style=\"width: 6.5%; text-align: justify;\" valign=\"top\">&nbsp;</td>\n			<td valign=\"top\">\n			<table align=\"left\" style=\"width: 100%; border-collapse: collapse;\">\n				<tbody>\n					<tr>\n						<td style=\"width: 6.5%; text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">1.10.4</span></span></td>\n						<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">Petugas HSE lokasi akan memberikan petunjuk kepada Petugas Keselamatan dari <strong>KONTRAKTOR </strong>tentang penghentian pekerjaan dan pekerjaan dapat dimulai kembali sesuai dengan ketentuan <strong>PERUSAHAAN</strong>.</span></span></td>\n					</tr>\n				</tbody>\n			</table>\n			</td>\n		</tr>\n		<tr>\n			<td style=\"width: 6.5%; text-align: justify;\" valign=\"top\">&nbsp;</td>\n			<td valign=\"top\">\n			<table align=\"left\" style=\"width: 100%; border-collapse: collapse;\">\n				<tbody>\n					<tr>\n						<td style=\"width: 6.5%; text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">1.10.5</span></span></td>\n						<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">Bila ada bunyi tanda bahaya, maka semua pekerjaan <strong>KONTRAKTOR </strong>harus dihentikan.</span></span></td>\n					</tr>\n				</tbody>\n			</table>\n			</td>\n		</tr>\n	</tbody>\n</table>\n\n<table align=\"left\" style=\"width: 100%; border-collapse: collapse;\">\n	<tbody>\n		<tr>\n			<td style=\"width: 6.5%; text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">1.11.</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">Alat Keselamatan Kerja</span></span></td>\n		</tr>\n	</tbody>\n</table>\n\n<table align=\"left\" style=\"width: 100%; border-collapse: collapse;\">\n	<tbody>\n		<tr>\n			<td style=\"width: 6.5%; text-align: justify;\" valign=\"top\">&nbsp;</td>\n			<td valign=\"top\">\n			<table align=\"left\" style=\"width: 100%; border-collapse: collapse;\">\n				<tbody>\n					<tr>\n						<td style=\"width: 6.5%; text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">1.11.1</span></span></td>\n						<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">Setiap pekerja diwajibkan / diharuskan menggunakan alat pelindung diri minimal topi keselamatan kerja, <em>safety</em> <em>shoes</em> dan rompi / <em>safety vest </em>di dalam area Terminal BBM, DPPU &amp; Depot LPG dan di lokasi tempat pekerjaan <strong>KONTRAKTOR / SUB KONTRAKTOR</strong>. Adapun alat pelindung diri lainnya disesuaikan dengan sifat pekerjaan dan sifat bahayanya yang akan dilakukan dan atau mengacu mitigasi resiko yang ada pada JHSEA di HSE Plan.</span></span></td>\n					</tr>\n				</tbody>\n			</table>\n			</td>\n		</tr>\n		<tr>\n			<td style=\"width: 6.5%; text-align: justify;\" valign=\"top\">&nbsp;</td>\n			<td valign=\"top\">\n			<table align=\"left\" style=\"width: 100%; border-collapse: collapse;\">\n				<tbody>\n					<tr>\n						<td style=\"width: 6.5%; text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">1.11.2</span></span></td>\n						<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">Setiap pekerja dan peralatan kerja (mesin-mesin) <strong>KONTRAKTOR / SUB KONTRAKTOR </strong>harus diperiksa dan dilengkapi dengan alat-alat keselamatan kerja yang sesuai dengan sifat pekerjaan dan sifat bahayanya.</span></span></td>\n					</tr>\n				</tbody>\n			</table>\n			</td>\n		</tr>\n		<tr>\n			<td style=\"width: 6.5%; text-align: justify;\" valign=\"top\">&nbsp;</td>\n			<td valign=\"top\">\n			<table align=\"left\" style=\"width: 100%; border-collapse: collapse;\">\n				<tbody>\n					<tr>\n						<td style=\"width: 6.5%; text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">1.11.3</span></span></td>\n						<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">Alat-alat keselamatan yang digunakan oleh <strong>KONTRAKTOR / SUB KONTRAKTOR </strong>harus memenuhi standar yang berlaku di <strong>PERUSAHAAN.</strong></span></span></td>\n					</tr>\n				</tbody>\n			</table>\n			</td>\n		</tr>\n	</tbody>\n</table>\n\n<table align=\"left\" style=\"width: 100%; border-collapse: collapse;\">\n	<tbody>\n		<tr>\n			<td style=\"width: 6.5%; text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">1.12.</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">Ketentuan Keselamatan Kerja yang sifatnya umum</span></span></td>\n		</tr>\n	</tbody>\n</table>\n\n<table align=\"left\" style=\"width: 100%; border-collapse: collapse;\">\n	<tbody>\n		<tr>\n			<td style=\"width: 6.5%; text-align: justify;\" valign=\"top\">&nbsp;</td>\n			<td valign=\"top\">\n			<table align=\"left\" style=\"width: 100%; border-collapse: collapse;\">\n				<tbody>\n					<tr>\n						<td style=\"width: 6.5%; text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">1.12.1</span></span></td>\n						<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><strong>KONTRAKTOR </strong>harus yakin dan bertanggungjawab bahwa semua sistem dan metode kerja yang aman dan selamat melaksanakan pekerjaan, dipatuhi oleh semua pekerjanya sesuai dengan Peraturan Pemerintah maupun <strong>PERUSAHAAN </strong>yang berlaku untuk semua jenis pekerjaan antara lain :</span></span></td>\n					</tr>\n				</tbody>\n			</table>\n			</td>\n		</tr>\n		<tr>\n			<td style=\"width: 6.5%; text-align: justify;\" valign=\"top\">&nbsp;</td>\n			<td valign=\"top\">\n			<table align=\"left\" style=\"width: 100%; border-collapse: collapse;\">\n				<tbody>\n					<tr>\n						<td style=\"width: 6.5%; text-align: justify;\" valign=\"top\">&nbsp;</td>\n						<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">1.12.1.1&nbsp; &nbsp;Pemakaian alat-alat berat &nbsp;</span></span></td>\n					</tr>\n				</tbody>\n			</table>\n			</td>\n		</tr>\n		<tr>\n			<td style=\"width: 6.5%; text-align: justify;\" valign=\"top\">&nbsp;</td>\n			<td valign=\"top\">\n			<table align=\"left\" style=\"width: 100%; border-collapse: collapse;\">\n				<tbody>\n					<tr>\n						<td style=\"width: 6.5%; text-align: justify;\" valign=\"top\">&nbsp;</td>\n						<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">1.12.1.2&nbsp; &nbsp;Pekerjaan listrik</span></span></td>\n					</tr>\n				</tbody>\n			</table>\n			</td>\n		</tr>\n		<tr>\n			<td style=\"width: 6.5%; text-align: justify;\" valign=\"top\">&nbsp;</td>\n			<td valign=\"top\">\n			<table align=\"left\" style=\"width: 100%; border-collapse: collapse;\">\n				<tbody>\n					<tr>\n						<td style=\"width: 6.5%; text-align: justify;\" valign=\"top\">&nbsp;</td>\n						<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">1.12.1.3&nbsp; &nbsp;Pekerjaan pengelasan</span></span></td>\n					</tr>\n				</tbody>\n			</table>\n			</td>\n		</tr>\n		<tr>\n			<td style=\"width: 6.5%; text-align: justify;\" valign=\"top\">&nbsp;</td>\n			<td valign=\"top\">\n			<table align=\"left\" style=\"width: 100%; border-collapse: collapse;\">\n				<tbody>\n					<tr>\n						<td style=\"width: 6.5%; text-align: justify;\" valign=\"top\">&nbsp;</td>\n						<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">1.12.1.4&nbsp; &nbsp;Pekerjaan instrumen</span></span></td>\n					</tr>\n				</tbody>\n			</table>\n			</td>\n		</tr>\n		<tr>\n			<td style=\"width: 6.5%; text-align: justify;\" valign=\"top\">&nbsp;</td>\n			<td valign=\"top\">\n			<table align=\"left\" style=\"width: 100%; border-collapse: collapse;\">\n				<tbody>\n					<tr>\n						<td style=\"width: 6.5%; text-align: justify;\" valign=\"top\">&nbsp;</td>\n						<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">1.12.1.5&nbsp; &nbsp;Penimbunan barang</span></span></td>\n					</tr>\n				</tbody>\n			</table>\n			</td>\n		</tr>\n		<tr>\n			<td style=\"width: 6.5%; text-align: justify;\" valign=\"top\">&nbsp;</td>\n			<td valign=\"top\">\n			<table align=\"left\" style=\"width: 100%; border-collapse: collapse;\">\n				<tbody>\n					<tr>\n						<td style=\"width: 6.5%; text-align: justify;\" valign=\"top\">&nbsp;</td>\n						<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">1.12.1.6&nbsp; &nbsp;Pemakaian scaffolding / perancah</span></span></td>\n					</tr>\n				</tbody>\n			</table>\n			</td>\n		</tr>\n		<tr>\n			<td style=\"width: 6.5%; text-align: justify;\" valign=\"top\">&nbsp;</td>\n			<td valign=\"top\">\n			<table align=\"left\" style=\"width: 100%; border-collapse: collapse;\">\n				<tbody>\n					<tr>\n						<td style=\"width: 6.5%; text-align: justify;\" valign=\"top\">&nbsp;</td>\n						<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">1.12.1.7&nbsp; &nbsp;Pemeliharaan kebersihan</span></span></td>\n					</tr>\n				</tbody>\n			</table>\n			</td>\n		</tr>\n		<tr>\n			<td style=\"width: 6.5%; text-align: justify;\" valign=\"top\">&nbsp;</td>\n			<td valign=\"top\">\n			<table align=\"left\" style=\"width: 100%; border-collapse: collapse;\">\n				<tbody>\n					<tr>\n						<td style=\"width: 6.5%; text-align: justify;\" valign=\"top\">&nbsp;</td>\n						<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">1.12.1.8&nbsp; &nbsp;Dll</span></span></td>\n					</tr>\n				</tbody>\n			</table>\n			</td>\n		</tr>\n		<tr>\n			<td style=\"width: 6.5%; text-align: justify;\" valign=\"top\">&nbsp;</td>\n			<td valign=\"top\">\n			<table align=\"left\" style=\"width: 100%; border-collapse: collapse;\">\n				<tbody>\n					<tr>\n						<td style=\"width: 6.5%; text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">1.12.2</span></span></td>\n						<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">Syarat-syarat keselamatan kerja untuk pekerjaan lain yang belum tercakup di dalam item 1.12.1 akan disesuaikan dengan Peraturan-peraturan Pemerintah dan <strong>PERUSAHAAN </strong>yang berlaku.</span></span></td>\n					</tr>\n				</tbody>\n			</table>\n			</td>\n		</tr>\n		<tr>\n			<td style=\"width: 6.5%; text-align: justify;\" valign=\"top\">&nbsp;</td>\n			<td valign=\"top\">\n			<table align=\"left\" style=\"width: 100%; border-collapse: collapse;\">\n				<tbody>\n					<tr>\n						<td style=\"width: 6.5%; text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">1.12.3</span></span></td>\n						<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><strong>KONTRAKTOR </strong>diwajibkan melaporkan setiap kejadian / kecelakaan sekecil apapun yang terjadi di dalam daerah kerjanya kepada petugas HSE Lokasi dalam waktu 1 x 12 (satu kali dua belas) jam melalui pengawas pekerjaan.</span></span></td>\n					</tr>\n				</tbody>\n			</table>\n			</td>\n		</tr>\n	</tbody>\n</table>\n\n<table align=\"left\" style=\"width: 100%; border-collapse: collapse;\">\n	<tbody>\n		<tr>\n			<td style=\"width: 6.5%; text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">1.13</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">Perlindungan Terhadap Kebakaran</span></span></td>\n		</tr>\n	</tbody>\n</table>\n\n<table align=\"left\" style=\"width: 100%; border-collapse: collapse;\">\n	<tbody>\n		<tr>\n			<td style=\"width: 6.5%; text-align: justify;\" valign=\"top\">&nbsp;</td>\n			<td valign=\"top\">\n			<table align=\"left\" style=\"width: 100%; border-collapse: collapse;\">\n				<tbody>\n					<tr>\n						<td style=\"width: 6.5%; text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">1.13.1</span></span></td>\n						<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><strong>KONTRAKTOR </strong>harus menyediakan peralatan dan sarana pemadam kebakaran untuk pertolongan sesuai dengan standar <strong>PERUSAHAAN </strong>dan menunjuk petugas khusus mengawasi pekerjaan-pekerjaan yang dapat menimbulkan kebakaran.</span></span></td>\n					</tr>\n				</tbody>\n			</table>\n			</td>\n		</tr>\n		<tr>\n			<td style=\"width: 6.5%; text-align: justify;\" valign=\"top\">&nbsp;</td>\n			<td valign=\"top\">\n			<table align=\"left\" style=\"width: 100%; border-collapse: collapse;\">\n				<tbody>\n					<tr>\n						<td style=\"width: 6.5%; text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">1.13.2</span></span></td>\n						<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">Personil <strong>KONTRAKTOR </strong>harus mampu menggunakan alat pemadam untuk menanggulangi kebakaran tingkat pertama.</span></span></td>\n					</tr>\n				</tbody>\n			</table>\n			</td>\n		</tr>\n		<tr>\n			<td style=\"width: 6.5%; text-align: justify;\" valign=\"top\">&nbsp;</td>\n			<td valign=\"top\">\n			<table align=\"left\" style=\"width: 100%; border-collapse: collapse;\">\n				<tbody>\n					<tr>\n						<td style=\"width: 6.5%; text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">1.13.3</span></span></td>\n						<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">Penggunaan <em>Fire Hydrant</em> dan perlengkapan pemadam lainnya milik <strong>PERUSAHAAN </strong>baik untuk upaya pemadam kebakaran maupun lingkup pekerjaan, harus sepengetahuan dan seijin Petugas HSE Lokasi dan harus ditutup kembali bila pemakaian telah selesai.</span></span></td>\n					</tr>\n				</tbody>\n			</table>\n			</td>\n		</tr>\n		<tr>\n			<td style=\"width: 6.5%; text-align: justify;\" valign=\"top\">&nbsp;</td>\n			<td valign=\"top\">\n			<table align=\"left\" style=\"width: 100%; border-collapse: collapse;\">\n				<tbody>\n					<tr>\n						<td style=\"width: 6.5%; text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">1.13.4</span></span></td>\n						<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">Setiap pekerja <strong>KONTRAKTOR </strong>harus mengetahui dan mematuhi semua tanda bahaya kebakaran yang berlaku di <strong>PERUSAHAAN</strong>.</span></span></td>\n					</tr>\n				</tbody>\n			</table>\n			</td>\n		</tr>\n		<tr>\n			<td style=\"width: 6.5%; text-align: justify;\" valign=\"top\">&nbsp;</td>\n			<td valign=\"top\">\n			<table align=\"left\" style=\"width: 100%; border-collapse: collapse;\">\n				<tbody>\n					<tr>\n						<td style=\"width: 6.5%; text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">1.13.5</span></span></td>\n						<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">Tenaga <strong>KONTRAKTOR </strong>yang sudah terlatih, diharuskan membantu penanggulangan setiap kebakaran yang terjadi di daerah kerjanya sambil menunggu datangnya petugas pemadam kebakaran di <strong>PERUSAHAAN</strong>.</span></span></td>\n					</tr>\n				</tbody>\n			</table>\n			</td>\n		</tr>\n	</tbody>\n</table>\n\n<p style=\"text-align: justify;\">&nbsp;</p>\n\n<div style=\"page-break-after: always\"><span style=\"display: none;\">&nbsp;</span></div>\n\n<p style=\"text-align: right;\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">LAMPIRAN F</span></span></p>\n\n<p style=\"text-align: center;\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><strong>ASURANSI</strong></span></span></p>\n\n<p><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">Dalam pelaksanaan&nbsp;<strong>PEKERJAAN&nbsp;</strong>sebagaimana <b>KONTRAK&nbsp;</b>ini tidak mengatur mengenai Asuransi.</span></span></p>\n\n<div style=\"page-break-after: always\"><span style=\"display: none;\">&nbsp;</span></div>\n\n<p style=\"text-align: right;\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">LAMPIRAN G</span></span></p>\n\n<p style=\"text-align: center;\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><strong>PASAL 1</strong></span></span></p>\n\n<p style=\"text-align: center;\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><strong>PENGAWASAN PEKERJAAN</strong></span></span></p>\n\n<table align=\"left\" style=\"width: 100%; border-collapse: collapse;\">\n	<tbody>\n		<tr>\n			<td style=\"width: 6.5%;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">1.1.</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><strong>PERUSAHAAN </strong>menunjukan <strong>Sr. Spv. HSE Operation </strong>untuk mewakili <strong>PERUSAHAAN </strong>selaku Direksi Pekerjaan dibantu <strong>Jr. Analyst Safety & Hygiene Industry </strong>sebagai pengawasan harian dan pengawas lapangan yang masing-masing sesuai hierarki atau bersama-sama berwenang untuk memberikan instruksi, pemberitahuan serta bertanggung jawab atas pengawasan pelaksanaan <strong>PEKERJAAN </strong>berdasarkan <strong>KONTRAK </strong>ini.</span></span></td>\n		</tr>\n		<tr>\n			<td style=\"width: 6.5%;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">1.2.</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><strong>KONTRAKTOR </strong>menunjuk Manajer Proyek atau wakil yang ditunjuk oleh pejabat yang bersangkutan untuk mewakili <strong>KONTRAKTOR </strong>yang mempunyai wewenang penuh untuk mengambil tindakan atau keputusan dan melaksanakan setiap instruksi / permintaan <strong>PERUSAHAAN </strong>dalam pelaksanaan <strong>PEKERJAAN </strong>berdasarkan <strong>KONTRAK </strong>ini.</span></span></td>\n		</tr>\n		<tr>\n			<td style=\"width: 6.5%;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">1.3.</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><strong>PERUSAHAAN </strong>berhak meminta pemberhentian dan penggantian Manajer Proyek dan / atau karyawan <strong>KONTRAKTOR</strong>, apabila berdasarkan pertimbangan <strong>PERUSAHAAN </strong>yang bersangkutan dianggap tidak cakap atau lalai dalam melaksanakan <strong>PEKERJAAN </strong>sehingga akan menghambat pelaksanaan <strong>PEKERJAAN</strong>.<strong> </strong></span></span></td>\n		</tr>\n	</tbody>\n</table>\n\n<p>&nbsp;</p>\n\n<p style=\"text-align: center;\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><strong>PASAL 2<br />\nPEMBERITAHUAN</strong></span></span></p>\n\n<table align=\"left\" style=\"width: 100%; border-collapse: collapse;\">\n	<tbody>\n		<tr>\n			<td style=\"width: 6.5%;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">2.1.</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">Segala surat menyurat, komunikasi atau korespondensi dalam pelaksanaan <strong>KONTRAK </strong>akan dibertahukan atau disampaikan melalui alamat seperti tertera di bawah ini</span></span></td>\n		</tr>\n		<tr>\n			<td style=\"width: 6.5%;\" valign=\"top\">&nbsp;</td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><strong>PERUSAHAAN :</strong></span></span></td>\n		</tr>\n		<tr>\n			<td style=\"width: 6.5%;\" valign=\"top\">&nbsp;</td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">Jalan Garuda No. 1 Makassar, Sulawesi Selatan</span></span></td>\n		</tr>\n		<tr>\n			<td style=\"width: 6.5%;\" valign=\"top\">&nbsp;</td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">(0411) 871181</span></span></td>\n		</tr>\n		<tr>\n			<td style=\"width: 6.5%;\" valign=\"top\">&nbsp;</td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">(0411) 823123</span></span></td>\n		</tr>\n		<tr>\n			<td style=\"width: 6.5%;\" valign=\"top\">&nbsp;</td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">Up.&nbsp;Region Manager HSSE MOR VII</span></span></td>\n		</tr>\n		<tr>\n			<td style=\"width: 6.5%;\" valign=\"top\">&nbsp;</td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\"><strong>KONTRAKTOR :</strong></span></span></td>\n		</tr>\n		<tr>\n			<td style=\"width: 6.5%;\" valign=\"top\">&nbsp;</td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">PT. Mitrasejati Yakapepe</span></span></td>\n		</tr>\n		<tr>\n			<td style=\"width: 6.5%;\" valign=\"top\">&nbsp;</td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">Jalan Garuda No. 1 Makassar, Sulawesi Selatan</span></span></td>\n		</tr>\n		<tr>\n			<td style=\"width: 6.5%;\" valign=\"top\">&nbsp;</td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">(0411) 835458</span></span></td>\n		</tr>\n		<tr>\n			<td style=\"width: 6.5%;\" valign=\"top\">&nbsp;</td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">(0411) 871181</span></span></td>\n		</tr>\n		<tr>\n			<td style=\"width: 6.5%;\" valign=\"top\">&nbsp;</td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">Up. Direktur</span></span></td>\n		</tr>\n		<tr>\n			<td style=\"width: 6.5%;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">2.2.</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">Setiap <strong>PIHAK </strong>terkait akan segera memberitahukan secara tertulis kepada <strong>PIHAK </strong>lainnya dalam hal perubahan alamat atau tempat pemberitahuan sebagaimana tersebut di atas.</span></span></td>\n		</tr>\n	</tbody>\n</table>\n\n<p>&nbsp;</p>\n\n<p style=\"text-align: center;\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">&nbsp;<strong>PASAL 3<br />\nADDENDUM</strong></span></span></p>\n\n<table align=\"left\" style=\"width: 100%; border-collapse: collapse;\">\n	<tbody>\n		<tr>\n			<td style=\"width: 6.5%; text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family:Arial Narrow;\">Setiap perubahan, modifikasi atau pengaturan lebih lanjut terhadap hal-hal yang tidak dan/atau belum cukup diatur dalam <strong>KONTRAK </strong>ini harus disetujui secara tertulis oleh <strong>PARA PIHAK </strong>serta dibuat dalam bentuk Addendum yang merupakan bagian yang tidak terpisahkan dari <strong>KONTRAK </strong>ini.</span></span></td>\n		</tr>\n	</tbody>\n</table>\n\n<p style=\"text-align: justify;\">&nbsp;</p>\n', NULL, 1, '2019-09-03 08:49:29', 47, 3, '', '2019-03-13 08:22:21', 48, 3, '', '2019-03-13 07:38:17', 28, 3, 'Ok', '2019-03-13 05:10:07', 11, '', '3', '0000-00-00 00:00:00', 7, '', '3', '0000-00-00 00:00:00', 1, 0, '2019-03-12 14:12:30', '1', '2019-09-03 01:49:29', '1', 0, 0, 0, 0);
