INSERT INTO `tr_template` (`TEMPLATE_ID`, `USER_ID`, `TEMPLATE_CONTENT`, `TEMPLATE_TITLE`, `ACTIVE`, `DELETED`, `CREATED_ON`, `CREATED_BY`, `UPDATED_ON`, `UPDATED_BY`) VALUES
(2, 1, '<p style=\"text-align: center;\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\"><strong>POKOK-POKOK PERJANJIAN<br />\n[CONTRACT_TITLE]<br />\n[CONTRACT_NUMBER]</strong></span></span></p>\n\n<p><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">Perjanjian ini (&ldquo;KONTRAK&rdquo;) dibuat dan ditandatangani pada&nbsp;[CONTRACT_DATE_CREATION] (&ldquo;<strong>TANGGAL KONTRAK</strong>&rdquo;),&nbsp;oleh:</span></span></p>\n\n<table align=\"left\" style=\"width: 100%; border-collapse: collapse;\">\n	<tbody>\n		<tr>\n			<td style=\"width: 5%; text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">I.</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\"><strong>PT PERTAMINA (PERSERO)</strong>, suatu perusahaan yang didirikan berdasarkan hukum Indonesia, berkedudukan dan berkantor pusat di Jakarta Pusat, dalam hal ini diwakili oleh&nbsp;<strong>[PERTAMINA_REPRESENTATIVE_NAME]</strong> selaku <strong>[PERTAMINA_REPRESENTATIVE_POSITION]</strong>, selanjutnya dalam <strong>KONTRAK </strong>ini disebut &rdquo;PERUSAHAAN&rdquo;, dan</span></span></td>\n		</tr>\n		<tr>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">II.</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\"><strong>[VENDOR_COMPANY_NAME]</strong>, [VENDOR_COMPANY_DESCRIPTION], berkedudukan dan berkantor pusat di&nbsp;[VENDOR_COMPANY_ADDRESS] dalam hal ini diwakili oleh [VENDOR_REPRESENTATIVE_NAME], selaku&nbsp; [VENDOR_REPRESENTATIVE_POSITION], selanjutnya dalam KONTRAK ini disebut &rdquo;KONTRAKTOR&rdquo;.</span></span></td>\n		</tr>\n	</tbody>\n</table>\n\n<p><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">&nbsp;&nbsp;<br />\n<span style=\"font-family: Arial Narrow;\">Selanjutnya PERUSAHAAN dan KONTRAKTOR secara sendiri-sendiri disebut &ldquo;PIHAK&rdquo; dan secara bersama-sama disebut &ldquo;PARA PIHAK&rdquo;.</span></span></span></p>\n\n<p style=\"text-align: center;\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\"><strong>BAHWASANYA;</strong></span></span></p>\n\n<p style=\"text-align: justify;\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">PERUSAHAAN mempunyai kegiatan usaha dibidang energi yaitu minyak dan gas bumi, energi baru dan terbarukan, dan dalam kaitannya dengan hal tersebut PERUSAHAAN memerlukan dilakukannya pekerjaan sebagaimana diatur dalam KONTRAK ini; dan<br />\nKONTRAKTOR adalah perusahaan yang mempunyai kemampuan dan pengalaman serta sanggup untuk melaksanakan pekerjaan yang diperlukan oleh PERUSAHAAN.</span></span></p>\n\n<p style=\"text-align: justify;\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">BERDASARKAN PERTIMBANGAN DI ATAS, PARA PIHAK MENYEPAKATI HAL-HAL BERIKUT:</span></span></p>\n\n<table align=\"left\" style=\"width: 100%; border-collapse: collapse;\">\n	<tbody>\n		<tr>\n			<td style=\"width: 5%; text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">1.</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">Dokumen-dokumen yang disebut dibawah merupakan bagian yang tidak terpisahkan dari &nbsp;KONTRAK ini:</span></span></td>\n		</tr>\n		<tr>\n			<td style=\"text-align: justify;\" valign=\"top\">&nbsp;</td>\n			<td valign=\"top\">\n			<table align=\"left\" style=\"width: 100%; border-collapse: collapse;\">\n				<tbody>\n					<tr>\n						<td style=\"width: 5%; text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">A.</span></span></td>\n						<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">Lampiran A&nbsp;&nbsp;&nbsp;&nbsp; : Syarat-syarat dan Ketentuan-ketentuan Umum.</span></span></td>\n					</tr>\n					<tr>\n						<td style=\"width: 5%; text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">B.</span></span></td>\n						<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">Lampiran A-1 &nbsp;: Pengecualian Lampiran A</span></span></td>\n					</tr>\n					<tr>\n						<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">C.</span></span></td>\n						<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">Lampiran B&nbsp;&nbsp;&nbsp; &nbsp;: Lingkup Pekerjaan.</span></span></td>\n					</tr>\n					<tr>\n						<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">D.</span></span></td>\n						<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">Lampiran C&nbsp;&nbsp;&nbsp;&nbsp; : Harga Kontrak dan Ketentuan Pembayaran.</span></span></td>\n					</tr>\n					<tr>\n						<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">E.</span></span></td>\n						<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">Lampiran D&nbsp;&nbsp;&nbsp; &nbsp;: Jangka Waktu.&nbsp;</span></span></td>\n					</tr>\n					<tr>\n						<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">F.</span></span></td>\n						<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">Lampiran E&nbsp;&nbsp;&nbsp;&nbsp; : Health, Safety, Environment(HSE) dan Contractor Safety&nbsp;Management</span></span></td>\n					</tr>\n					<tr>\n						<td style=\"text-align: justify;\" valign=\"top\">&nbsp;</td>\n						<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; System (CSMS)</span></span></td>\n					</tr>\n					<tr>\n						<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">G.</span></span></td>\n						<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">Lampiran F&nbsp;&nbsp;&nbsp;&nbsp; : Asuransi</span></span></td>\n					</tr>\n					<tr>\n						<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">H.</span></span></td>\n						<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">Lampiran G&nbsp;&nbsp; &nbsp;&nbsp;: Lain-lain.&nbsp;</span></span></td>\n					</tr>\n				</tbody>\n			</table>\n\n			<p>&nbsp;</p>\n			</td>\n		</tr>\n		<tr>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">2.</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">Pekerjaan yang harus dilaksanakan oleh KONTRAKTOR adalah&nbsp;[CONTRACT_TITLE] sebagaimana diuraikan dalam Lampiran B dan dilaksanakan sesuai jangka waktu yang diuraikan dalam Lampiran D (&ldquo;PEKERJAAN&rdquo;).</span></span></td>\n		</tr>\n		<tr>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">3.</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">Nilai PEKERJAAN yang harus dibayar oleh PERUSAHAAN adalah sebesar&nbsp;[NILAI_PEKERJAAN] belum termasuk PPN 10% (Sepuluh persen), dengan tata cara pembayaran sebagaimana diuraikan dalam Lampiran C (&ldquo;HARGA KONTRAK&rdquo;).</span></span></td>\n		</tr>\n		<tr>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">4.</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">KONTRAK ini mulai berlaku pada TANGGAL KONTRAK atau jangka waktu lain yang disepakati PARA PIHAK &nbsp;dan akan tetap berlaku sampai dengan ditandatangninya Berita Acara Serah Terima Pekerjaan (BASTP) (&ldquo;JANGKA WAKTU KONTRAK&rdquo;) kecuali diakhiri lebih awal sesuai dengan ketentuan KONTRAK ini.</span></span></td>\n		</tr>\n	</tbody>\n</table>\n\n<p><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">&nbsp;&nbsp;</span></span></p>\n\n<p><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">KONTRAK ini dibuat dalam rangkap 2 (dua), masing-masing mempunyai kekuatan hukum yang sama, dan ditandatangani di atas meterai cukup oleh PARA PIHAK.</span></span></p>\n\n<table style=\"width: 100%; border-collapse: collapse;\">\n	<tbody>\n		<tr>\n			<td style=\"width: 50%; text-align: center;\" valign=\"top\">&nbsp;</td>\n			<td style=\"text-align: center;\" valign=\"top\">&nbsp;</td>\n		</tr>\n		<tr>\n			<td style=\"width: 50%; text-align: center;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">Pihak Pertama</span></span></td>\n			<td style=\"text-align: center;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">Pihak Kedua</span></span></td>\n		</tr>\n		<tr>\n			<td style=\"text-align: center;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">[PERTAMINA_SUBSIDIARY_NAME]</span></span></td>\n			<td style=\"text-align: center;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">[VENDOR_COMPANY_NAME]</span></span></td>\n		</tr>\n		<tr>\n			<td style=\"text-align: center;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">[OFFICER_SIGNATURE]</span></span></td>\n			<td style=\"text-align: center;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">[VENDOR_SIGNATURE]</span></span></td>\n		</tr>\n		<tr>\n			<td style=\"text-align: center;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">[PERTAMINA_REPRESENTATIVE_NAME]</span></span></td>\n			<td style=\"text-align: center;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">[VENDOR_REPRESENTATIVE_NAME]</span></span></td>\n		</tr>\n		<tr>\n			<td style=\"text-align: center;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">[PERTAMINA_REPRESENTATIVE_POSITION]</span></span></td>\n			<td style=\"text-align: center;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">[VENDOR_REPRESENTATIVE_POSITION]</span></span></td>\n		</tr>\n	</tbody>\n</table>\n\n<p>&nbsp;</p>\n\n<div style=\"page-break-after: always\"><span style=\"display: none;\">&nbsp;</span></div>\n\n<p style=\"text-align: right;\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">LAMPIRAN A-1</span></span></p>\n\n<p style=\"text-align: center;\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\"><strong>PENGECUALIAN LAMPIRAN A</strong></span></span></p>\n\n<p><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\"><strong>PARA PIHAK </strong>sepakat untuk melakukan perubahan beberapa ketentuan yang tercantum dalam Lampiran A <strong>KONTRAK </strong>ini sebagai berikut :</span></span></p>\n\n<ol>\n	<li><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">Menghapus ketentuan Pasal 12 Lampiran A dan ketentuan-ketentuan yang berhubungan dengan Jaminan Pelaksanaan yang tercantum dalam Lampiran A.</span></span></li>\n	<li><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">Penghapusan ketentuan-ketentuan tentang Jaminan Pelaksanaan untuk <strong>PEKERJAAN </strong>tidak menghapus hak <strong>PERUSAHAAN </strong>untuk meminta Jaminan Masa Pemeliharaan <strong>PEKERJAAN </strong>dari <strong>KONTRAKTOR </strong>yang dibutuhkan oleh <strong>PERUSAHAAN.</strong></span></span></li>\n</ol>\n\n<p style=\"text-align: right;\"><br />\n<br />\n<span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">LAMPIRAN B</span></span></p>\n\n<p style=\"text-align: center;\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\"><strong>LINGKUP PEKERJAAN</strong></span></span></p>\n\n<p align=\"center\" style=\"margin: 0cm 0cm 10pt; text-align: center;\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\"><b>PASAL 1</b></span></span></p>\n\n<p align=\"center\" style=\"margin: 0cm 0cm 10pt; text-align: center;\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\"><b>DASAR PELAKSANAAN PEKERJAAN</b></span></span></p>\n\n<table align=\"left\" style=\"width: 100%; border-collapse: collapse;\">\n	<tbody>\n		<tr>\n			<td style=\"width: 5%; text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">1.1</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">Dasar pelaksanaan <strong>PEKERJAAN </strong>dalam <strong>KONTRAK </strong>adalah :</span></span></td>\n		</tr>\n		<tr>\n			<td style=\"text-align: justify;\" valign=\"top\">&nbsp;</td>\n			<td valign=\"top\">\n			<table align=\"left\" style=\"width: 100%; border-collapse: collapse;\">\n				<tbody>\n					<tr>\n						<td style=\"width: 5%; text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">1.</span></span></td>\n						<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">Nomor pemilihan langsung No.&nbsp;[NOMOR_LELANG]</span></span></td>\n					</tr>\n					<tr>\n						<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">2.</span></span></td>\n						<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\"><i>Request For Quotation</i> (RFQ) dari <b>PERUSAHAAN </b>kepada <b>KONTRAKTOR </b>No.&nbsp;[NOMOR_RFQ] beserta semua kelengkapannya.</span></span></td>\n					</tr>\n					<tr>\n						<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">3.</span></span></td>\n						<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">Berita Acara Penjelasan (<i>Aanwijzing</i> Kantor) No. BA.&nbsp;[NO_BA_AANWIJZING_KANTOR] tanggal [TANGGAL_BA_AANWIJZING_KANTOR].</span></span></td>\n					</tr>\n					<tr>\n						<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">4.</span></span></td>\n						<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">Berita Acara Penjelasan (<i>Aanwijzing</i> Lapangan) No. BA.&nbsp;[NO_BA_AANWIJZING_LAPANGAN] tanggal [TANGGAL_BA_AANWIJZING_LAPANGAN].</span></span></td>\n					</tr>\n					<tr>\n						<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">5.</span></span></td>\n						<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">Berita Acara Pembukaan Penawaran Sampul I No. BA.&nbsp;[NO_BA_SAMPUL_] tanggal [TANGGAL_BA_SAMPUL_I].</span></span></td>\n					</tr>\n					<tr>\n						<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">6.</span></span></td>\n						<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">Surat Penawaran dan dokumen-dokumen kelengkapannya yang diserahkan <b>KONTRAKTOR </b>kepada<b> </b><b>PERUSAHAAN </b>No. [SURAT_ADMINISTRASI_VENDOR]<b>&nbsp;</b>tanggal&nbsp;[TANGGAL_SURAT_ADMINISTRASI] Perihal Penawaran Adminsitrasi/Teknis, untuk selanjutnya disebut SURAT PENAWARAN.</span></span></td>\n					</tr>\n					<tr>\n						<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">7.</span></span></td>\n						<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">Berita Acara Negosiasi Harga (Manual) No. BA.&nbsp;[NO_BA_NEGOSIASI]&nbsp;tanggal [TANGGAL_BA_NEGOSIASI].</span></span></td>\n					</tr>\n					<tr>\n						<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">8.</span></span></td>\n						<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">Surat <b>KONTRAKTOR </b>kepada <b>PERUSAHAAN </b>No.&nbsp;[SRT_NEGOSIASI_VENDOR] tanggal&nbsp;[TANGGAL_SURAT_NEGOSIASI] perihal Penawaran Harga - Negosiasi.</span></span></td>\n					</tr>\n					<tr>\n						<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">9.</span></span></td>\n						<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">Surat &nbsp;<b>KONTRAKTOR </b>kepada<b> </b><b>&nbsp;PERUSAHAAN </b>No.&nbsp;[SURAT_KESANGGUPAN] tanggal&nbsp;[TANGGAL_SURAT_KESANGGUPAN] perihal Pernyataan Kesanggupan Melaksanakan Pekerjaan.</span></span></td>\n					</tr>\n					<tr>\n						<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">10.</span></span></td>\n						<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">Surat Keputusan Penetapan Pelaksana Pekerjaan Pemilihan Langsung dari <b>PERUSAHAAN</b> kepada <b>KONTRAKTOR</b> No. Kpts-[NO_SK_PENETAPAN] tanggal [TANGGAL_SK_PENETAPAN].</span></span></td>\n					</tr>\n					<tr>\n						<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">11.</span></span></td>\n						<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">Surat Penunjukan Pemenang Pemilihan Langsung dari <b>PERUSAHAAN</b> kepada <b>KONTRAKTOR</b> No.&nbsp;[SURAT_PENUNJUKAN_PEMENANG] tanggal [TANGGAL_SURAT_PENUNJUKAN].</span></span></td>\n					</tr>\n					<tr>\n						<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">12.</span></span></td>\n						<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">Lingkup Pekerjaan sebagaimana diuraikan lebih rinci dalam RKS / Dokumen Pengadaan, Bill of Quantity, dan Berita Acara Rapat Penjelasan.</span></span></td>\n					</tr>\n					<tr>\n						<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">13.</span></span></td>\n						<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">Semua dokumen yang terkait dengan proses pengadaan <b>PEKERJAAN</b>.</span></span></td>\n					</tr>\n					<tr>\n						<td colspan=\"2\" style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">Dokumen-dokumen tersebut diatas selanjutnya disebut &ldquo;<b>DOKUMEN PENGADAAN</b>&rdquo;</span></span></td>\n					</tr>\n				</tbody>\n			</table>\n\n			<p>&nbsp;</p>\n			</td>\n		</tr>\n		<tr>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">1.2.</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">Apabila terdapat pengertian/penafsiran yang berbeda antara <b>DOKUMEN KONTRAK </b>termasuk perubahan - perubahannya dengan <b>DOKUMEN PENGADAAN</b>, maka ketentuan dalam &nbsp;<b>DOKUMEN KONTRAK</b> yang berlaku sebagai dasar penyelesaian masalah.</span></span></td>\n		</tr>\n		<tr>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">1.3</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">Apabila terdapat pengertian/penafsiran yang berbeda antara dokumen-dokumen yang termasuk dalam <b>DOKUMEN PENGADAAN</b>, maka <b>PERUSAHAAN </b>akan menentukan dokumen mana yang berlaku.</span></span></td>\n		</tr>\n	</tbody>\n</table>\n\n<p style=\"text-align: center;\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">&nbsp;&nbsp;<b>PASAL 2</b></span></span></p>\n\n<p align=\"center\" class=\"CxSpLast\" style=\"text-align: center; margin-right: -2.15pt;\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\"><b>LINGKUP PEKERJAAN</b></span></span></p>\n\n<table align=\"left\" style=\"width: 100%; border-collapse: collapse;\">\n	<tbody>\n		<tr>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">2.1.&nbsp;&nbsp;&nbsp;</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\"><b>KONTRAKTOR</b> wajib melaksanakan [CONTRACT_TITLE] di <b>LOKASI PEKERJAAN</b> sesuai ketentuan <b>KONTRAK</b> dan <b>DOKUMEN PENGADAAN</b> (selanjutnya disebut &ldquo;<b>PEKERJAAN</b>&rdquo;) dengan lingkup <b>PEKERJAAN</b> meliputi:</span></span></td>\n		</tr>\n		<tr>\n			<td style=\"text-align: justify;\" valign=\"top\">&nbsp;</td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">[LINGKUP_PEKERJAAN].</span></span></td>\n		</tr>\n		<tr>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">2.2.</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\"><strong>LOKASI PEKERJAAN </strong>ini adalah di&nbsp;[LOKASI_PEKERJAAN].</span></span></td>\n		</tr>\n		<tr>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">2.3.</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\"><b>PARA PIHAK </b>akan menandatangani <strong>Surat Perintah Kerja (SPK) </strong>sebagai dasar pelaksanaan setiap pekerjaan berdasarkan <strong>KONTRAK </strong>ini.</span></span></td>\n		</tr>\n	</tbody>\n</table>\n\n<p style=\"text-align: center;\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\"><b>PASAL 3</b></span></span></p>\n\n<p align=\"center\" style=\"margin: 0cm 0cm 10pt; text-align: center;\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\"><b>PEMELIHARAAN HASIL PEKERJAAN</b></span></span></p>\n\n<table align=\"left\" style=\"width: 100%; border-collapse: collapse;\">\n	<tbody>\n		<tr>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">3.1.&nbsp;&nbsp;&nbsp;</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\"><b>KONTRAKTOR</b> wajib melaksanakan pemeliharaan terhadap hasil <b>PEKERJAAN</b> selama Masa Pemeliharaan sebagaimana diatur dalam Lampiran D.</span></span></td>\n		</tr>\n		<tr>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">3.2.</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">Apabila dalam Masa Pemeliharaan terjadi kerusakan / trouble / cacat / ketidaksesuaian dengan rencana kerja yang disetujui <b>PERUSAHAAN</b> yang diakibatkan oleh, namun tidak terbatas pada, kesalahan konstruksi atau kerusakan akibat operasi yang dilakukan <b>PERUSAHAAN</b>, maka <b>KONTRAKTOR</b> wajib memperbaiki hal tersebut dengan biaya <b>KONTRAKTOR</b>. Masa Pemeliharaan dapat diperpanjang sampai dengan maksimal setengah dari Masa Pemeliharaan sebagaimana diatur dalam Lampiran D dan <b>KONTRAKTOR</b> wajib memperpanjang Jaminan Pemeliharaan sebesar 5% dari <b>HARGA KONTRAK </b>sesuai dengan perpanjangan Masa Pemeliharaan tersebut.</span></span></td>\n		</tr>\n		<tr>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">3.3.</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">Dalam Masa Pemeliharaan ini, segala risiko, kerusakan, cacat, gangguan teknis atau operasional yang mungkin terjadi atau diketahui oleh <b>PERUSAHAAN</b> beserta biaya yang ditimbulkannya, menjadi beban dan tanggung jawab <b>KONTRAKTOR</b>, termasuk untuk melaksanakan penggantian, pembangunan atau pemasangan kembali Peralatan dan Material yang diperlukan dalam rangka perbaikan tersebut, kecuali hal tersebut di atas adalah akibat dari kesalahan <b>PERUSAHAAN</b>.</span></span></td>\n		</tr>\n		<tr>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">3.4.</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">Apabila <b>KONTRAKTOR</b> tidak melakukan perbaikan sebagaimana dimaksud ayat 3.2 Pasal ini dalam jangka waktu yang ditetapkan <b>PERUSAHAAN</b>, maka <b>PERUSAHAAN</b> berhak menunjuk pihak lain untuk melakukan perbaikan tersebut atas beban atau biaya <b>KONTRAKTOR</b>. Apabila setelah masa perpanjangan tersebut masih terdapat kerusakan/trouble/ketidaksesuaian, maka Jaminan Pemeliharaan menjadi milik <b>PERUSAHAAN</b> dan <b>PERUSAHAAN</b> berhak mencairkan Jaminan Pemeliharaan tersebut tanpa persetujuan terlebih dahulu <b>KONTRAKTOR.</b></span></span></td>\n		</tr>\n		<tr>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">3.5.</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">Dalam hal <b>KON</b><b>T</b><b>RAKTOR </b>tidak melaksanakan kewajibannya berdasarkan Pasal ini, maka jaminan Pemeliharaan menjadi milik <b>PERUSAHAAN</b> dan <b>PERUSAHAAN</b> berhak secara hukum untuk mencairkan surat Jaminan Pemeliharaan tersebut tanpa persetujuan terlebih dahulu dari <b>KONTRAKTOR.</b></span></span></td>\n		</tr>\n		<tr>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">3.6.</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">Setelah lewat jangka waktu Masa Pemeliharaan atau setelah selesainya perbaikan oleh <b>KONTRAKTOR</b> terhadap <b>PEKERJAAN</b> (jika ada), dan diterima/disetujui oleh <b>PERUSAHAAN</b>, PARA PIHAK akan menandatangani Berita Acara Serah Terima <b>PEKERJAAN </b>(BASTP) II</span></span></td>\n		</tr>\n	</tbody>\n</table>\n\n<p style=\"text-align: center;\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">&nbsp;&nbsp;&nbsp;<b>PASAL 4</b></span></span></p>\n\n<p align=\"center\" style=\"margin: 0cm 0cm 10pt; text-align: center;\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\"><b>PENERIMAAN ATAS KONDISI PEKERJAAN DAN LOKASI</b></span></span></p>\n\n<table align=\"left\" style=\"width: 100%; border-collapse: collapse;\">\n	<tbody>\n		<tr>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">4.1. &nbsp; &nbsp; &nbsp;&nbsp; &nbsp;</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\"><b>KONTRAKTOR</b> telah memahami dan menerima <b>PEKERJAAN </b>dengan segala resiko yang mungkin muncul dikemudian hari dalam pelaksanaan <b>PEKERJAAN</b>, perhitungan <b>HARGA KONTRAK </b>dan penentuan <b>MASA PENYELESAIAN PEKERJAAN </b>sebagaimana dituangkan dalam Jadwal Pelaksanaan, termasuk mutu dan ketersediaan <b>PERALATAN, MATERIAL </b>dan tenaga kerja.</span></span></td>\n		</tr>\n		<tr>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">4.2.&nbsp;</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\"><b>KONTRAKTOR</b> telah mengetahui, memeriksa secara seksama, memahami dan menerima kondisi <b>LOKASI PEKERJAAN </b>dan telah memperhitungkan segala resiko yang ada dan akan ada, termasuk tetapi tidak terbatas pada operasional di <b>LOKASI PEKERJAAN</b>, pengangkutan laut dan darat, akses ke <b>LOKASI PEKERJAAN </b>termasuk ketersediaan kondisi jalan, penanganan dan penyimpanan bahan-bahan, air, pasir, batu, curah hujan, pasang surut air laut, arus, perkiraan kedalaman air tanah, topografi, kondisi laut, dan pelabuhan.</span></span></td>\n		</tr>\n		<tr>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">4.3.</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\"><b>KONTRAKTOR</b> mengakui bahwa hal-hal dan resiko sebagaimana dimaksudkan dalam ayat 4.1 dan 4.2 Pasal ini telah diperhitungkan dalam <b>HARGA KONTRAK </b>dan penentuan <b>MASA PENYELESAIAN PEKERJAAN</b>. Sehubungan dengan hal tersebut, <b>KONTRAKTOR </b>tidak akan atau tidak berhak untuk mengajukan klaim kenaikan terhadap <b>HARGA KONTRAK </b>atau <b>MASA PENYELESAIAN PEKERJAAN</b> baik secara keseluruhan atau sebagian atas perbedaan antara keadaan sebenarnya yang dihadapi <b>KONTRAKTOR</b> dengan yang diperkirakan sebelumnya oleh <b>KONTRAKTOR.</b></span></span></td>\n		</tr>\n		<tr>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">4.4.</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">Apabila selama pelaksanaan <b>PEKERJAAN</b>, <b>KONTRAKTOR</b> menghadapi secara tak terduga adanya suatu kondisi fisik atau rintangan buatan dibawah tanah di <b>LOKASI PEKERJAAN</b> yang secara wajar tidak dapat diramalkan atau diduga oleh kontraktor yang berpengalaman pada saat penandatanganan <b>KONTRAK</b>, maka <b>PARA PIHAK</b> sepakat untuk membicarakan kembali hal-hal yang berkaitan dengan Lingkup <b>PEKERJAAN.</b></span></span></td>\n		</tr>\n	</tbody>\n</table>\n\n<p style=\"text-align: center;\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">&nbsp;&nbsp;&nbsp;<b>PASAL 5</b></span></span></p>\n\n<p align=\"center\" style=\"margin: 0cm 0cm 10pt; text-align: center;\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\"><b>PEKERJAAN TAMBAH/KURANG</b></span></span></p>\n\n<table align=\"left\" style=\"width: 100%; border-collapse: collapse;\">\n	<tbody>\n		<tr>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">5.1.</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">Apabila terjadi penambahan atau pengurangan pekerjaan di luar Lingkup <b>PEKERJAAN </b>sebagaimana Pasal 2 Lampiran B ini, maka penambahan atau pengurangan Pekerjaan tersebut akan dituangkan dalam suatu Addendum yang merupakan satu kesatuan yang tidak terpisahkan dari <b>KONTRAK</b>.</span></span></td>\n		</tr>\n		<tr>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">5.2.</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">Setiap perubahan / penambahan atas Lingkup <b>PEKERJAAN </b>harus didahului dengan persetujuan tertulis dari <b>PERUSAHAAN</b></span></span></td>\n		</tr>\n		<tr>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">5.3.</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">Apabila permintaan perubahan tersebut berasal dari <b>PERUSAHAAN</b> maka <b>PERUSAHAAN</b> akan memberitahukan secara tertulis kepada <b>KONTRAKTOR</b> dan <b>KONTRAKTOR</b> wajib memberikan konfirmasi kesanggupan atau ketidaksanggupan secara tertulis dalam waktu 2 (dua) <b>HARI KERJA </b>sejak tanggal surat <b>PERUSAHAAN</b> tersebut. Apabila <b>KONTRAKTOR</b> tidak sanggup maka PARA PIHAK sepakat untuk mengakhiri <b>KONTRAK,</b> kemudian akan diadakan perhitungan oleh <b>PERUSAHAAN</b> mengenai prestasi <b>KONTRAKTOR</b> dan pembayaran-pembayaran sebelumnya, serta seluruh hasil <b>PEKERJAAN</b> otomatis menjadi milik <b>PERUSAHAAN</b>.</span></span></td>\n		</tr>\n		<tr>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">5.4.</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">Apabila permintaan perubahan tersebut berasal dari <b>KONTRAKTOR</b> maka <b>KONTRAKTOR</b> wajib mengajukan secara tertulis beserta data dan/atau desain ulang kepada <b>PERUSAHAAN</b> pada kesempatan pertama dan keputusan ada pada <b>PERUSAHAAN</b> yang akan disampaikan secara tertulis dalam waktu 2 (dua) <b>HARI KERJA </b>setelah <b>PERUSAHAAN</b> menerima surat <b>KONTRAKTOR</b>. Apabila <b>PERUSAHAAN</b> tidak menyetujui usul perubahan tersebut maka <b>KONTRAKTOR</b> menyelesaikan <b>PEKERJAAN </b>sesuai yang diatur dalam <b>PERJANJIAN </b>ini dan <b>DOKUMEN PENGADAAN</b>.</span></span></td>\n		</tr>\n		<tr>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">5.5.</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">Kondisi dan syarat-syarat untuk penambahan Pekerjaan harus memperhatikan dan memenuhi ketentuan peraturan yang berlaku di lingkungan <b>PERUSAHAAN</b>.</span></span></td>\n		</tr>\n		<tr>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">5.6.</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">Bilamana terdapat kerja tambah/kurang yang telah disetujui oleh <b>PERUSAHAAN</b> dan mengakibatkan perubahan <b>HARGA KONTRAK</b>, maka akan diperhitungkan dan dibayarkan oleh <b>PERUSAHAAN</b> setelah adanya BASTP I dengan disertai bukti-bukti yang disetujui oleh <b>PERUSAHAAN</b>.</span></span></td>\n		</tr>\n	</tbody>\n</table>\n\n<p style=\"text-align: center;\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">&nbsp;&nbsp;&nbsp;<b>PASAL 6</b></span></span></p>\n\n<p align=\"center\" style=\"margin: 0cm 0cm 10pt; text-align: center;\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\"><b>PERALATAN DAN MATERIAL</b></span></span></p>\n\n<table align=\"left\" style=\"width: 100%; border-collapse: collapse;\">\n	<tbody>\n		<tr>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">6.1.&nbsp; &nbsp;&nbsp;</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\"><b>KONTRAKTOR </b>wajib menyediakan dan mengadakan <b>PERALATAN</b> dan <b>MATERIAL </b>sesuai dengan <b>DOKUMEN PENGADAAN</b>, spesifikasi, mutu/kualitas, persyaratan teknis atau persyaratan lain untuk pelaksanaan <b>PEKERJAAN</b>.</span></span></td>\n		</tr>\n		<tr>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">6.2.</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\"><b>PERUSAHAAN </b>akan memeriksa seluruh <b>PERALATAN</b> dan <b>MATERIAL </b>yang akan digunakan oleh <b>KONTRAKTOR </b>untuk pelaksanaan <b>PEKERJAAN. </b>Apabila <b>PERALATAN</b> dan <b>MATERIAL </b>tersebut telah sesuai dengan spesifikasi dan layak untuk pelaksanaan <b>PEKERJAAN </b>sebagaimana dimaksud ayat 6.1 Pasal ini, maka akan dibuatkan Berita Acara Pemeriksaan yang ditandatangani oleh <b>PARA PIHAK</b>.</span></span></td>\n		</tr>\n		<tr>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">6.3.</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">Apabila <b>PERALATAN</b> dan/atau <b>MATERIAL </b>yang dipergunakan untuk <b>PEKERJAAN </b>diharuskan atau diperlukan persyaratan sertifikasi teknis, jaminan kualitas, garansi atau jaminan vendor/supplier, test/pengujian khusus, atau sertifikasi lain yang ditentukan <b>PERUSAHAAN </b>atau berdasarkan suatu peraturan, maka <b>KONTRAKTOR </b>atas beban dan biaya <b>KONTRAKTOR</b> wajib untuk memenuhi persyaratan tersebut.</span></span></td>\n		</tr>\n		<tr>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">6.4.</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">Apabila diperlukan penambahan <b>PERALATAN</b> dan/atau <b>MATERIAL </b>akibat kesalahan <b>KONTRAKTOR</b> yang mengakibatkan adanya perubahan lingkup <b>PEKERJAAN </b>untuk penyelesaian seluruh <b>PEKERJAAN</b>, maka hal tersebut akan menjadi beban dan tanggung jawab <b>KONTRAKTOR</b> tanpa tambahan biaya dari <b>PERUSAHAAN</b>.</span></span></td>\n		</tr>\n		<tr>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">6.5.</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">Apabila dalam suatu keadaan atau karena alasan tertentu yang bersifat sangat teknis dalam pelaksanaan <b>PEKERJAAN </b>mengakibatkan terjadinya perubahan, penambahan atau pengurangan <b>PERALATAN</b> dan/atau <b>MATERIAL</b>, maka perubahan, penambahan atau pengurangan <b>PERALATAN</b> dan/atau <b>MATERIAL </b>tersebut harus mendapatkan persetujuan tertulis <b>PERUSAHAAN</b>.</span></span></td>\n		</tr>\n		<tr>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">6.6.&nbsp;&nbsp;</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\"><b>KONTRAKTOR</b> dalam pelaksanaan <b>PEKERJAAN</b> wajib untuk secara maksimal mengutamakan penggunaan <b>PERALATAN</b> dan <b>MATERIAL </b>produksi dalam negeri sesuai standar atau spesifikasi yang ditentukan sesuai peraturan yang berlaku, kecuali ditentukan lain dan diperlukan spesifikasi khusus</span></span></td>\n		</tr>\n		<tr>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">6.7.</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\"><b>KONTRAKTOR</b> wajib menyerahterimakan kepemilikan atas <b>MATERIAL </b>yang diadakan oleh <b>KONTRAKTOR</b> untuk pelaksanaan <b>PEKERJAAN</b> kepada <b>PERUSAHAAN</b> setelah sampai di <b>LOKASI PEKERJAAN.</b> Namun selanjutnya <b>KONTRAKTOR</b> tetap bertanggung jawab penuh dan atas bebannya sendiri untuk perawatan, keamanan dan penjagaan terhadap <b>MATERIAL </b>tersebut sampai dengan <b>TANGGAL PENYERAHAN PEKERJAAN</b>, sehingga kehilangan, kerusakan, penurunan mutu/kualitas dari <b>MATERIAL </b>tersebut merupakan tanggung jawab <b>KONTRAKTOR</b></span></span></td>\n		</tr>\n	</tbody>\n</table>\n\n<p style=\"text-align: center;\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">&nbsp;&nbsp;&nbsp;<b>PASAL 7</b></span></span></p>\n\n<p align=\"center\" style=\"margin: 0cm 0cm 10pt; text-align: center;\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\"><b>METODE PELAKSANAAN PEKERJAAN</b></span></span></p>\n\n<table align=\"left\" style=\"width: 100%; border-collapse: collapse;\">\n	<tbody>\n		<tr>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">7.1.</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\"><b>KONTRAKTOR</b> wajib melaksanakan <b>PEKERJAAN</b> ini sesuai dengan revisi-revisi terakhir dan penemuan-penemuan terbaru atau cara-cara pelaksanaan praktis yang sesuai dengan engineering dan desain serta Jadwal Pelaksanaan.</span></span></td>\n		</tr>\n		<tr>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">7.2.</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">Setiap saat <b>KONTRAKTOR</b> harus menjaga kebersihan, menjaga jangan sampai ada penimbunan bahan-bahan atau material-material yang sudah tak terpakai atau kotoran termasuk didalam bangunan-bangunan sesuai dengan syarat-syarat pelaksanaan kerja yang baik.</span></span></td>\n		</tr>\n		<tr>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">7.3.</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\"><b>KONTRAKTOR</b> harus menjaga dan melindungi kondisi <b>LOKASI PEKERJAAN</b>, termasuk semua fasilitas-fasilitas yang sudah ada ditempat atau didekat <b>LOKASI PEKERJAAN, </b>semua tanaman, seperti pohon-pohon, semak-semak, rumput-rumput, agar tetap dalam keadaan baik. <b>KONTRAKTOR</b>, atas biayanya sendiri, wajib memperbaiki jika terjadi kerusakan-kerusakan akibat kesalahan atau kelalaian <b>KONTRAKTOR</b>.</span></span></td>\n		</tr>\n		<tr>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">7.4.</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\"><b>KONTRAKTOR</b> wajib melakukan tindakan-tindakan untuk mencegah terjadinya pencemaran lingkungan, termasuk laut, air, udara dan tanah, yang timbul akibat pelaksanaan <b>PEKERJAAN</b>. <b>KONTRAKTOR</b> bertanggung jawab penuh dan atas bebannya sendiri atas pencemaran lingkungan yang terjadi akibat pelaksanaan <b>PEKERJAAN </b>termasuk penanganannya.</span></span></td>\n		</tr>\n		<tr>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">7.5.</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\"><b>KONTRAKTOR</b> harus menggunakan peralatan-peralatan keselamatan kerja, melaksanakan peraturan-peraturan keselamatan kerja, dan melakukan semua tanggung jawabnya sesuai dengan ketentuan-ketentuan umum Industri, Konstruksi International dan dengan cara-cara yang tidak membahayakan kesehatan, jiwa manusia, serta mencegah terjadinya kerusakan-kerusakan harta benda</span></span></td>\n		</tr>\n	</tbody>\n</table>\n\n<p style=\"text-align: center;\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">&nbsp;&nbsp;&nbsp;<b>PASAL 8</b></span></span></p>\n\n<p align=\"center\" style=\"margin: 0cm 0cm 10pt; text-align: center;\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\"><b>FORMALITAS DAN PERIJINAN</b></span></span></p>\n\n<table align=\"left\" style=\"width: 100%; border-collapse: collapse;\">\n	<tbody>\n		<tr>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">8.1.&nbsp; &nbsp;&nbsp; &nbsp;&nbsp;</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">Apabila dalam rangka penyelesaian <b>PEKERJAAN, </b>diperlukan uji coba, test/pengujian khusus, sertifikasi, pengesahan atau formalitas dari instansi yang berwenang, maka <b>KONTRAKTOR</b> atas beban dan biaya <b>KONTRAKTOR</b> sendiri wajib untuk memenuhi persyaratan tersebut.</span></span></td>\n		</tr>\n		<tr>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">8.2.</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">Apabila didalam pelaksanaan <b>PEKERJAAN,</b> diperlukan perijinan dari Instansi Pemerintah, maka <b>PERUSAHAAN</b> akan menyelesaikannya dan segala biaya yang timbul untuk perijinan diatas menjadi beban <b>PERUSAHAAN</b>. Dalam hal ini, <b>KONTRAKTOR</b> wajib membantu <b>PERUSAHAAN</b> dalam pengurusan perijinan tersebut.</span></span></td>\n		</tr>\n		<tr>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">8.3.</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\"><b>KONTRAKTOR</b> harus mendapatkan dan menyimpan semua surat-surat registrasi, lisensi-lisensi dan perijinan dimana hal tersebut diperlukan menurut hukum yang berlaku, dan <b>KONTRAKTOR</b> wajib menyerahkan kepada <b>PERUSAHAAN</b> jika diminta / dibutuhkan</span></span></td>\n		</tr>\n	</tbody>\n</table>\n\n<p style=\"margin: 0cm 0cm 10pt; text-align: right;\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">&nbsp;<br />\n<br />\nLAMPIRAN C</span></span></p>\n\n<p align=\"center\" style=\"margin: 0cm 0cm 10pt; text-align: center;\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\"><b>HARGA KONTRAK DAN KETENTUAN PEMBAYARAN</b></span></span></p>\n\n<p align=\"center\" style=\"margin: 0cm 0cm 10pt; text-align: center;\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\"><b>PASAL 1</b></span></span></p>\n\n<p align=\"center\" style=\"margin: 0cm 0cm 10pt; text-align: center;\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\"><b>HARGA KONTRAK DAN HARGA SEWA</b></span></span></p>\n\n<table align=\"left\" style=\"width: 100%; border-collapse: collapse;\">\n	<tbody>\n		<tr>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">1.1.&nbsp; &nbsp;&nbsp; &nbsp;&nbsp;</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\"><strong>HARGA KONTRAK </strong>atas pelaksanaan seluruh <strong>PEKERJAAN </strong>ini adalah sebesar [NILAI_PEKERJAAN] belum termasuk PPN 10%.</span></span></td>\n		</tr>\n		<tr>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">1.2.</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\"><strong>HARGA KONTRAK </strong>tersebut dalam ayat 1.1 Pasal ini telah termasuk pajak-pajak, perijinan-perijinan di luar perijinan dari ionstansi pemerintah dan pembayaran-pembayaran yang merupakan kewajiban <strong>KONTRAKTOR </strong>kecuali PPN.</span></span></td>\n		</tr>\n		<tr>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">1.3.</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\"><b>HARGA KONTRAK </b>tersebut dalam ayat 1.1 Pasal ini adalah tetap selama <strong>JANGKA WAKTU KONTRAK </strong>dan <strong>KONTRAKTOR </strong>tidak berhak untuk menuntut kenaikan <strong>HARGA KONTRAK </strong>tersebut dalam kondisi apapun.</span></span></td>\n		</tr>\n	</tbody>\n</table>\n\n<p style=\"text-align: center;\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">&nbsp;&nbsp;<b>PASAL 2</b></span></span></p>\n\n<p align=\"center\" style=\"margin: 0cm 0cm 10pt; text-align: center;\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\"><b>TATA CARA PEMBAYARAN</b></span></span></p>\n\n<table align=\"left\" style=\"width: 100%; border-collapse: collapse;\">\n	<tbody>\n		<tr>\n			<td style=\"width: 6.5%;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">2.1.</span></span></td>\n			<td valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">Pembayaran <strong>HARGA KONTRAK </strong>sebagaimana tercantum pada Pasal 1 Lampiran ini akan dilaksanakan oleh <strong>PERUSAHAAN </strong>kepada <strong>KONTRAKTOR </strong>dengan ketentuan dan syarat-syarat sebagai berikut :</span></span></td>\n		</tr>\n		<tr>\n			<td style=\"width: 5%; text-align: justify;\" valign=\"top\">&nbsp;</td>\n			<td style=\"text-align: justify;\" valign=\"top\">\n			<table align=\"left\" style=\"width: 100%; border-collapse: collapse;\">\n				<tbody>\n					<tr>\n						<td style=\"width: 6.5%;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">a.</span></span></td>\n						<td valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">Penagihan sekaligus:</span></span></td>\n					</tr>\n				</tbody>\n			</table>\n			</td>\n		</tr>\n		<tr>\n			<td style=\"width: 5%; text-align: justify;\" valign=\"top\">&nbsp;</td>\n			<td style=\"text-align: justify;\" valign=\"top\">\n			<table align=\"left\" style=\"width: 100%; border-collapse: collapse;\">\n				<tbody>\n					<tr>\n						<td style=\"width: 6.5%;\" valign=\"top\">&nbsp;</td>\n						<td valign=\"top\">\n						<table align=\"left\" style=\"width: 100%; border-collapse: collapse;\">\n							<tbody>\n								<tr>\n									<td style=\"width: 6.5%;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">1)</span></span></td>\n									<td valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\"><strong>KONTRAKTOR </strong>dapat menagih pembayaran 100% (Seratus persen) dari <strong>HARGA KONTRAK </strong>dengan ketentuan bahwa <strong>PEKERJAAN </strong>telah dinyatakan selesai 100% (Seratus persen) sesuai Laporan Kemajuan <strong>PEKERJAAN </strong>dan BASTP I yang telah disetujui Pengawas Lapangan dan/atau Direksi <strong>PEKERJAAN </strong>berdasarkan Jadwal Pelaksanaan yang telah disetujui oleh <strong>PARA PIHAK </strong>serta telah diserahkan kepada <strong>PERUSAHAAN</strong></span></span></td>\n								</tr>\n							</tbody>\n						</table>\n						</td>\n					</tr>\n				</tbody>\n			</table>\n			</td>\n		</tr>\n		<tr>\n			<td style=\"width: 5%; text-align: justify;\" valign=\"top\">&nbsp;</td>\n			<td style=\"text-align: justify;\" valign=\"top\">\n			<table align=\"left\" style=\"width: 100%; border-collapse: collapse;\">\n				<tbody>\n					<tr>\n						<td style=\"width: 6.5%;\" valign=\"top\">&nbsp;</td>\n						<td valign=\"top\">\n						<table align=\"left\" style=\"width: 100%; border-collapse: collapse;\">\n							<tbody>\n								<tr>\n									<td style=\"width: 6.5%;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">2)</span></span></td>\n									<td valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">Dalam hal ini penagihan pembayaran seperti dimaksud pada Pasal 2.1.a (1) di atas, <strong>KONTRAKTOR </strong>wajib menyerahkan Jaminan Pemeliharaan sebesar 5% (Lima persen) dari <strong>HARGA KONTRAK</strong>. Apabila <strong>KONTRAKTOR </strong>tidak menyerahkan Jaminan Pemeliharaan, <strong>PERUSAHAAN </strong>dapat melakukan retensi pembayaran sebesar 5% (Lima persen) dari <strong>HARGA KONTRAK</strong></span></span></td>\n								</tr>\n							</tbody>\n						</table>\n						</td>\n					</tr>\n				</tbody>\n			</table>\n			</td>\n		</tr>\n		<tr>\n			<td style=\"width: 5%; text-align: justify;\" valign=\"top\">&nbsp;</td>\n			<td style=\"text-align: justify;\" valign=\"top\">\n			<table align=\"left\" style=\"width: 100%; border-collapse: collapse;\">\n				<tbody>\n					<tr>\n						<td style=\"width: 6.5%;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">b.</span></span></td>\n						<td valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">Penagihan dengan termin:</span></span></td>\n					</tr>\n				</tbody>\n			</table>\n			</td>\n		</tr>\n		<tr>\n			<td style=\"width: 5%; text-align: justify;\" valign=\"top\">&nbsp;</td>\n			<td style=\"text-align: justify;\" valign=\"top\">\n			<table align=\"left\" style=\"width: 100%; border-collapse: collapse;\">\n				<tbody>\n					<tr>\n						<td style=\"width: 6.5%;\" valign=\"top\">&nbsp;</td>\n						<td valign=\"top\">\n						<table align=\"left\" style=\"width: 100%; border-collapse: collapse;\">\n							<tbody>\n								<tr>\n									<td style=\"width: 6.5%;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">1)</span></span></td>\n									<td valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">Penagihan dalam termin yang dapat dilakukan oleh <strong>KONTRAKTOR </strong>selama <strong>JANGKA WAKTU </strong><strong>KONTRK </strong>adalah sebanyak-banyuak dalam 4 (Empat) termin</span></span></td>\n								</tr>\n							</tbody>\n						</table>\n						</td>\n					</tr>\n				</tbody>\n			</table>\n			</td>\n		</tr>\n		<tr>\n			<td style=\"width: 5%; text-align: justify;\" valign=\"top\">&nbsp;</td>\n			<td style=\"text-align: justify;\" valign=\"top\">\n			<table align=\"left\" style=\"width: 100%; border-collapse: collapse;\">\n				<tbody>\n					<tr>\n						<td style=\"width: 6.5%;\" valign=\"top\">&nbsp;</td>\n						<td valign=\"top\">\n						<table align=\"left\" style=\"width: 100%; border-collapse: collapse;\">\n							<tbody>\n								<tr>\n									<td style=\"width: 6.5%;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">2)</span></span></td>\n									<td valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">Penaguhan termin pertama sampai dengan termin kedua/ketiga oleh <strong>KONTRAKTOR </strong>dibayarkan sesuai dengan prosentase kemajuan <strong>PEKERJAAN </strong>yang telah dicapai oleh <strong>KONTRAKTOR </strong>sesuai Berita Acara Laporan Kemajuan <strong>PEKERJAAN </strong>yang disetujui Penawas Lapangan dan/atau Direksi <strong>PEKERJAAN </strong>dengan batasan / besaran penagihan sesuai dengan Jadwal Pelaksanaan yang telah disetujui oleh <strong>PARA PIHAK</strong>. Akumulasi pembayaran termin pertama sampai dengan termin kedua / ketiga adalah maksimal sebesar 95% (Sembilan puluh lima persen) dari <strong>HARGA KONTRAK</strong></span></span></td>\n								</tr>\n							</tbody>\n						</table>\n						</td>\n					</tr>\n				</tbody>\n			</table>\n			</td>\n		</tr>\n		<tr>\n			<td style=\"width: 5%; text-align: justify;\" valign=\"top\">&nbsp;</td>\n			<td style=\"text-align: justify;\" valign=\"top\">\n			<table align=\"left\" style=\"width: 100%; border-collapse: collapse;\">\n				<tbody>\n					<tr>\n						<td style=\"width: 6.5%;\" valign=\"top\">&nbsp;</td>\n						<td valign=\"top\">\n						<table align=\"left\" style=\"width: 100%; border-collapse: collapse;\">\n							<tbody>\n								<tr>\n									<td style=\"width: 6.5%;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">3)</span></span></td>\n									<td valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\"><strong>KONTRAKTOR </strong>dapat menagih pembayaran pelunasan sisa termin sebesar 100% (Seratus persen) dari <strong>HARGA KONTRAK </strong>setelah dikurangi dengan pembayaran yang telah dilakukan oleh <strong>PERUSAHAAN </strong>pada termin sebelumnya dengan ketentuan <strong>PEKERJAAN </strong>telah dinyatakan selesai 100% (Seratus persen) sesuai Laporan Kemajuan <strong>PEKERJAAN </strong>dan BASTP I yang telah disetujui Pengawas Lapangan dan/atau Direksi <strong>PEKERJAAN </strong>berdasarkan Jadwal Pelaksanaan yang telah disetujui oleh <strong>PARA PIHAK </strong>serta telah diserahkan kepada <strong>PERUSAHAAN</strong></span></span></td>\n								</tr>\n							</tbody>\n						</table>\n						</td>\n					</tr>\n				</tbody>\n			</table>\n			</td>\n		</tr>\n		<tr>\n			<td style=\"width: 5%; text-align: justify;\" valign=\"top\">&nbsp;</td>\n			<td style=\"text-align: justify;\" valign=\"top\">\n			<table align=\"left\" style=\"width: 100%; border-collapse: collapse;\">\n				<tbody>\n					<tr>\n						<td style=\"width: 6.5%;\" valign=\"top\">&nbsp;</td>\n						<td valign=\"top\">\n						<table align=\"left\" style=\"width: 100%; border-collapse: collapse;\">\n							<tbody>\n								<tr>\n									<td style=\"width: 6.5%;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">4)</span></span></td>\n									<td valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">Dalam hal penagihan pelunasan pembayaran termin seperti dimaksud pada Pasal 2.1..b.(3) di atas, <strong>KONTRAKTOR </strong>wajib menyerahkan Jaminan Pemeliharaan sebesar 5% (Lima persen) dari <strong>HARGA KONTRAK</strong>. Apabila <strong>KONTRAKTOR </strong>tidak menyerahkan Jaminan Pemeliharaan, <strong>PERUSAHAAN </strong>dapat melakukan retensi pembayaran sebesar 5% (Lima persen) dari <strong>HARGA KONTRAK</strong></span></span></td>\n								</tr>\n							</tbody>\n						</table>\n						</td>\n					</tr>\n				</tbody>\n			</table>\n			</td>\n		</tr>\n		<tr>\n			<td style=\"width: 5%; text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">2.2.</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\"><strong>PERUSAHAAN </strong>akan melakukan pembayaran sebagaimana tersebut pada ayat 2.1 Pasal ini, apabila <strong>KONTRAKTOR </strong>telah melampirkan dokumen yang merupakan dokumen-dokumen sebagai berikut :</span></span></td>\n		</tr>\n		<tr>\n			<td style=\"text-align: justify;\" valign=\"top\">&nbsp;</td>\n			<td valign=\"top\">\n			<table align=\"left\" style=\"width: 100%; border-collapse: collapse;\">\n				<tbody>\n					<tr>\n						<td style=\"width: 6.5%;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">a.</span></span></td>\n						<td valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">Purchase Order</span></span></td>\n					</tr>\n				</tbody>\n			</table>\n			</td>\n		</tr>\n		<tr>\n			<td style=\"text-align: justify;\" valign=\"top\">&nbsp;</td>\n			<td valign=\"top\">\n			<table align=\"left\" style=\"width: 100%; border-collapse: collapse;\">\n				<tbody>\n					<tr>\n						<td style=\"width: 6.5%;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">b.</span></span></td>\n						<td valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">Asli Tagihan / Invoice</span></span></td>\n					</tr>\n				</tbody>\n			</table>\n			</td>\n		</tr>\n		<tr>\n			<td style=\"text-align: justify;\" valign=\"top\">&nbsp;</td>\n			<td valign=\"top\">\n			<table align=\"left\" style=\"width: 100%; border-collapse: collapse;\">\n				<tbody>\n					<tr>\n						<td style=\"width: 6.5%;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">c.</span></span></td>\n						<td valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">Asli Kwitansi Bermaterai Cukup</span></span></td>\n					</tr>\n				</tbody>\n			</table>\n			</td>\n		</tr>\n		<tr>\n			<td style=\"text-align: justify;\" valign=\"top\">&nbsp;</td>\n			<td valign=\"top\">\n			<table align=\"left\" style=\"width: 100%; border-collapse: collapse;\">\n				<tbody>\n					<tr>\n						<td style=\"width: 6.5%;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">d.</span></span></td>\n						<td valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">Asli Faktur Pajak Standar</span></span></td>\n					</tr>\n				</tbody>\n			</table>\n			</td>\n		</tr>\n		<tr>\n			<td style=\"text-align: justify;\" valign=\"top\">&nbsp;</td>\n			<td valign=\"top\">\n			<table align=\"left\" style=\"width: 100%; border-collapse: collapse;\">\n				<tbody>\n					<tr>\n						<td style=\"width: 6.5%;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">e.</span></span></td>\n						<td valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">Asli <em>Service Acceptance </em>(SA) yang telah ditandatangani <strong>PARA PIHAK</strong>.</span></span></td>\n					</tr>\n				</tbody>\n			</table>\n			</td>\n		</tr>\n		<tr>\n			<td style=\"text-align: justify;\" valign=\"top\">&nbsp;</td>\n			<td valign=\"top\">\n			<table align=\"left\" style=\"width: 100%; border-collapse: collapse;\">\n				<tbody>\n					<tr>\n						<td style=\"width: 6.5%;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">f.</span></span></td>\n						<td valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\"><em>Copy </em><strong>KONTRAK</strong></span></span></td>\n					</tr>\n				</tbody>\n			</table>\n			</td>\n		</tr>\n		<tr>\n			<td style=\"text-align: justify;\" valign=\"top\">&nbsp;</td>\n			<td valign=\"top\">\n			<table align=\"left\" style=\"width: 100%; border-collapse: collapse;\">\n				<tbody>\n					<tr>\n						<td style=\"width: 6.5%;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">g.</span></span></td>\n						<td valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">Asli <em>Laboratory Test Report</em></span></span></td>\n					</tr>\n				</tbody>\n			</table>\n			</td>\n		</tr>\n		<tr>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">2.3.</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">Jangka waktu pembayaran dilaksanakan 30 (tiga puluh) hari kalender sejak dokumen-dokumen sesuai ketentuan ayat 2.1 Pasal ini diterima secara lengkap dan benar oleh Fungsi Finance <strong>PERUSAHAAN </strong>dari <strong>KONTRAKTOR</strong>. Apabila jatuh tempo pembayaran pada hari Sabtu, Minggu atau hari libur nasional maka pembayaran akan dilaksanakan pada hari kerja berikutnya.</span></span></td>\n		</tr>\n		<tr>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">2.4.</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">Pembayaran <strong>HARGA KONTRAK </strong>akan dilaksanakan oleh <strong>PERUSAHAAN </strong>kepada <strong>KONTRAKTOR </strong>melalui transfer ke rekening sebagai berikut :</span></span></td>\n		</tr>\n		<tr>\n			<td style=\"text-align: justify;\" valign=\"top\">&nbsp;</td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">(No Rekening Kontraktor)</span></span></td>\n		</tr>\n		<tr>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">2.5.</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">Apabila terjadi perubahan nomor rekening bank <strong>KONTRAKTOR </strong>sebagaimana disebutkan dalam ayat 2.4 Pasal ini, maka <strong>KONTRAKTOR </strong>akan memberitahukan kepada <strong>PERUSAHAAN </strong>secara tertulis dilengkapi dokumen pendukungnya, tanpa dibuat Addendum <strong>KONTRAK </strong>ini dan menjadi lampiran <strong>KONTRAK</strong> yang mengikat <strong>PARA PIHAK</strong>. <strong>KONTRAKTOR </strong>bertanggung jawab penuh terhadap segala akibat hukum yang timbul berkaitan dengan perubahan nomor rekening bank <strong>KONTRAKTOR </strong>tersebut.<strong> </strong></span></span></td>\n		</tr>\n		<tr>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">2.6.</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">Pembayaran <strong>HARGA KONTRAK </strong>sebagaimana dimaksud Pasal ini akan diperhitungkan dengan denda yang dikenakan kepada <strong>KONTRAKTOR </strong>sebagaimana dimaksud pada Lampiran ini.</span></span></td>\n		</tr>\n	</tbody>\n</table>\n\n<p style=\"margin: 0cm 0cm 10pt;\">&nbsp;</p>\n\n<p align=\"center\" style=\"margin: 0cm 0cm 10pt; text-align: center;\">&nbsp;</p>\n\n<p align=\"center\" style=\"margin: 0cm 0cm 10pt; text-align: center;\">&nbsp;</p>\n\n<p><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">&nbsp;&nbsp;<strong>PASAL 3</strong></span></span></p>\n\n<p>&nbsp;</p>\n\n<p align=\"center\" style=\"margin: 0cm 0cm 10pt; text-align: center;\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\"><strong>KETENTUAN DENDA / SANKSI</strong></span></span></p>\n\n<table align=\"left\" style=\"width: 100%; border-collapse: collapse;\">\n	<tbody>\n		<tr>\n			<td style=\"width: 5%; text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">3.1.</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">Apabila <strong>KONTRAKTOR </strong>tidak dapat menyelesaikan <strong>PEKERJAAN </strong>sesuai dengan <strong>MASA </strong><strong>PENYELESAIN PEKERJAAN</strong>, maka untuk setiap hari keterlambatan, <strong>KONTRAKTOR </strong>akan dikenakan denda keterlambatan sebesar 1<sup>o</sup>/<sub>oo</sub> (satu permil) per hari dari seluruh <strong>HARGA KONTRAK </strong>dengan nilai maksimum denda adalah sebesar 5% (lima persen) dari seluruh <b>HARGA KONTRAK</b>.</span></span></td>\n		</tr>\n		<tr>\n			<td style=\"width: 5%; text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">3.2.</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">Apabila denda sebagaimana ayat 4.1 Pasal ini telah mencapai nilai maksimum, <strong>PERUSAHAAN </strong>secara sepihak berhak untuk memutus <strong>KONTRAK </strong>ini.</span></span></td>\n		</tr>\n		<tr>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">3.3.</span></span></td>\n			<td valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">Apabila terjadi keterlambatan kemajuan pelaksanaan <strong>PEKERJAAN </strong>dari Jadwal Pelaksanaan sebagai berikut :</span></span></td>\n		</tr>\n		<tr>\n			<td style=\"text-align: justify;\" valign=\"top\">&nbsp;</td>\n			<td valign=\"top\">\n			<table align=\"left\" style=\"width: 100%; border-collapse: collapse;\">\n				<tbody>\n					<tr>\n						<td style=\"width: 5%; text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">a.</span></span></td>\n						<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">20 % (Dua puluh persen) atau lebih pada rencana pelaksanaan dibawah 70% (tujuh puluh persen); atau</span></span></td>\n					</tr>\n					<tr>\n						<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">b.</span></span></td>\n						<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">10 % (Sepuluh persen) atau lebih pada rencana pelaksanaan di atas 70% (Tujuh puluh persen)</span></span></td>\n					</tr>\n					<tr>\n						<td colspan=\"2\" style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">maka <strong>KONTRAKTOR </strong>akan diberikan Surat Peringatan I (pertama) oleh <strong>PERUSAHAAN</strong>. Apabila <strong>KONTRAKTOR </strong>tidak melakukan langkah-langkah perbaikan yang memadai sehingga diperkirakan <strong>PEKERJAAN </strong>akan mengalami keterlambatan melewati 15 (Lima belas) hari kalender (barlarut-larut), maka <strong>KONTRAKTOR </strong>akan diberikan Surat Peringatan II (dua) oleh <strong>PERUSAHAAN</strong>.</span></span></td>\n					</tr>\n				</tbody>\n			</table>\n\n			<p>&nbsp;</p>\n			</td>\n		</tr>\n		<tr>\n			<td style=\"width: 5%; text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">3.4.</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">Jika peringatan tertulis dari&nbsp;<strong>PERUSAHAAN&nbsp;</strong>atas segala kelalaian&nbsp;<strong>KONTRAKTOR&nbsp;</strong>dalam mentaati segala peraturan yang berlaku dan syarat-syarat yang tercantum dalam&nbsp;<strong>KONTRAK&nbsp;</strong>ini telah 2 (dua) kali diberikan, tetapi masih belum ditaati atau belum terdapat kemajuan dalam jangka waktu 7 (tujuh) hari kalender, maka&nbsp;<strong>PERUSAHAAN&nbsp;</strong>berhak secara sepihak memutuskan&nbsp;<strong>KONTRAK</strong>. Setelah pemutusan&nbsp;<strong>KONTRAK</strong>, akan diadakan perhitungan oleh&nbsp;<strong>PERUSAHAAN&nbsp;</strong>mengenai prestasi dan pembayaran-pembayaran sebelumnya. Dalam hal ini&nbsp;<strong>KONTRAKTOR&nbsp;</strong>tidak dapat menuntut ganti rugi apapun juga dan keputusan mutlak ditangan&nbsp;<strong>PERUSAHAAN</strong>.</span></span></td>\n		</tr>\n		<tr>\n			<td style=\"width: 5%; text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">3.5.</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">Jika terjadi pemutusan&nbsp;<strong>KONTRAK&nbsp;</strong>sebagaimana dimaksud pada Pasal ini, maka seluruh hasil&nbsp;<strong>PEKERJAAN KONTRAKTOR </strong>otomatis menjadi milik&nbsp;<strong>PERUSAHAAN</strong>.<strong>&nbsp;</strong></span></span></td>\n		</tr>\n	</tbody>\n</table>\n\n<p style=\"text-align: center;\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">&nbsp;&nbsp;<strong>PASAL 4</strong></span></span></p>\n\n<p align=\"center\" style=\"margin: 0cm 0cm 10pt; text-align: center;\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\"><strong>KETENTUAN PAJAK DAN PUNGUTAN LAINNYA</strong></span></span></p>\n\n<table align=\"left\" style=\"width: 100%; border-collapse: collapse;\">\n	<tbody>\n		<tr>\n			<td style=\"width: 5%; text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">4.1.</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">Semua pajak, bea materai, retribusi dan / atau pungutan lain berdasarkan ketentuan peraturan perundang-undangan yang berlaku yang timbul sehubungan dengan pelaksanaan <strong>KONTRAK&nbsp;</strong>ini menjadi beban dan tanggung jawab <strong>KONTRAKTOR</strong>, kecuali Pajak Pertambahan Nilai (PPN) menjadi beban dan tanggung jawab <strong>PERUSAHAAN</strong>.</span></span></td>\n		</tr>\n		<tr>\n			<td style=\"width: 5%; text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">4.2.</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">Pungutan Pajak Pertambahan Nilai (PPN) yang dikenakan / terhutang sebagaimana dimaksud dalam Pasal ini akan disetorkan langsung oleh <strong>PERUSAHAAN&nbsp;</strong>kepada Kas Negara dalam jangka waktu yang telah ditetapkan oleh perundang-undangan / peraturan yang berlaku.</span></span></td>\n		</tr>\n		<tr>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">4.3.</span></span></td>\n			<td valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\"><strong>PERUSAHAAN&nbsp;</strong>berhak memungut Pajak Pengjasilan (PPh) dari pembayaran kepada&nbsp;<strong>KONTRAKTOR&nbsp;</strong>sesuai dengan ketentuan peraturan perundang-undangan yang berlaku, kecuali terdapat pengecualian / pembebasan pajak dari pihak yang berwenang sehubungan dengan pelaksanaan&nbsp;<strong>PEKERJAAN&nbsp;</strong>berdasarkan&nbsp;<strong>KONTRAK&nbsp;</strong>ini.</span></span></td>\n		</tr>\n		<tr>\n			<td style=\"width: 5%; text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">4.4.</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">Bilamana diperlukan oleh salah satu&nbsp;<strong>PIHAK&nbsp;</strong>atau instansi yang berwenang untuk kepentingan administrasi atau audit, maka baik&nbsp;<strong>PERUSAHAAN&nbsp;</strong>maupun&nbsp;<strong>KONTRAKTOR&nbsp;</strong>akan memberikan bukti-bukti pembayaran yang berkaitan dengan pajak, retribusi dan / atau pungutan lain sebagaimana dimaksud pada ayat 5.1 Pasal ini.</span></span></td>\n		</tr>\n	</tbody>\n</table>\n\n<div style=\"page-break-after: always\"><span style=\"display: none;\">&nbsp;</span></div>\n\n<p style=\"margin: 0cm 0cm 10pt; text-align: right;\"><br />\n<span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">LAMPIRAN D</span></span></p>\n\n<p align=\"center\" style=\"margin: 0cm 0cm 10pt; text-align: center;\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\"><b>PASAL 1</b></span></span></p>\n\n<p align=\"center\" style=\"margin: 0cm 0cm 10pt; text-align: center;\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\"><strong>JANGKA WAKTU PEKERJAAN</strong></span></span></p>\n\n<p style=\"margin: 0cm 0cm 10pt; text-align: justify;\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\"><strong>MASA PENYELESAIAN PEKERJAAN </strong>yang disepakati oleh <strong>PARA PIHAK </strong>sesuai dengan yang tercantum dalam <strong>SPK </strong>dan terhitung sejak <strong>TANGGAL SPK</strong>.</span></span></p>\n\n<p style=\"text-align: center;\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">&nbsp;&nbsp;<strong>PASAL 2</strong></span></span></p>\n\n<p align=\"center\" style=\"margin: 0cm 0cm 10pt; text-align: center;\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\"><strong>JANGKA WAKTU KONTRAK</strong></span></span></p>\n\n<table align=\"left\" style=\"width: 100%; border-collapse: collapse;\">\n	<tbody>\n		<tr>\n			<td style=\"width: 5%; text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\"><strong>KONTRAK&nbsp;</strong>ini berlaku sejak&nbsp;[TANGGAL_MULAI_PEKERJAAN] sampai dengan [TANGGAL_SELESAI_PEKERJAAN]</span></span></td>\n		</tr>\n	</tbody>\n</table>\n\n<div style=\"page-break-after: always\"><span style=\"display: none;\">&nbsp;</span></div>\n\n<p style=\"text-align: right;\"><br />\n<span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">LAMPIRAN E</span></span></p>\n\n<p align=\"center\" style=\"margin: 0cm 0cm 10pt; text-align: center;\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\"><strong>HEALTH, SAFETY, ENVIRONMENT (HSE) DAN&nbsp;</strong></span></span></p>\n\n<p align=\"center\" style=\"margin: 0cm 0cm 10pt; text-align: center;\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\"><strong>CONTRACTOR SAFETY MANAGEMENT SYSTEM (CSMS)</strong></span></span></p>\n\n<p align=\"center\" style=\"margin: 0cm 0cm 10pt; text-align: center;\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\"><strong>PASAL 1</strong></span></span></p>\n\n<p align=\"center\" style=\"margin: 0cm 0cm 10pt; text-align: center;\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\"><strong>KOMITMEN TERHADAP ASPEK HSE</strong></span></span></p>\n\n<table align=\"left\" style=\"width: 100%;\">\n	<tbody>\n		<tr>\n			<td style=\"width: 5%; text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">1.1.</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">Dalam rangka mengurangi bahaya kecelakaan kerja dan bahaya lain (kebakaran, peledakan, pencemaran lingkungan dll), pihak&nbsp;<strong>KONTRAKTOR&nbsp;</strong>harus memenui persyaratan / peraturan perundangan yang berlaku, antara lain :</span></span></td>\n		</tr>\n		<tr>\n			<td style=\"text-align: justify;\" valign=\"top\">&nbsp;</td>\n			<td rowspan=\"1\" valign=\"top\">\n			<table align=\"left\" style=\"width: 100%;\">\n				<tbody>\n					<tr>\n						<td style=\"width: 5%; text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">a.</span></span></td>\n						<td colspan=\"2\" rowspan=\"1\" style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">UU. No. 1 Th. 1970 tentang Keselamatan Kerja.</span></span></td>\n					</tr>\n					<tr>\n						<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">b.</span></span></td>\n						<td colspan=\"2\" rowspan=\"1\" style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">Peraturan Menteri Tenaga Kerja dan Transmigrasi No. 01 Th. 1980 tentang Keselamatan dan Kesehatan Kerja pada Konstruksi Bangunan.</span></span></td>\n					</tr>\n					<tr>\n						<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">c.</span></span></td>\n						<td colspan=\"2\" style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">Kebijakan, Keselamatan, Kesehatan Kerja dan Lindungan Lingkungan yang ditetapkan sesuai ketentuan yang berlaku dan&nbsp;<strong>PERUSAHAAN</strong>.</span></span></td>\n					</tr>\n					<tr>\n						<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">d.</span></span></td>\n						<td colspan=\"2\" style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">Peraturan pemerintah dan MIGAS&nbsp;yang terkait dengan Keselamatan Kesehatan Kerja dan Lindungan Lingkungan di tempat kerja.</span></span></td>\n					</tr>\n					<tr>\n						<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">e.</span></span></td>\n						<td colspan=\"2\" style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">Praktek keselamatan industri yang standar, dan juga kewajiban keselamatan terkait lainnya yang diterapkan atas&nbsp;<strong>KONTRAKTOR&nbsp;</strong>berdasarkan ketentuan yang berlaku dan ketentuan&nbsp;<strong>PERUSAHAAN&nbsp;</strong>dan&nbsp;<strong>KONTRAK</strong>.</span></span></td>\n					</tr>\n				</tbody>\n			</table>\n\n			<p style=\"text-align: justify;\">&nbsp;</p>\n			</td>\n		</tr>\n		<tr>\n			<td style=\"width: 5%; text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">1.2.</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\"><strong>PARAK PIHAK&nbsp;</strong>berkomitmen untuk mewujudkan&nbsp;<em>zero incident&nbsp;</em>(tidak ada kecelakaan, kerusakan asset dan lingkungan).</span></span></td>\n		</tr>\n		<tr>\n			<td style=\"width: 5%; text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">1.3.</span></span></td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">Manajemen Keselamatan dan Resiko</span></span></td>\n		</tr>\n		<tr>\n			<td style=\"width: 5%; text-align: justify;\" valign=\"top\">&nbsp;</td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\"><strong>KONTRAKTOR&nbsp;</strong>dapat menunjukkan bahwa :</span></span></td>\n		</tr>\n		<tr>\n			<td style=\"text-align: justify;\" valign=\"top\">&nbsp;</td>\n			<td valign=\"top\">\n			<table align=\"left\" style=\"width: 100%; border-collapse: collapse;\">\n				<tbody>\n					<tr>\n						<td style=\"width: 6.5%; text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">-</span></span></td>\n						<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">Semua bahaya yang terkait dengan ruang lingkup kerja dan/atau jasa dan mitigasi tindakan diidentifikasi dan didedinisakan;</span></span></td>\n					</tr>\n				</tbody>\n			</table>\n			</td>\n		</tr>\n		<tr>\n			<td style=\"text-align: justify;\" valign=\"top\">&nbsp;</td>\n			<td valign=\"top\">\n			<table align=\"left\" style=\"width: 100%; border-collapse: collapse;\">\n				<tbody>\n					<tr>\n						<td style=\"width: 6.5%; text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">-</span></span></td>\n						<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">Semua kompetensi HSE dan pelatihan merupakan persyaratan untuk personil&nbsp;<strong>KONTRAKTOR&nbsp;</strong>teridentifikasi; dan</span></span></td>\n					</tr>\n				</tbody>\n			</table>\n			</td>\n		</tr>\n		<tr>\n			<td style=\"text-align: justify;\" valign=\"top\">&nbsp;</td>\n			<td valign=\"top\">\n			<table align=\"left\" style=\"width: 100%; border-collapse: collapse;\">\n				<tbody>\n					<tr>\n						<td style=\"width: 6.5%; text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">-</span></span></td>\n						<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">Tanggung jawab dan akuntabilitas untuk manajemen HSE selama kontrak teridentifikasi</span></span></td>\n					</tr>\n				</tbody>\n			</table>\n			</td>\n		</tr>\n		<tr>\n			<td style=\"text-align: justify;\" valign=\"top\">&nbsp;</td>\n			<td valign=\"top\">\n			<table align=\"left\" style=\"width: 100%; border-collapse: collapse;\">\n				<tbody>\n					<tr>\n						<td style=\"width: 6.5%; text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">a.</span></span></td>\n						<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">Identifikasi Bahaya</span></span></td>\n					</tr>\n				</tbody>\n			</table>\n			</td>\n		</tr>\n		<tr>\n			<td style=\"text-align: justify;\" valign=\"top\">&nbsp;</td>\n			<td valign=\"top\">\n			<table align=\"left\" style=\"width: 100%; border-collapse: collapse;\">\n				<tbody>\n					<tr>\n						<td style=\"width: 6.5%; text-align: justify;\" valign=\"top\">&nbsp;</td>\n						<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\"><strong>KONTRAKTOR&nbsp;</strong>diminta untuk mengetahui tingkat pemahamannya terhadap bahaya-bahaya yang terdapat dalam Lingkup Kerja ini. Semua Hazard (bahaya) yang terdapat dalam pekerjaan ini akan dibuat list dan dianalisa bahayanya oleh&nbsp;<strong>KONTRAKTOR</strong>. Hazard yang termasuk dalam pekerjaan ini adalah :</span></span></td>\n					</tr>\n				</tbody>\n			</table>\n			</td>\n		</tr>\n		<tr>\n			<td style=\"text-align: justify;\" valign=\"top\">&nbsp;</td>\n			<td valign=\"top\">\n			<table align=\"left\" style=\"width: 100%; border-collapse: collapse;\">\n				<tbody>\n					<tr>\n						<td style=\"width: 6.5%; text-align: justify;\" valign=\"top\">&nbsp;</td>\n						<td valign=\"top\">\n						<table style=\"width: 100%; border-collapse: collapse;\">\n							<tbody>\n								<tr>\n									<td style=\"text-align: justify;\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">- Bekerja di ketinggian</span></span></td>\n									<td style=\"text-align: justify;\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">- Asphyxia (kekurangan oksigen)</span></span></td>\n								</tr>\n								<tr>\n									<td style=\"text-align: justify;\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">- Bekerja di ruang terbatas</span></span></td>\n									<td style=\"text-align: justify;\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">- Kerja mekanik di dalam stack</span></span></td>\n								</tr>\n								<tr>\n									<td style=\"text-align: justify;\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">- Bekerja dalam lingkungan kerja bertemperatur tinggi</span></span></td>\n									<td style=\"text-align: justify;\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">- Bekerja dalam ruangan yang dindingnya bertemperatur tinggi</span></span></td>\n								</tr>\n								<tr>\n									<td style=\"text-align: justify;\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">- Bekerja di lingkungan bising</span></span></td>\n									<td style=\"text-align: justify;\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">- Terpeleset, tersanding dan terjartuh</span></span></td>\n								</tr>\n								<tr>\n									<td style=\"text-align: justify;\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">- Paparan gas hidrokarbon</span></span></td>\n									<td style=\"text-align: justify;\">&nbsp;</td>\n								</tr>\n							</tbody>\n						</table>\n						</td>\n					</tr>\n				</tbody>\n			</table>\n			</td>\n		</tr>\n		<tr>\n			<td style=\"text-align: justify;\" valign=\"top\">&nbsp;</td>\n			<td valign=\"top\">\n			<table align=\"left\" style=\"width: 100%; border-collapse: collapse;\">\n				<tbody>\n					<tr>\n						<td style=\"width: 6.5%; text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">b.</span></span></td>\n						<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">Evaluasi Bahaya</span></span></td>\n					</tr>\n				</tbody>\n			</table>\n			</td>\n		</tr>\n		<tr>\n			<td style=\"text-align: justify;\" valign=\"top\">&nbsp;</td>\n			<td valign=\"top\">\n			<table align=\"left\" style=\"width: 100%; border-collapse: collapse;\">\n				<tbody>\n					<tr>\n						<td style=\"width: 6.5%; text-align: justify;\" valign=\"top\">&nbsp;</td>\n						<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">Semua Bahaya yang telah diidentifikasi pada bagian sebelumnya,&nbsp;<strong>KONTRAKTOR&nbsp;</strong>diminta untuk mengevaluasi semua risiko bahaya yang ada. Untuk mengevaluasi risiko bahayanya, tabel berikut dapat digunakan sebagai acuan:</span></span></td>\n					</tr>\n				</tbody>\n			</table>\n			</td>\n		</tr>\n		<tr>\n			<td style=\"text-align: justify;\" valign=\"top\">&nbsp;</td>\n			<td valign=\"top\">\n			<table align=\"left\" style=\"width: 100%; border-collapse: collapse;\">\n				<tbody>\n					<tr>\n						<td style=\"width: 6.5%; text-align: justify;\" valign=\"top\">&nbsp;</td>\n						<td valign=\"top\">\n						<table style=\"width: 100%; border-collapse: collapse;\">\n							<tbody>\n								<tr>\n									<td style=\"text-align: justify;\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\"><strong>Tingkatan Resiko</strong></span></span></td>\n									<td style=\"text-align: justify;\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\"><strong>Deskripsi</strong></span></span></td>\n								</tr>\n								<tr>\n									<td style=\"text-align: justify;\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">Rendah</span></span></td>\n									<td style=\"text-align: justify;\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">Membutuhkan kontrol yang sederhana; risiko bahaya dapat menyebabkan kerusakan kecil atau luka ringan (contoh : kasus&nbsp;<em>First Aid</em>&nbsp;/ P3K).</span></span></td>\n								</tr>\n								<tr>\n									<td style=\"text-align: justify;\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">Menengah</span></span></td>\n									<td style=\"text-align: justify;\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">Membutuhkan kontrol yang lebih tinggi; risiko bahaya dapat menyebabkan kerusakan sedang terhadap&nbsp;<em>equipment</em>&nbsp;dan / atau luka / cedera sedang (contoh : kasus kecelakaan yang menyebabkan kehilangan hari kerja atau membutuhkan perawatan medis).</span></span></td>\n								</tr>\n								<tr>\n									<td style=\"text-align: justify;\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">Tinggi</span></span></td>\n									<td style=\"text-align: justify;\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">Membutuhkan kontrol paling tinggi; risiko bahaya dapat menimbulkan kerusakan besar pada&nbsp;<em>equipment</em>&nbsp;dan / atau cedera berat (contoh : kasus kecelakaan yang menyebabkan kehilangan hari kerja,&nbsp;<em>Fatality</em>).</span></span></td>\n								</tr>\n							</tbody>\n						</table>\n						</td>\n					</tr>\n				</tbody>\n			</table>\n			</td>\n		</tr>\n		<tr>\n			<td style=\"text-align: justify;\" valign=\"top\">&nbsp;</td>\n			<td valign=\"top\">\n			<table align=\"left\" style=\"width: 100%; border-collapse: collapse;\">\n				<tbody>\n					<tr>\n						<td style=\"width: 6.5%; text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">c.</span></span></td>\n						<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">Definisi Kontrol Untuk Mengelola Bahaya</span></span></td>\n					</tr>\n				</tbody>\n			</table>\n			</td>\n		</tr>\n		<tr>\n			<td style=\"text-align: justify;\" valign=\"top\">&nbsp;</td>\n			<td valign=\"top\">\n			<table align=\"left\" style=\"width: 100%; border-collapse: collapse;\">\n				<tbody>\n					<tr>\n						<td style=\"width: 6.5%; text-align: justify;\" valign=\"top\">&nbsp;</td>\n						<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\"><strong>KONTRAKTOR&nbsp;</strong>diminta untuk mengidentifikasi kontrol-kontrol yang sesuai untuk mengelola risiko-risiko yang telah diidentifikasi pada bagian sebelumnya. Kontrol ini dapat berupa peralatan yang digunakan, bisa juga prosedur dalam pengawasan selama bekerja</span></span></td>\n					</tr>\n				</tbody>\n			</table>\n			</td>\n		</tr>\n		<tr>\n			<td style=\"text-align: justify;\" valign=\"top\">&nbsp;</td>\n			<td valign=\"top\">\n			<table align=\"left\" style=\"width: 100%; border-collapse: collapse;\">\n				<tbody>\n					<tr>\n						<td style=\"width: 6.5%; text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">d.</span></span></td>\n						<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">Tindakan Pemulihan Jika Kontrol Gagal (Tanggap Darurat)</span></span></td>\n					</tr>\n				</tbody>\n			</table>\n			</td>\n		</tr>\n		<tr>\n			<td style=\"text-align: justify;\" valign=\"top\">&nbsp;</td>\n			<td valign=\"top\">\n			<table align=\"left\" style=\"width: 100%; border-collapse: collapse;\">\n				<tbody>\n					<tr>\n						<td style=\"width: 6.5%; text-align: justify;\" valign=\"top\">&nbsp;</td>\n						<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\"><strong>KONTRAKTOR&nbsp;</strong>diminta untuk mengantisipasi apabila kontrol-kontrol yang disebutkan pada bagian sebelumnya gagal dan mempersiapkan tindakan apa yang akan dilakukan jika kontrol tersebut gagal. Sebagian besar kontrol-kontrol ini berhubungan dengan Prosedur Tanggap Darurat</span></span></td>\n					</tr>\n				</tbody>\n			</table>\n			</td>\n		</tr>\n		<tr>\n			<td style=\"text-align: justify;\" valign=\"top\">&nbsp;</td>\n			<td valign=\"top\">\n			<table align=\"left\" style=\"width: 100%; border-collapse: collapse;\">\n				<tbody>\n					<tr>\n						<td style=\"width: 6.5%; text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">e.</span></span></td>\n						<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">Pelatihan dan Kompetensi Personil</span></span></td>\n					</tr>\n				</tbody>\n			</table>\n			</td>\n		</tr>\n		<tr>\n			<td style=\"text-align: justify;\" valign=\"top\">&nbsp;</td>\n			<td valign=\"top\">\n			<table align=\"left\" style=\"width: 100%; border-collapse: collapse;\">\n				<tbody>\n					<tr>\n						<td style=\"width: 6.5%; text-align: justify;\" valign=\"top\">&nbsp;</td>\n						<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\"><strong>KONTRAKTOR&nbsp;</strong>diminta untuk membuat list pelatihan dan kompetensi yang dibutuhkan dari tenaga ahli yang terlibat di dalam Lingkup Kerja untuk setiap kontrol yang tercatat di atas, akan ada beberapa pelatihan dan kompetensi yang dibutuhkan bagi pekerja yang akan melakukannya agar dapat mengerjakan pekerjaannya dengan efektif</span></span></td>\n					</tr>\n				</tbody>\n			</table>\n			</td>\n		</tr>\n		<tr>\n			<td style=\"text-align: justify;\" valign=\"top\">&nbsp;</td>\n			<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">Minimum Persyaratan Kesehatan, Keselamatan, Keamanan dan Lingkungan</span></span></td>\n		</tr>\n		<tr>\n			<td style=\"text-align: justify;\" valign=\"top\">&nbsp;</td>\n			<td valign=\"top\">\n			<table align=\"left\" style=\"width: 100%; border-collapse: collapse;\">\n				<tbody>\n					<tr>\n						<td style=\"width: 6.5%; text-align: justify;\" valign=\"top\">&nbsp;</td>\n						<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">Rencana HSSE</span></span></td>\n					</tr>\n				</tbody>\n			</table>\n			</td>\n		</tr>\n		<tr>\n			<td style=\"text-align: justify;\" valign=\"top\">&nbsp;</td>\n			<td valign=\"top\">\n			<table align=\"left\" style=\"width: 100%; border-collapse: collapse;\">\n				<tbody>\n					<tr>\n						<td style=\"width: 6.5%; text-align: justify;\" valign=\"top\">&nbsp;</td>\n						<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\"><b>KONTRAKTOR&nbsp;</b>harus mempersiapkan dan mematuhi untuk melaksanakan HSSE Plan Kontrak dengan baik.</span></span></td>\n					</tr>\n				</tbody>\n			</table>\n			</td>\n		</tr>\n		<tr>\n			<td style=\"text-align: justify;\" valign=\"top\">&nbsp;</td>\n			<td valign=\"top\">\n			<table align=\"left\" style=\"width: 100%; border-collapse: collapse;\">\n				<tbody>\n					<tr>\n						<td style=\"width: 6.5%; text-align: justify;\" valign=\"top\">&nbsp;</td>\n						<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">Rencana HSSE dalam kontrak adalah :</span></span></td>\n					</tr>\n				</tbody>\n			</table>\n			</td>\n		</tr>\n		<tr>\n			<td style=\"text-align: justify;\" valign=\"top\">&nbsp;</td>\n			<td valign=\"top\">\n			<table align=\"left\" style=\"width: 100%; border-collapse: collapse;\">\n				<tbody>\n					<tr>\n						<td style=\"width: 6.5%; text-align: justify;\" valign=\"top\">&nbsp;</td>\n						<td valign=\"top\">\n						<table align=\"left\" style=\"width: 100%; border-collapse: collapse;\">\n							<tbody>\n								<tr>\n									<td style=\"width: 6.5%; text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">1.</span></span></td>\n									<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">Mengidentidikasi semua kegiatan utama (<em>key activities</em>) yang termasuk dalam Lingkup Kerja serta membuat tindakan mitigasinya</span></span></td>\n								</tr>\n							</tbody>\n						</table>\n						</td>\n					</tr>\n				</tbody>\n			</table>\n			</td>\n		</tr>\n		<tr>\n			<td style=\"text-align: justify;\" valign=\"top\">&nbsp;</td>\n			<td valign=\"top\">\n			<table align=\"left\" style=\"width: 100%; border-collapse: collapse;\">\n				<tbody>\n					<tr>\n						<td style=\"width: 6.5%; text-align: justify;\" valign=\"top\">&nbsp;</td>\n						<td valign=\"top\">\n						<table align=\"left\" style=\"width: 100%; border-collapse: collapse;\">\n							<tbody>\n								<tr>\n									<td style=\"width: 6.5%; text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">2.</span></span></td>\n									<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">Memastikan bahwa penerapan kontrol / tindakan mitigasi berjalan dengan baik untuk mengurangi risiko HSSE seminimal mungkin.</span></span></td>\n								</tr>\n							</tbody>\n						</table>\n						</td>\n					</tr>\n				</tbody>\n			</table>\n			</td>\n		</tr>\n		<tr>\n			<td style=\"text-align: justify;\" valign=\"top\">&nbsp;</td>\n			<td valign=\"top\">\n			<table align=\"left\" style=\"width: 100%; border-collapse: collapse;\">\n				<tbody>\n					<tr>\n						<td style=\"width: 6.5%; text-align: justify;\" valign=\"top\">&nbsp;</td>\n						<td valign=\"top\">\n						<table align=\"left\" style=\"width: 100%; border-collapse: collapse;\">\n							<tbody>\n								<tr>\n									<td style=\"width: 6.5%; text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">3.</span></span></td>\n									<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">Mengidentifikasi dan menjadwalkan semua aktifitas untuk memastikan bahwa aspek HSSE telah ditujukan dengan tepat mulai dari tahap planning sampai dengan eksekusi proyek</span></span></td>\n								</tr>\n							</tbody>\n						</table>\n						</td>\n					</tr>\n				</tbody>\n			</table>\n			</td>\n		</tr>\n		<tr>\n			<td style=\"text-align: justify;\" valign=\"top\">&nbsp;</td>\n			<td valign=\"top\">\n			<table align=\"left\" style=\"width: 100%; border-collapse: collapse;\">\n				<tbody>\n					<tr>\n						<td style=\"width: 6.5%; text-align: justify;\" valign=\"top\">&nbsp;</td>\n						<td valign=\"top\">\n						<table align=\"left\" style=\"width: 100%; border-collapse: collapse;\">\n							<tbody>\n								<tr>\n									<td style=\"width: 6.5%; text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">4.</span></span></td>\n									<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">HSE&nbsp;<em>performance indicator&nbsp;</em>/ KPI</span></span></td>\n								</tr>\n							</tbody>\n						</table>\n						</td>\n					</tr>\n				</tbody>\n			</table>\n			</td>\n		</tr>\n		<tr>\n			<td style=\"text-align: justify;\" valign=\"top\">&nbsp;</td>\n			<td valign=\"top\">\n			<table align=\"left\" style=\"width: 100%; border-collapse: collapse;\">\n				<tbody>\n					<tr>\n						<td style=\"width: 6.5%; text-align: justify;\" valign=\"top\">&nbsp;</td>\n						<td valign=\"top\">\n						<table align=\"left\" style=\"width: 100%; border-collapse: collapse;\">\n							<tbody>\n								<tr>\n									<td style=\"width: 6.5%; text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">5.</span></span></td>\n									<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">Struktur organisasi proyek yang mengakomodir posisi personil HSE&nbsp;<strong>KONTRAKTOR</strong>.</span></span></td>\n								</tr>\n							</tbody>\n						</table>\n						</td>\n					</tr>\n				</tbody>\n			</table>\n			</td>\n		</tr>\n		<tr>\n			<td style=\"text-align: justify;\" valign=\"top\">&nbsp;</td>\n			<td valign=\"top\">\n			<table align=\"left\" style=\"width: 100%; border-collapse: collapse;\">\n				<tbody>\n					<tr>\n						<td style=\"width: 6.5%; text-align: justify;\" valign=\"top\">&nbsp;</td>\n						<td valign=\"top\">\n						<table align=\"left\" style=\"width: 100%; border-collapse: collapse;\">\n							<tbody>\n								<tr>\n									<td style=\"width: 6.5%; text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">6.</span></span></td>\n									<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">Mengidentifikasi kemampuan / kompetensi personil HSSE dan kebutuhan pelatihannya.</span></span></td>\n								</tr>\n							</tbody>\n						</table>\n						</td>\n					</tr>\n				</tbody>\n			</table>\n			</td>\n		</tr>\n		<tr>\n			<td style=\"text-align: justify;\" valign=\"top\">&nbsp;</td>\n			<td valign=\"top\">\n			<table align=\"left\" style=\"width: 100%; border-collapse: collapse;\">\n				<tbody>\n					<tr>\n						<td style=\"width: 6.5%; text-align: justify;\" valign=\"top\">&nbsp;</td>\n						<td valign=\"top\">\n						<table align=\"left\" style=\"width: 100%; border-collapse: collapse;\">\n							<tbody>\n								<tr>\n									<td style=\"width: 6.5%; text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">7.</span></span></td>\n									<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">Menjelaskan detail mobilisasi dan demobilisasi.</span></span></td>\n								</tr>\n							</tbody>\n						</table>\n						</td>\n					</tr>\n				</tbody>\n			</table>\n			</td>\n		</tr>\n		<tr>\n			<td style=\"text-align: justify;\" valign=\"top\">&nbsp;</td>\n			<td valign=\"top\">\n			<table align=\"left\" style=\"width: 100%; border-collapse: collapse;\">\n				<tbody>\n					<tr>\n						<td style=\"width: 6.5%; text-align: justify;\" valign=\"top\">&nbsp;</td>\n						<td valign=\"top\">\n						<table align=\"left\" style=\"width: 100%; border-collapse: collapse;\">\n							<tbody>\n								<tr>\n									<td style=\"width: 6.5%; text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">8.</span></span></td>\n									<td style=\"text-align: justify;\" valign=\"top\"><span style=\"font-size:14px;\"><span style=\"font-family: Arial Narrow;\">HSE&nbsp;<em>police&nbsp;</em>dan HSE <i>objective&nbsp;</i><strong>KONTRAKTOR&nbsp;</strong>terhadap&nbsp;<strong>PEKERJAAN</strong></span></span></td>\n								</tr>\n							</tbody>\n						</table>\n						</td>\n					</tr>\n				</tbody>\n			</table>\n			</td>\n		</tr>\n		<tr>\n			<td style=\"text-align: justify;\" valign=\"top\">&nbsp;</td>\n			